     1→/**
     2→ * Data Lookup Utilities
     3→ * Provides cached lookups for neighborhoods, boroughs, and property types
     4→ * to transform raw database IDs into human-readable names
     5→ *
     6→ * Usage:
     7→ *   import { initializeLookups, getNeighborhoodName, getBoroughName } from './dataLookups.js';
     8→ *
     9→ *   // On app startup:
    10→ *   await initializeLookups();
    11→ *
    12→ *   // In transform functions:
    13→ *   const neighborhoodName = getNeighborhoodName(neighborhoodId); // Fast, synchronous
    14→ */
    15→
    16→import { supabase } from './supabase.js';
    17→import { DATABASE } from './constants.js';
    18→
    19→// ============================================================================
    20→// Cache Storage
    21→// ============================================================================
    22→
    23→const lookupCache = {
    24→  neighborhoods: new Map(), // neighborhoodId -> name
    25→  boroughs: new Map(),       // boroughId -> name
    26→  propertyTypes: new Map(),  // propertyTypeId -> label
    27→  amenities: new Map(),      // amenityId -> {name, icon}
    28→  safety: new Map(),         // safetyId -> {name, icon}
    29→  houseRules: new Map(),     // houseRuleId -> {name, icon}
    30→  parking: new Map(),        // parkingId -> {label}
    31→  cancellationPolicies: new Map(), // cancellationPolicyId -> {display}
    32→  storage: new Map(),        // storageId -> {title, summaryGuest}
    33→  guestCancellationReasons: new Map(), // reasonId -> {reason, displayOrder}
    34→  hostCancellationReasons: new Map(),  // reasonId -> {reason, displayOrder}
    35→  initialized: false
    36→};
    37→
    38→// ============================================================================
    39→// Property Type Mappings (Static)
    40→// ============================================================================
    41→
    42→const PROPERTY_TYPE_MAP = {
    43→  'Entire Place': 'Entire Place',
    44→  'Private Room': 'Private Room',
    45→  'Shared Room': 'Shared Room',
    46→  'Studio': 'Studio',
    47→  '1 Bedroom': '1 Bedroom',
    48→  '2 Bedroom': '2 Bedroom',
    49→  '3+ Bedroom': '3+ Bedroom'
    50→};
    51→
    52→// ============================================================================
    53→// Initialization Functions
    54→// ============================================================================
    55→
    56→/**
    57→ * Initialize all lookup caches by fetching data from Supabase
    58→ * Call this once when the app starts
    59→ * @returns {Promise<void>}
    60→ */
    61→export async function initializeLookups() {
    62→  if (lookupCache.initialized) {
    63→    return; // Already initialized
    64→  }
    65→
    66→  try {
    67→    // Fetch all in parallel for performance
    68→    await Promise.all([
    69→      initializeBoroughLookups(),
    70→      initializeNeighborhoodLookups(),
    71→      initializePropertyTypeLookups(),
    72→      initializeAmenityLookups(),
    73→      initializeSafetyLookups(),
    74→      initializeHouseRuleLookups(),
    75→      initializeParkingLookups(),
    76→      initializeCancellationPolicyLookups(),
    77→      initializeStorageLookups(),
    78→      initializeCancellationReasonLookups()
    79→    ]);
    80→
    81→    lookupCache.initialized = true;
    82→    console.log('Data lookups initialized successfully');
    83→  } catch (error) {
    84→    console.error('Failed to initialize data lookups:', error);
    85→    // Don't throw - allow app to continue with empty caches
    86→    // This follows "No Fallback Mechanisms" - we show "Unknown" instead
    87→  }
    88→}
    89→
    90→/**
    91→ * Fetch and cache all boroughs
    92→ * @returns {Promise<void>}
    93→ */
    94→async function initializeBoroughLookups() {
    95→  try {
    96→    const { data, error } = await supabase
    97→      .schema('reference_table')
    98→      .from(DATABASE.TABLES.BOROUGH)
    99→      .select('_id, "Display Borough"');
   100→
   101→    if (error) throw error;
   102→
   103→    if (data && Array.isArray(data)) {
   104→      data.forEach(borough => {
   105→        const name = borough['Display Borough'] || 'Unknown Borough';
   106→        lookupCache.boroughs.set(borough._id, name.trim());
   107→      });
   108→      console.log(`Cached ${lookupCache.boroughs.size} boroughs`);
   109→    }
   110→  } catch (error) {
   111→    console.error('Failed to initialize borough lookups:', error);
   112→  }
   113→}
   114→
   115→/**
