   759→    } = data.data;
   760→
   761→    logger.debug('📋 Extracted session data:');
   762→    logger.debug('   access_token:', access_token ? `${access_token.substring(0, 20)}...` : 'MISSING');
   763→    logger.debug('   refresh_token:', refresh_token ? `${refresh_token.substring(0, 20)}...` : 'MISSING');
   764→    logger.debug('   user_id:', user_id);
   765→    logger.debug('   user_type:', user_type);
   766→
   767→    // Set Supabase session using the client
   768→    // This stores the session in localStorage and enables authenticated requests
   769→    logger.debug('🔐 About to call setSession...');
   770→    let sessionError = null;
   771→    try {
   772→      const result = await supabase.auth.setSession({
   773→        access_token,
   774→        refresh_token
   775→      });
   776→      sessionError = result.error;
   777→      logger.debug('🔐 setSession completed, error:', sessionError);
   778→    } catch (setSessionErr) {
   779→      logger.error('🔐 setSession threw an exception:', setSessionErr);
   780→      sessionError = setSessionErr;
   781→    }
   782→
   783→    if (sessionError) {
   784→      logger.error('❌ Failed to set Supabase session:', sessionError.message);
   785→      // Continue anyway - tokens are still valid, just not in client state
   786→    } else {
   787→      logger.debug('✅ Supabase session set successfully');
   788→    }
   789→
   790→    // CRITICAL: Verify the session is actually persisted before proceeding
   791→    // This fixes a race condition where the page can reload before localStorage is written
   792→    let verifyAttempts = 0;
   793→    const maxVerifyAttempts = 5;
   794→    while (verifyAttempts < maxVerifyAttempts) {
   795→      const { data: { session: verifiedSession } } = await supabase.auth.getSession();
   796→      if (verifiedSession && verifiedSession.access_token === access_token) {
   797→        logger.debug('✅ Session verified and persisted');
   798→        break;
   799→      }
   800→      verifyAttempts++;
   801→      logger.debug(`⏳ Waiting for session to persist (attempt ${verifyAttempts}/${maxVerifyAttempts})...`);
   802→      await delay(100);
   803→    }
   804→
   805→    if (verifyAttempts >= maxVerifyAttempts) {
   806→      logger.warn('⚠️ Session may not be fully persisted, but continuing...');
   807→    }
   808→
   809→    // Store access_token as auth token for backward compatibility with existing code
   810→    setAuthToken(access_token);
   811→    setSessionId(user_id);
   812→
   813→    // Set auth state with user ID (public, non-sensitive)
   814→    setAuthState(true, user_id);
   815→
   816→    // Store user type
   817→    if (user_type) {
   818→      setUserType(user_type);
   819→    }
   820→
   821→    // Update login state
   822→    isUserLoggedInState = true;
   823→
   824→    logger.debug('✅ Signup successful (Supabase Auth)');
   825→    logger.debug('   User ID (_id):', user_id);
   826→    logger.debug('   Supabase Auth ID:', supabase_user_id);
   827→    logger.debug('   Host Account ID:', host_account_id);
   828→    logger.debug('   Guest Account ID:', guest_account_id);
   829→    logger.debug('   User Type:', user_type);
   830→    logger.debug('   Session expires in:', expires_in, 'seconds');
   831→
   832→    // Store Supabase user ID for reference
   833→    if (supabase_user_id) {
   834→      localStorage.setItem('splitlease_supabase_user_id', supabase_user_id);
   835→    }
   836→
   837→    return {
   838→      success: true,
   839→      user_id: user_id,
   840→      host_account_id: host_account_id,
   841→      guest_account_id: guest_account_id,
   842→      supabase_user_id: supabase_user_id,
   843→      user_type: user_type,
   844→      expires_in: expires_in
   845→    };
   846→
   847→  } catch (error) {
   848→    logger.error('❌ Signup error:', error);
   849→    return {
   850→      success: false,
   851→      error: 'Network error. Please check your connection and try again.'
   852→    };
   853→  }
   854→}
