   856→/**
   857→ * Validate token via Supabase Edge Function (auth-user) and fetch user data
   858→ * Two-step process:
   859→ * 1. Validate token via Edge Function (validates with Bubble + fetches from Supabase)
   860→ * 2. Cache user type locally
   861→ *
   862→ * ✅ MIGRATED: Now uses Edge Functions instead of direct Bubble API calls
   863→ * API key is stored server-side in Supabase Secrets
   864→ *
   865→ * @param {Object} options - Configuration options
   866→ * @param {boolean} options.clearOnFailure - If true, clears auth data when validation fails. Default: true.
   867→ *                                           Set to false when calling immediately after login/signup to preserve
   868→ *                                           the fresh session even if user profile fetch fails.
   869→ * @returns {Promise<Object|null>} User data object with firstName, fullName, email, profilePhoto, userType, etc. or null if invalid
   870→ */
   871→export async function validateTokenAndFetchUser({ clearOnFailure = true } = {}) {
   872→  let token = getAuthToken();
   873→  let userId = getSessionId();
   874→
   875→  // If no legacy token/userId, check for Supabase Auth session and sync it
   876→  if (!token || !userId) {
   877→    logger.debug('[Auth] No legacy token found, checking for Supabase Auth session...');
   878→
   879→    try {
   880→      let { data: { session }, error } = await supabase.auth.getSession();
   881→
   882→      // CRITICAL: Supabase client may not have loaded session from localStorage yet
   883→      // Wait briefly for initialization if no session found
   884→      if (!session && !error) {
   885→        logger.debug('[Auth] 🔄 No immediate session, waiting briefly for Supabase initialization...');
   886→        await delay(200);
   887→        const retryResult = await supabase.auth.getSession();
   888→        session = retryResult.data?.session;
   889→        error = retryResult.error;
   890→        if (session) {
   891→          logger.debug('[Auth] ✅ Found Supabase session after brief wait');
   892→        }
   893→      }
   894→
   895→      if (session && !error) {
   896→        logger.debug('[Auth] ✅ Found Supabase Auth session, syncing to secure storage');
   897→        token = session.access_token;
   898→        userId = session.user?.user_metadata?.user_id || session.user?.id;
   899→
   900→        // Sync to secure storage for consistency
   901→        setSecureAuthToken(token);
   902→        if (userId) {
   903→          setSecureSessionId(userId);
   904→          setAuthState(true, userId);
   905→        }
   906→        const userType = session.user?.user_metadata?.user_type;
   907→        if (userType) {
   908→          setSecureUserType(userType);
   909→        }
   910→      }
   911→    } catch (err) {
   912→      logger.debug('[Auth] Error checking Supabase session:', err.message);
   913→    }
   914→  }
   915→
   916→  if (!token || !userId) {
   917→    logger.debug('[Auth] No token or user ID found - user not logged in');
   918→    return null;
   919→  }
   920→
   921→  logger.debug('🔍 Validating token and fetching user data via Edge Function...');
   922→
   923→  try {
   924→    const { data, error } = await supabase.functions.invoke('auth-user', {
   925→      body: {
   926→        action: 'validate',
   927→        payload: {
   928→          token,
   929→          user_id: userId
   930→        }
   931→      }
   932→    });
   933→
   934→    if (error) {
   935→      logger.error('❌ Edge Function error:', error);
   936→      logger.error('   Error context:', error.context);
   937→
   938→      // Extract detailed error for logging
   939→      // In newer Supabase JS versions, error.context is the Response object
   940→      if (error.context && typeof error.context.json === 'function') {
   941→        try {
   942→          const errorBody = await error.context.json();
   943→          logger.error('   Error body from Response:', errorBody);
   944→          if (errorBody?.error) {
   945→            logger.error('   Detailed error from response:', errorBody.error);
   946→          }
   947→        } catch (parseErr) {
   948→          // Silent - just for logging
   949→        }
   950→      } else if (error.context?.body) {
   951→        // Fallback for older Supabase JS versions
   952→        try {
   953→          const errorBody = typeof error.context.body === 'string'
   954→            ? JSON.parse(error.context.body)
   955→            : error.context.body;
   956→          if (errorBody?.error) {
   957→            logger.error('   Detailed error from response:', errorBody.error);
   958→          }
   959→        } catch (parseErr) {
   960→          // Silent - just for logging
   961→        }
   962→      }
   963→
   964→      // Only clear auth data if clearOnFailure is true
   965→      // This allows callers to preserve fresh sessions even when validation fails
   966→      if (clearOnFailure) {
   967→        clearAuthData();
   968→      }
   969→      isUserLoggedInState = false;
   970→      return null;
   971→    }
   972→
   973→    if (!data.success) {
   974→      logger.debug('❌ Token validation failed');
   975→      logger.debug('   Reason:', data.error || 'Unknown');
   976→      // Only clear auth data if clearOnFailure is true
   977→      if (clearOnFailure) {
   978→        logger.debug('   Clearing auth data...');
   979→        clearAuthData();
   980→      } else {
   981→        logger.debug('   Preserving session (clearOnFailure=false)');
   982→      }
   983→      isUserLoggedInState = false;
   984→      return null;
   985→    }
   986→
   987→    // Extract user data from Edge Function response
   988→    const userData = data.data;
   989→
   990→    // Cache user type if provided
   991→    let userType = getUserType();
   992→    if (!userType || userType === '') {
   993→      userType = userData.userType || null;
   994→      if (userType) {
   995→        setUserType(userType);
   996→        logger.debug('✅ User type fetched and cached:', userType);
   997→      }
   998→    } else {
   999→      logger.debug('✅ User type loaded from cache:', userType);
  1000→    }
  1001→
  1002→    const userDataObject = {
  1003→      userId: userData.userId,
  1004→      firstName: userData.firstName || null,
  1005→      fullName: userData.fullName || null,
  1006→      email: userData.email || null,
  1007→      profilePhoto: userData.profilePhoto || null,
  1008→      userType: userType,
  1009→      // Host account ID for fetching host-specific data (listings, etc.)
  1010→      // Note: After migration, user._id is used directly as host reference
  1011→      accountHostId: userData.accountHostId || userData._id || null,
  1012→      // User profile fields for proposal prefilling
  1013→      aboutMe: userData.aboutMe || null,
  1014→      needForSpace: userData.needForSpace || null,
  1015→      specialNeeds: userData.specialNeeds || null,
  1016→      // Proposal count for determining first proposal flow
  1017→      proposalCount: userData.proposalCount ?? 0,
  1018→      // Rental application submission status for hiding CTA in success modal
  1019→      hasSubmittedRentalApp: userData.hasSubmittedRentalApp ?? false
  1020→    };
  1021→
  1022→    // Cache firstName and avatarUrl for optimistic UI on page load
  1023→    if (userDataObject.firstName) {
  1024→      setSecureFirstName(userDataObject.firstName);
  1025→    }
  1026→    if (userDataObject.profilePhoto) {
  1027→      setSecureAvatarUrl(userDataObject.profilePhoto);
  1028→    }
  1029→
  1030→    logger.debug('✅ User data validated:', userDataObject.firstName, '- Type:', userDataObject.userType);
  1031→    logger.debug('📊 User proposalCount from Edge Function:', userData.proposalCount, '→ stored as:', userDataObject.proposalCount);
  1032→    isUserLoggedInState = true;
  1033→
  1034→    return userDataObject;
  1035→
  1036→  } catch (error) {
  1037→    logger.error('❌ Token validation error:', error);
  1038→    // Only clear auth data if clearOnFailure is true
  1039→    if (clearOnFailure) {
  1040→      clearAuthData();
  1041→    }
  1042→    isUserLoggedInState = false;
  1043→    return null;
  1044→  }
  1045→}
