  1047→/**
  1048→ * Check if current page is a protected page requiring authentication
  1049→ * Protected pages redirect to home if user is not logged in
  1050→ *
  1051→ * Handles both clean URLs (/guest-proposals) and .html URLs (/guest-proposals.html)
  1052→ * by normalizing the path before comparison
  1053→ *
  1054→ * @returns {boolean} True if current page requires authentication
  1055→ */
  1056→export function isProtectedPage() {
  1057→  const protectedPaths = [
  1058→    '/guest-proposals',
  1059→    '/host-proposals',
  1060→    '/account-profile',
  1061→    '/host-dashboard',
  1062→    '/self-listing',
  1063→    '/listing-dashboard',
  1064→    '/host-overview',
  1065→    '/favorite-listings',
  1066→    '/rental-application',
  1067→    '/preview-split-lease'
  1068→  ];
  1069→
  1070→  // Normalize path by removing .html extension for consistent matching
  1071→  const currentPath = window.location.pathname.replace(/\.html$/, '');
  1072→
  1073→  // Check if current path matches any protected path exactly or starts with it
  1074→  return protectedPaths.some(path =>
  1075→    currentPath === path || currentPath.startsWith(path + '/')
  1076→  );
  1077→}
