  1119→          token
  1120→        }
  1121→      }
  1122→    });
  1123→
  1124→    // Clear auth data regardless of API response
  1125→    // This ensures clean logout even if API call fails
  1126→    clearAuthData();
  1127→
  1128→    if (error || !data.success) {
  1129→      logger.debug('⚠️ Logout API returned error, but local data cleared');
  1130→      return {
  1131→        success: true,
