    57→
    58→// ============================================================================
    59→// Auth State Management
    60→// ============================================================================
    61→
    62→let isUserLoggedInState = false;
    63→let authCheckAttempts = 0;
    64→const MAX_AUTH_CHECK_ATTEMPTS = SESSION_VALIDATION.MAX_AUTH_CHECK_ATTEMPTS;
    65→
    66→// ============================================================================
    67→// Cookie Parsing Utilities
    68→// ============================================================================
    69→
    70→/**
    71→ * Parse username from document cookies
    72→ * Decodes URL-encoded cookie values and removes surrounding quotes
    73→ *
    74→ * @returns {string|null} Username if found, null if no username cookie exists
    75→ */
    76→export function getUsernameFromCookies() {
    77→  const cookies = document.cookie.split('; ');
    78→  const usernameCookie = cookies.find(c => c.startsWith('username='));
    79→
    80→  if (usernameCookie) {
    81→    let username = decodeURIComponent(usernameCookie.split('=')[1]);
    82→    // Remove surrounding quotes if present (both single and double quotes)
    83→    username = username.replace(/^["']|["']$/g, '');
    84→    return username;
    85→  }
    86→
    87→  return null;
    88→}
    89→
    90→/**
    91→ * Check Split Lease cookies from Bubble app
    92→ * Verifies both loggedIn and username cookies
    93→ *
    94→ * @returns {Object} Authentication status object
    95→ *   - isLoggedIn: boolean indicating if user is logged in
    96→ *   - username: string with username or null if not set
    97→ */
    98→export function checkSplitLeaseCookies() {
    99→  const cookies = document.cookie.split('; ');
   100→  const loggedInCookie = cookies.find(c => c.startsWith('loggedIn='));
   101→  const usernameCookie = cookies.find(c => c.startsWith('username='));
   102→
   103→  const isLoggedIn = loggedInCookie ? loggedInCookie.split('=')[1] === 'true' : false;
   104→  const username = getUsernameFromCookies();
   105→
   106→  // Log the authentication status to console
   107→  logger.debug('🔐 Split Lease Cookie Auth Check:');
   108→  logger.debug('   Logged In:', isLoggedIn);
   109→  logger.debug('   Username:', username || 'not set');
   110→  logger.debug('   Raw Cookies:', { loggedInCookie, usernameCookie });
   111→
   112→  return { isLoggedIn, username };
   113→}
   114→
   115→// ============================================================================
   116→// Authentication Status Checking
   117→// ============================================================================
   118→
   119→/**
   120→ * Lightweight authentication status check
   121→ * Checks auth state (not tokens) and validates session
   122→ *
   123→ * Supports both:
   124→ * - Supabase Auth sessions (native signup)
   125→ * - Legacy Bubble auth (tokens in localStorage)
   126→ *
   127→ * No fallback mechanisms - returns boolean directly
   128→ * On failure: returns false without fallback logic
   129→ *
   130→ * @returns {Promise<boolean>} True if user is authenticated, false otherwise
   131→ */
   132→export async function checkAuthStatus() {
   133→  logger.debug('🔍 Checking authentication status...');
   134→
   135→  // Try to migrate from legacy storage first
   136→  const migrated = await migrateFromLegacyStorage();
   137→  if (migrated) {
   138→    logger.debug('✅ Migrated from legacy storage');
   139→  }
   140→
   141→  // First check cross-domain cookies from .split.lease (for compatibility)
   142→  const splitLeaseAuth = checkSplitLeaseCookies();
   143→
   144→  if (splitLeaseAuth.isLoggedIn) {
   145→    logger.debug('✅ User authenticated via Split Lease cookies');
   146→    logger.debug('   Username:', splitLeaseAuth.username);
   147→    isUserLoggedInState = true;
   148→    setAuthState(true);
   149→    return true;
   150→  }
   151→
   152→  // Check Supabase Auth session (for native Supabase signups)
   153→  // CRITICAL: Supabase client may not have loaded session from localStorage yet
   154→  // We need to handle the race condition where getSession() returns null initially
   155→  // but INITIAL_SESSION fires shortly after with a valid session
   156→  try {
   157→    let { data: { session }, error } = await supabase.auth.getSession();
   158→
   159→    // If no session on first check, wait briefly for Supabase to initialize
   160→    // This handles the race condition on page load
   161→    if (!session && !error) {
   162→      logger.debug('🔄 No immediate Supabase session, waiting briefly for initialization...');
   163→      await delay(200);
   164→      const retryResult = await supabase.auth.getSession();
   165→      session = retryResult.data?.session;
   166→      error = retryResult.error;
   167→      if (session) {
   168→        logger.debug('✅ Found Supabase session after brief wait');
   169→      }
   170→    }
   171→
   172→    if (session && !error) {
   173→      logger.debug('✅ User authenticated via Supabase Auth session');
   174→      logger.debug('   User ID:', session.user?.id);
   175→      logger.debug('   Email:', session.user?.email);
   176→
   177→      // Sync Supabase session to our storage for consistency
   178→      const userId = session.user?.user_metadata?.user_id || session.user?.id;
   179→      const userType = session.user?.user_metadata?.user_type;
   180→
   181→      setSecureAuthToken(session.access_token);
   182→      if (userId) {
   183→        setSecureSessionId(userId);
   184→        setAuthState(true, userId);
   185→      }
   186→      if (userType) {
   187→        setSecureUserType(userType);
   188→      }
   189→
   190→      isUserLoggedInState = true;
   191→      return true;
   192→    }
   193→  } catch (err) {
   194→    logger.debug('⚠️ Supabase session check failed:', err.message);
   195→    // Continue to check legacy auth
   196→  }
   197→
   198→  // Check auth state (not tokens directly) - legacy Bubble auth
   199→  const authState = getAuthState();
   200→
   201→  if (authState) {
   202→    // Verify we actually have tokens
   203→    const hasTokens = await hasValidTokens();
   204→
   205→    if (hasTokens) {
   206→      logger.debug('✅ User authenticated via secure storage (legacy)');
   207→      isUserLoggedInState = true;
   208→      return true;
   209→    }
   210→  }
   211→
   212→  logger.debug('❌ User not authenticated');
   213→  isUserLoggedInState = false;
   214→  setAuthState(false);
   215→  return false;
   216→}
   217→
   218→// ============================================================================
   219→// Session Management
   220→// ============================================================================
   221→
   222→/**
   223→ * Validate session by checking if tokens exist
   224→ * Bubble API handles actual token expiry - we validate on each request
   225→ *
   226→ * @returns {boolean} True if session is valid, false if expired or missing
   227→ */
   228→export function isSessionValid() {
   229→  // Simply check if auth state is set
   230→  // Bubble will reject expired tokens on API calls
   231→  return getAuthState();
   232→}
   233→
   234→/**
   235→ * Clear authentication data from storage
   236→ * Removes all auth tokens, session IDs, timestamps, user type, and cookies
   237→ */
   238→export function clearAuthData() {
   239→  // Use secure storage clear (handles both secure tokens and state)
