   241→
   242→  isUserLoggedInState = false;
   243→  logger.debug('🗑️ Authentication data cleared (secure storage and cookies)');
   244→}
   245→
   246→// ============================================================================
   247→// Token Management
   248→// ============================================================================
   249→
   250→/**
   251→ * Get authentication token from secure storage
   252→ * NOTE: This should only be used internally for API calls
   253→ * External code should not access tokens directly
   254→ *
   255→ * @returns {string|null} Auth token if exists, null otherwise
   256→ */
   257→export function getAuthToken() {
