   316→    setSecureUserType(userType);
   317→  }
   318→}
   319→
   320→// ============================================================================
   321→// User Information
   322→// ============================================================================
   323→
   324→/**
   325→ * Get current logged-in username from cookies
   326→ *
   327→ * @returns {string|null} Username if logged in, null otherwise
   328→ */
   329→export function getCurrentUsername() {
