   370→}
   371→
   372→/**
   373→ * Get avatar URL
   374→ * @returns {string|null} Avatar URL or null
   375→ */
   376→export function getAvatarUrl() {
   377→  return getSecureAvatarUrl();
   378→}
   379→
   380→// ============================================================================
   381→// Authentication Redirect Utilities
   382→// ============================================================================
   383→
   384→/**
   385→ * Redirect to login page
   386→ * Direct redirect without modal or iframe
   387→ *
   388→ * @param {string} returnUrl - Optional URL to return to after login
   389→ */
   390→export function redirectToLogin(returnUrl = null) {
   391→  let url = SIGNUP_LOGIN_URL;
   392→
   393→  if (returnUrl) {
   394→    url += `?returnTo=${encodeURIComponent(returnUrl)}`;
   395→  }
   396→
   397→  window.location.href = url;
   398→}
   399→
   400→/**
   401→ * Redirect to account profile page with user ID
   402→ * Only redirect if user is logged in and has a valid session ID
   403→ *
   404→ * @returns {Promise<boolean>} True if redirect initiated, false if not logged in or no user ID
   405→ */
   406→export async function redirectToAccountProfile() {
   407→  if (!isUserLoggedInState) {
   408→    logger.warn('User is not logged in, cannot redirect to account profile');
   409→    return false;
   410→  }
   411→
   412→  const userId = getSessionId();
   413→  if (!userId) {
   414→    logger.error('No user ID found in session, cannot redirect to account profile');
   415→    return false;
   416→  }
   417→
   418→  window.location.href = `${ACCOUNT_PROFILE_URL}/${userId}`;
   419→  return true;
   420→}
   421→
   422→// ============================================================================
   423→// Authentication Check Attempts
   424→// ============================================================================
   425→
   426→/**
   427→ * Increment authentication check attempt counter
   428→ * Useful for limiting retry attempts
   429→ *
   430→ * @returns {number} New attempt count
   431→ */
   432→export function incrementAuthCheckAttempts() {
