   461→
   462→// ============================================================================
   463→// Bubble Authentication API - Edge Function Proxy
   464→// ============================================================================
   465→
   466→/**
   467→ * Login user via Supabase Edge Function (auth-user)
   468→ * Stores token and user_id in localStorage on success
   469→ *
   470→ * ✅ MIGRATED: Now uses Edge Functions instead of direct Bubble API calls
   471→ * API key is stored server-side in Supabase Secrets
   472→ *
   473→ * @param {string} email - User email
   474→ * @param {string} password - User password
   475→ * @returns {Promise<Object>} Response object with status, token, user_id, or error
   476→ */
   477→export async function loginUser(email, password) {
   478→  logger.debug('🔐 Attempting login via Edge Function for:', email);
   479→
   480→  try {
   481→    // CRITICAL: Use direct fetch to bypass Supabase client session handling
   482→    // The Supabase client's functions.invoke can hang indefinitely when there's a stale
   483→    // session because it tries to refresh the token before making requests. Since auth-user
   484→    // has verify_jwt=false, we don't need any auth token. Using direct fetch bypasses this.
   485→    logger.debug('🔄 Using direct fetch to bypass Supabase client session handling...');
   486→
   487→    // Clear localStorage directly to avoid signOut() hanging
   488→    try {
   489→      const storageKey = `sb-${import.meta.env.VITE_SUPABASE_URL?.split('//')[1]?.split('.')[0]}-auth-token`;
   490→      if (storageKey && storageKey !== 'sb-undefined-auth-token') {
   491→        localStorage.removeItem(storageKey);
   492→        logger.debug('✅ Cleared auth token from localStorage');
   493→      }
   494→    } catch (clearErr) {
   495→      logger.warn('⚠️ Could not clear localStorage:', clearErr);
   496→    }
   497→
   498→    const supabaseUrl = import.meta.env.VITE_SUPABASE_URL;
   499→    const supabaseAnonKey = import.meta.env.VITE_SUPABASE_ANON_KEY;
   500→
   501→    logger.debug('📡 Calling auth-user edge function via direct fetch...');
   502→    const response = await fetch(`${supabaseUrl}/functions/v1/auth-user`, {
   503→      method: 'POST',
   504→      headers: {
   505→        'Content-Type': 'application/json',
   506→        'apikey': supabaseAnonKey,
   507→        'Authorization': `Bearer ${supabaseAnonKey}` // Use anon key, not user token
   508→      },
   509→      body: JSON.stringify({
   510→        action: 'login',
   511→        payload: {
   512→          email,
   513→          password
   514→        }
   515→      })
   516→    });
   517→
   518→    const data = await response.json();
   519→    logger.debug('📡 Response status:', response.status);
   520→    logger.debug('📡 Response data:', data);
   521→
   522→    // Handle HTTP errors
   523→    if (!response.ok) {
   524→      logger.error('❌ Edge Function HTTP error:', response.status);
   525→      const errorMessage = data?.error || `HTTP ${response.status}: Failed to authenticate`;
   526→      return {
   527→        success: false,
   528→        error: errorMessage
   529→      };
   530→    }
   531→
   532→    if (!data.success) {
   533→      logger.error('❌ Login failed:', data.error);
   534→      return {
   535→        success: false,
   536→        error: data.error || 'Login failed. Please try again.'
   537→      };
   538→    }
   539→
   540→    // Extract Supabase session data (login now returns same format as signup)
   541→    const {
   542→      access_token,
   543→      refresh_token,
   544→      expires_in,
   545→      user_id,
   546→      host_account_id,
   547→      guest_account_id,
   548→      supabase_user_id,
   549→      user_type
   550→    } = data.data;
   551→
   552→    // Set Supabase session using the client
   553→    // This stores the session in localStorage and enables authenticated requests
   554→    if (access_token && refresh_token) {
   555→      const { error: sessionError } = await supabase.auth.setSession({
   556→        access_token,
   557→        refresh_token
   558→      });
   559→
   560→      if (sessionError) {
   561→        logger.error('❌ Failed to set Supabase session:', sessionError.message);
   562→        // Continue anyway - tokens are still valid, just not in client state
   563→      } else {
   564→        logger.debug('✅ Supabase session set successfully');
   565→      }
   566→
   567→      // CRITICAL: Verify the session is actually persisted before proceeding
   568→      // This fixes a race condition where the page can reload before localStorage is written
   569→      let verifyAttempts = 0;
   570→      const maxVerifyAttempts = 5;
   571→      while (verifyAttempts < maxVerifyAttempts) {
   572→        const { data: { session: verifiedSession } } = await supabase.auth.getSession();
   573→        if (verifiedSession && verifiedSession.access_token === access_token) {
   574→          logger.debug('✅ Session verified and persisted');
   575→          break;
   576→        }
   577→        verifyAttempts++;
   578→        logger.debug(`⏳ Waiting for session to persist (attempt ${verifyAttempts}/${maxVerifyAttempts})...`);
   579→        await delay(100);
   580→      }
   581→
   582→      if (verifyAttempts >= maxVerifyAttempts) {
   583→        logger.warn('⚠️ Session may not be fully persisted, but continuing...');
   584→      }
   585→    }
   586→
   587→    // Store access_token as auth token for backward compatibility
   588→    setAuthToken(access_token);
   589→    setSessionId(user_id);
   590→
   591→    // Set auth state with user ID (public, non-sensitive)
   592→    setAuthState(true, user_id);
   593→
   594→    // Store user type
   595→    if (user_type) {
   596→      setUserType(user_type);
   597→    }
   598→
   599→    // Update login state
   600→    isUserLoggedInState = true;
   601→
   602→    logger.debug('✅ Login successful (Supabase Auth)');
   603→    logger.debug('   User ID (_id):', user_id);
   604→    logger.debug('   Supabase Auth ID:', supabase_user_id);
   605→    logger.debug('   User Type:', user_type);
   606→    logger.debug('   Session expires in:', expires_in, 'seconds');
   607→
   608→    // Store Supabase user ID for reference
   609→    if (supabase_user_id) {
   610→      localStorage.setItem('splitlease_supabase_user_id', supabase_user_id);
   611→    }
   612→
   613→    return {
   614→      success: true,
   615→      user_id: user_id,
   616→      host_account_id: host_account_id,
   617→      guest_account_id: guest_account_id,
   618→      supabase_user_id: supabase_user_id,
   619→      user_type: user_type,
   620→      expires_in: expires_in
   621→    };
   622→
   623→  } catch (error) {
   624→    logger.error('❌ Login error:', error);
   625→    return {
