   632→/**
   633→ * Sign up new user via Supabase Edge Function (auth-user)
   634→ * Uses Supabase Auth natively - stores session tokens for authentication
   635→ * Automatically logs in the user after successful signup
   636→ *
   637→ * ✅ MIGRATED TO SUPABASE AUTH: No longer uses Bubble for signup
   638→ * Creates user in Supabase Auth + public.user (account_host deprecated)
   639→ *
   640→ * @param {string} email - User email
   641→ * @param {string} password - User password
   642→ * @param {string} retype - Password confirmation
   643→ * @param {Object} additionalData - Optional additional signup data
   644→ * @param {string} additionalData.firstName - User's first name
   645→ * @param {string} additionalData.lastName - User's last name
   646→ * @param {string} additionalData.userType - 'Host' or 'Guest'
   647→ * @param {string} additionalData.birthDate - ISO date string (YYYY-MM-DD)
   648→ * @param {string} additionalData.phoneNumber - User's phone number
   649→ * @returns {Promise<Object>} Response object with status, user_id, or error
   650→ */
   651→export async function signupUser(email, password, retype, additionalData = null) {
   652→  logger.debug('📝 Attempting signup via Supabase Auth for:', email);
   653→
   654→  // Client-side validation
   655→  if (!email || !password || !retype) {
   656→    return {
   657→      success: false,
   658→      error: 'All fields are required.'
   659→    };
   660→  }
   661→
   662→  if (password.length < 4) {
   663→    return {
   664→      success: false,
   665→      error: 'Password must be at least 4 characters long.'
   666→    };
   667→  }
   668→
   669→  if (password !== retype) {
   670→    return {
   671→      success: false,
   672→      error: 'The two passwords do not match!'
   673→    };
   674→  }
   675→
   676→  // Build payload with optional additional data
   677→  const payload = {
   678→    email,
   679→    password,
   680→    retype
   681→  };
   682→
   683→  // Add additional signup data if provided
   684→  if (additionalData) {
   685→    payload.additionalData = additionalData;
   686→    logger.debug('📝 Additional signup data:', additionalData);
   687→  }
   688→
   689→  try {
   690→    // CRITICAL: Use direct fetch to bypass Supabase client session handling
   691→    // The Supabase client's functions.invoke can hang indefinitely when there's a stale
   692→    // session because it tries to refresh the token before making requests. Since auth-user
   693→    // has verify_jwt=false, we don't need any auth token. Using direct fetch bypasses this.
   694→    logger.debug('🔄 Using direct fetch to bypass Supabase client session handling...');
   695→
   696→    // Clear localStorage directly to avoid signOut() hanging
   697→    try {
   698→      const storageKey = `sb-${import.meta.env.VITE_SUPABASE_URL?.split('//')[1]?.split('.')[0]}-auth-token`;
   699→      if (storageKey && storageKey !== 'sb-undefined-auth-token') {
   700→        localStorage.removeItem(storageKey);
   701→        logger.debug('✅ Cleared auth token from localStorage');
   702→      }
   703→    } catch (clearErr) {
   704→      logger.warn('⚠️ Could not clear localStorage:', clearErr);
   705→    }
   706→
   707→    const supabaseUrl = import.meta.env.VITE_SUPABASE_URL;
   708→    const supabaseAnonKey = import.meta.env.VITE_SUPABASE_ANON_KEY;
   709→
   710→    logger.debug('📡 Calling auth-user edge function via direct fetch...');
   711→    const response = await fetch(`${supabaseUrl}/functions/v1/auth-user`, {
   712→      method: 'POST',
   713→      headers: {
   714→        'Content-Type': 'application/json',
   715→        'apikey': supabaseAnonKey,
   716→        'Authorization': `Bearer ${supabaseAnonKey}` // Use anon key, not user token
   717→      },
   718→      body: JSON.stringify({
   719→        action: 'signup',
   720→        payload
   721→      })
   722→    });
   723→
   724→    const data = await response.json();
   725→    logger.debug('📡 Response status:', response.status);
   726→    logger.debug('📡 Response data:', data);
   727→
   728→    // Handle HTTP errors
   729→    if (!response.ok) {
   730→      logger.error('❌ Edge Function HTTP error:', response.status);
   731→      const errorMessage = data?.error || `HTTP ${response.status}: Failed to create account`;
   732→      return {
   733→        success: false,
   734→        error: errorMessage
   735→      };
   736→    }
   737→
   738→    // Handle error in response body
   739→    if (!data.success) {
   740→      logger.error('❌ Signup failed:', data.error);
   741→      return {
   742→        success: false,
   743→        error: data.error || 'Signup failed. Please try again.'
   744→      };
   745→    }
   746→
   747→    logger.debug('✅ Edge Function returned successfully');
   748→
   749→    // Extract Supabase session data
   750→    const {
   751→      access_token,
   752→      refresh_token,
   753→      expires_in,
   754→      user_id,
   755→      host_account_id,
   756→      guest_account_id,
   757→      supabase_user_id,
   758→      user_type
   759→    } = data.data;
   760→
   761→    logger.debug('📋 Extracted session data:');
   762→    logger.debug('   access_token:', access_token ? `${access_token.substring(0, 20)}...` : 'MISSING');
   763→    logger.debug('   refresh_token:', refresh_token ? `${refresh_token.substring(0, 20)}...` : 'MISSING');
   764→    logger.debug('   user_id:', user_id);
   765→    logger.debug('   user_type:', user_type);
   766→
   767→    // Set Supabase session using the client
   768→    // This stores the session in localStorage and enables authenticated requests
   769→    logger.debug('🔐 About to call setSession...');
   770→    let sessionError = null;
   771→    try {
   772→      const result = await supabase.auth.setSession({
   773→        access_token,
   774→        refresh_token
   775→      });
   776→      sessionError = result.error;
   777→      logger.debug('🔐 setSession completed, error:', sessionError);
   778→    } catch (setSessionErr) {
   779→      logger.error('🔐 setSession threw an exception:', setSessionErr);
   780→      sessionError = setSessionErr;
   781→    }
   782→
   783→    if (sessionError) {
   784→      logger.error('❌ Failed to set Supabase session:', sessionError.message);
   785→      // Continue anyway - tokens are still valid, just not in client state
   786→    } else {
   787→      logger.debug('✅ Supabase session set successfully');
   788→    }
   789→
   790→    // CRITICAL: Verify the session is actually persisted before proceeding
   791→    // This fixes a race condition where the page can reload before localStorage is written
   792→    let verifyAttempts = 0;
   793→    const maxVerifyAttempts = 5;
   794→    while (verifyAttempts < maxVerifyAttempts) {
   795→      const { data: { session: verifiedSession } } = await supabase.auth.getSession();
   796→      if (verifiedSession && verifiedSession.access_token === access_token) {
   797→        logger.debug('✅ Session verified and persisted');
   798→        break;
   799→      }
   800→      verifyAttempts++;
   801→      logger.debug(`⏳ Waiting for session to persist (attempt ${verifyAttempts}/${maxVerifyAttempts})...`);
   802→      await delay(100);
   803→    }
   804→
   805→    if (verifyAttempts >= maxVerifyAttempts) {
   806→      logger.warn('⚠️ Session may not be fully persisted, but continuing...');
   807→    }
   808→
   809→    // Store access_token as auth token for backward compatibility with existing code
   810→    setAuthToken(access_token);
   811→    setSessionId(user_id);
   812→
   813→    // Set auth state with user ID (public, non-sensitive)
   814→    setAuthState(true, user_id);
   815→
   816→    // Store user type
   817→    if (user_type) {
   818→      setUserType(user_type);
   819→    }
   820→
   821→    // Update login state
   822→    isUserLoggedInState = true;
   823→
   824→    logger.debug('✅ Signup successful (Supabase Auth)');
   825→    logger.debug('   User ID (_id):', user_id);
   826→    logger.debug('   Supabase Auth ID:', supabase_user_id);
   827→    logger.debug('   Host Account ID:', host_account_id);
   828→    logger.debug('   Guest Account ID:', guest_account_id);
   829→    logger.debug('   User Type:', user_type);
   830→    logger.debug('   Session expires in:', expires_in, 'seconds');
   831→
   832→    // Store Supabase user ID for reference
   833→    if (supabase_user_id) {
   834→      localStorage.setItem('splitlease_supabase_user_id', supabase_user_id);
   835→    }
   836→
   837→    return {
   838→      success: true,
   839→      user_id: user_id,
   840→      host_account_id: host_account_id,
   841→      guest_account_id: guest_account_id,
   842→      supabase_user_id: supabase_user_id,
   843→      user_type: user_type,
   844→      expires_in: expires_in
   845→    };
   846→
   847→  } catch (error) {
   848→    logger.error('❌ Signup error:', error);
   849→    return {
   850→      success: false,
   851→      error: 'Network error. Please check your connection and try again.'
   852→    };
   853→  }
   854→}
