  1202→/**
  1203→ * Request password reset email via Edge Function (auth-user)
  1204→ * Always returns success to prevent email enumeration
  1205→ *
  1206→ * Uses Edge Functions - API keys stored server-side
  1207→ *
  1208→ * @param {string} email - User's email address
  1209→ * @returns {Promise<Object>} Response object with success status and message
  1210→ */
  1211→export async function requestPasswordReset(email) {
  1212→  logger.debug('🔐 Requesting password reset for:', email);
  1213→
  1214→  if (!email) {
  1215→    return {
  1216→      success: false,
  1217→      error: 'Email is required.'
  1218→    };
  1219→  }
  1220→
  1221→  try {
  1222→    const { data, error } = await supabase.functions.invoke('auth-user', {
  1223→      body: {
  1224→        action: 'request_password_reset',
  1225→        payload: {
  1226→          email,
  1227→          redirectTo: `${window.location.origin}/reset-password`
  1228→        }
  1229→      }
  1230→    });
  1231→
  1232→    if (error) {
  1233→      logger.error('❌ Password reset request failed:', error);
  1234→      // Don't expose error details - always show generic message
  1235→      return {
  1236→        success: true, // Return success even on error to prevent email enumeration
  1237→        message: 'If an account with that email exists, a password reset link has been sent.'
  1238→      };
  1239→    }
  1240→
  1241→    logger.debug('✅ Password reset request processed');
  1242→    return {
  1243→      success: true,
  1244→      message: data?.data?.message || 'If an account with that email exists, a password reset link has been sent.'
  1245→    };
  1246→
  1247→  } catch (error) {
  1248→    logger.error('❌ Password reset error:', error);
  1249→    return {
  1250→      success: true, // Return success even on error to prevent email enumeration
  1251→      message: 'If an account with that email exists, a password reset link has been sent.'
  1252→    };
  1253→  }
  1254→}
  1255→
  1256→/**
  1257→ * Update password after clicking reset link
  1258→ * Must be called when user has active session from PASSWORD_RECOVERY event
  1259→ *
  1260→ * After successful password update, the user remains logged in with their
  1261→ * existing session synced to secure storage.
  1262→ *
  1263→ * Uses Edge Functions - API keys stored server-side
  1264→ *
  1265→ * @param {string} newPassword - New password to set
  1266→ * @returns {Promise<Object>} Response object with success status
  1267→ */
  1268→export async function updatePassword(newPassword) {
  1269→  logger.debug('🔐 Updating password...');
  1270→
  1271→  if (!newPassword) {
  1272→    return {
  1273→      success: false,
  1274→      error: 'New password is required.'
  1275→    };
  1276→  }
  1277→
  1278→  if (newPassword.length < 4) {
  1279→    return {
  1280→      success: false,
  1281→      error: 'Password must be at least 4 characters long.'
  1282→    };
  1283→  }
  1284→
  1285→  // Get current session (from PASSWORD_RECOVERY event)
  1286→  const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1287→
  1288→  if (sessionError || !session) {
  1289→    logger.error('❌ No active session for password update');
  1290→    return {
  1291→      success: false,
  1292→      error: 'Invalid or expired reset link. Please request a new password reset.'
  1293→    };
  1294→  }
  1295→
  1296→  try {
  1297→    const { data, error } = await supabase.functions.invoke('auth-user', {
  1298→      body: {
  1299→        action: 'update_password',
  1300→        payload: {
  1301→          password: newPassword,
  1302→          access_token: session.access_token
  1303→        }
  1304→      }
  1305→    });
  1306→
  1307→    if (error) {
  1308→      logger.error('❌ Password update failed:', error);
  1309→
  1310→      // Extract detailed error from response body if available
  1311→      let errorMessage = 'Failed to update password. Please try again.';
  1312→
  1313→      // In newer Supabase JS versions, error.context is the Response object
  1314→      if (error.context && typeof error.context.json === 'function') {
  1315→        try {
  1316→          const errorBody = await error.context.json();
  1317→          logger.error('   Error body from Response:', errorBody);
  1318→          if (errorBody?.error) {
  1319→            errorMessage = errorBody.error;
  1320→          }
  1321→        } catch (parseErr) {
  1322→          // Silent - use default message
  1323→        }
  1324→      } else if (error.context?.body) {
  1325→        // Fallback for older Supabase JS versions
  1326→        try {
  1327→          const errorBody = typeof error.context.body === 'string'
  1328→            ? JSON.parse(error.context.body)
  1329→            : error.context.body;
  1330→          if (errorBody?.error) {
  1331→            errorMessage = errorBody.error;
  1332→          }
  1333→        } catch (parseErr) {
  1334→          // Silent - use default message
  1335→        }
  1336→      }
  1337→
  1338→      return {
  1339→        success: false,
  1340→        error: errorMessage
  1341→      };
  1342→    }
  1343→
  1344→    if (!data.success && data.error) {
  1345→      return {
  1346→        success: false,
  1347→        error: data.error
  1348→      };
  1349→    }
  1350→
  1351→    logger.debug('✅ Password updated successfully');
  1352→
  1353→    // Keep user logged in by syncing the Supabase session to secure storage
  1354→    // The user has proven account ownership by accessing their email
  1355→    const userId = session.user?.user_metadata?.user_id || session.user?.id;
  1356→    const userType = session.user?.user_metadata?.user_type;
  1357→
  1358→    // Store session tokens
  1359→    setSecureAuthToken(session.access_token);
  1360→    if (userId) {
  1361→      setSecureSessionId(userId);
  1362→      setAuthState(true, userId);
  1363→    }
  1364→    if (userType) {
  1365→      setSecureUserType(userType);
  1366→    }
  1367→
  1368→    // Update login state
  1369→    isUserLoggedInState = true;
  1370→
  1371→    logger.debug('✅ User session preserved after password update');
  1372→    logger.debug('   User ID:', userId);
  1373→    logger.debug('   User Type:', userType);
  1374→
  1375→    return {
  1376→      success: true,
  1377→      message: data?.data?.message || 'Password updated successfully.'
  1378→    };
  1379→
  1380→  } catch (error) {
  1381→    logger.error('❌ Password update error:', error);
  1382→    return {
  1383→      success: false,
  1384→      error: 'Network error. Please check your connection and try again.'
  1385→    };
  1386→  }
  1387→}
