  1819→    };
  1820→
  1821→  } catch (err) {
  1822→    clearGoogleOAuthUserType();
  1823→    return { success: false, error: err.message };
  1824→  }
  1825→}
  1826→
  1827→/**
  1828→ * Initiate Google OAuth login flow
  1829→ * Sets login flow flag and redirects to current page after OAuth
  1830→ *
  1831→ * @returns {Promise<Object>} Result with success status or error
  1832→ */
  1833→export async function initiateGoogleOAuthLogin() {
  1834→  logger.debug('[Auth] Initiating Google OAuth Login');
  1835→
  1836→  // Clear any existing signup flow flags to prevent conflicts
  1837→  clearGoogleOAuthUserType();
  1838→
  1839→  // Set login flow flag before redirect
  1840→  setGoogleOAuthLoginFlow(true);
  1841→
  1842→  try {
  1843→    const { data, error } = await supabase.auth.signInWithOAuth({
  1844→      provider: 'google',
  1845→      options: {
  1846→        // Stay on current page after OAuth (no redirect to profile)
  1847→        redirectTo: window.location.href,
  1848→        scopes: 'openid profile email',
  1849→      }
  1850→    });
  1851→
  1852→    if (error) {
  1853→      clearGoogleOAuthLoginFlow();
  1854→      return { success: false, error: error.message };
  1855→    }
  1856→
  1857→    // OAuth redirect will happen automatically
  1858→    return { success: true, data };
  1859→  } catch (err) {
  1860→    clearGoogleOAuthLoginFlow();
  1861→    return { success: false, error: err.message };
  1862→  }
  1863→}
  1864→
  1865→/**
  1866→ * Handle Google OAuth login callback
  1867→ * Verifies user exists in database, returns session data or error
  1868→ *
  1869→ * @returns {Promise<Object>} Result with user data or error (userNotFound: true if account doesn't exist)
  1870→ */
  1871→export async function handleGoogleOAuthLoginCallback() {
  1872→  logger.debug('[Auth] Handling Google OAuth Login callback');
  1873→
  1874→  // Verify this is a login flow
  1875→  if (!getGoogleOAuthLoginFlow()) {
  1876→    logger.debug('[Auth] Not a login flow callback, skipping');
  1877→    return { success: false, error: 'Not a login flow' };
  1878→  }
  1879→
  1880→  try {
  1881→    // Get the session from OAuth callback
  1882→    const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1883→
  1884→    if (sessionError || !session) {
  1885→      clearGoogleOAuthLoginFlow();
  1886→      return {
  1887→        success: false,
  1888→        error: sessionError?.message || 'No session found after OAuth'
  1889→      };
  1890→    }
  1891→
  1892→    // Check if this is a fresh OAuth session (from Google)
  1893→    const user = session.user;
  1894→    const isGoogleProvider =
  1895→      user?.app_metadata?.provider === 'google' ||
  1896→      user?.app_metadata?.providers?.includes('google');
  1897→
  1898→    if (!isGoogleProvider) {
  1899→      clearGoogleOAuthLoginFlow();
  1900→      return { success: false, error: 'Not a Google OAuth session' };
  1901→    }
  1902→
  1903→    // Extract Google data
  1904→    const email = user.email;
  1905→    const supabaseUserId = user.id;
  1906→
  1907→    logger.debug('[Auth] Google OAuth login data:', { email, supabaseUserId });
  1908→
  1909→    // Call Edge Function to verify user exists
  1910→    const response = await fetch(`${import.meta.env.VITE_SUPABASE_URL}/functions/v1/auth-user`, {
  1911→      method: 'POST',
  1912→      headers: {
  1913→        'Content-Type': 'application/json',
  1914→        'apikey': import.meta.env.VITE_SUPABASE_ANON_KEY,
  1915→        'Authorization': `Bearer ${session.access_token}`,
  1916→      },
  1917→      body: JSON.stringify({
  1918→        action: 'oauth_login',
  1919→        payload: {
  1920→          email,
  1921→          supabaseUserId,
  1922→          access_token: session.access_token,
  1923→          refresh_token: session.refresh_token,
  1924→        }
  1925→      })
  1926→    });
  1927→
  1928→    const data = await response.json();
  1929→
  1930→    // Always clear the login flow flag
  1931→    clearGoogleOAuthLoginFlow();
  1932→
  1933→    if (!response.ok || !data.success) {
  1934→      // Check if user was not found
  1935→      if (data.data?.userNotFound) {
  1936→        logger.debug('[Auth] User not found for OAuth login:', email);
  1937→        return {
  1938→          success: false,
  1939→          userNotFound: true,
  1940→          email: email,
  1941→          error: 'No account found with this email'
  1942→        };
  1943→      }
  1944→
  1945→      return {
  1946→        success: false,
  1947→        error: data.error || 'Failed to login'
  1948→      };
  1949→    }
  1950→
  1951→    // Store session data
  1952→    const { user_id, user_type, supabase_user_id, access_token, refresh_token } = data.data;
  1953→
  1954→    setAuthToken(session.access_token);
  1955→    setSessionId(user_id);
  1956→    setAuthState(true, user_id);
  1957→    if (user_type) {
  1958→      setUserType(user_type);
  1959→    }
  1960→
  1961→    // Store Supabase user ID for reference
  1962→    if (supabase_user_id) {
  1963→      localStorage.setItem('splitlease_supabase_user_id', supabase_user_id);
  1964→    }
  1965→
  1966→    logger.debug('[Auth] Google OAuth login successful');
  1967→    return {
  1968→      success: true,
  1969→      data: data.data
  1970→    };
  1971→
  1972→  } catch (err) {
  1973→    clearGoogleOAuthLoginFlow();
  1974→    logger.error('[Auth] Google OAuth login callback error:', err);
  1975→    return { success: false, error: err.message };
