   303→          worstCaseText: policy['Worst Case Text'] || null,
   304→          summaryTexts: policy['Summary Texts'] || null
   305→        });
   306→      });
   307→      console.log(`Cached ${lookupCache.cancellationPolicies.size} cancellation policies`);
   308→    }
   309→  } catch (error) {
   310→    console.error('Failed to initialize cancellation policy lookups:', error);
   311→  }
   312→}
   313→
   314→/**
   315→ * Fetch and cache all storage options
   316→ * @returns {Promise<void>}
   317→ */
   318→async function initializeStorageLookups() {
   319→  try {
   320→    const { data, error } = await supabase
   321→      .schema('reference_table')
   322→      .from(DATABASE.TABLES.STORAGE)
   323→      .select('_id, Title, "Summary - Guest"');
   324→
   325→    if (error) throw error;
   326→
   327→    if (data && Array.isArray(data)) {
   328→      data.forEach(storage => {
   329→        lookupCache.storage.set(storage._id, {
   330→          title: storage.Title || 'Unknown Storage',
   331→          summaryGuest: storage['Summary - Guest'] || ''
   332→        });
   333→      });
   334→      console.log(`Cached ${lookupCache.storage.size} storage options`);
   335→    }
