   338→  }
   339→}
   340→
   341→/**
   342→ * Fetch and cache all cancellation/rejection reasons for both guests and hosts
   343→ * @returns {Promise<void>}
   344→ */
   345→async function initializeCancellationReasonLookups() {
   346→  try {
   347→    const { data, error } = await supabase
   348→      .schema('reference_table')
