   350→      .select('id, user_type, reason, display_order')
   351→      .eq('is_active', true)
   352→      .order('display_order', { ascending: true });
   353→
   354→    if (error) throw error;
   355→
   356→    if (data && Array.isArray(data)) {
   357→      data.forEach(item => {
   358→        const cacheData = {
   359→          id: item.id,
   360→          reason: item.reason,
   361→          displayOrder: item.display_order
   362→        };
   363→
   364→        if (item.user_type === 'guest') {
