/**
 * Counteroffer Workflow Module
 *
 * PILLAR IV: Workflow Orchestrators (The "Flow" Layer)
 *
 * Implements the counteroffer acceptance workflow from Bubble.io:
 *
 * 7 Steps (Accept Counteroffer):
 * 1. Show success alert (48-hour timeline)
 * 2. Calculate lease numbering format (based on count)
 * 3. Set state: Number of zeros
 * 4. Calculate 4-week compensation (original proposal)
 * 5. Update proposal status -> "Drafting Lease Documents"
 * 6. Calculate 4-week rent (counteroffer terms)
 * 7. Schedule API workflow: CORE-create-lease (+15 seconds)
 */

import { supabase } from '../../../lib/supabase.js';
import { hasReviewableCounteroffer } from '../../rules/proposals/proposalRules.js';
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js';

/**
 * Accept a counteroffer
 * Updates proposal status and prepares for lease creation
 *
 * @param {string} proposalId - Proposal ID
 * @returns {Promise<Object>} Updated proposal data
 */
export async function acceptCounteroffer(proposalId) {
  if (!proposalId) {
    throw new Error('Proposal ID is required');
  }

  console.log('[counterofferWorkflow] Accepting counteroffer for proposal:', proposalId);

  // First, fetch the proposal to validate it has a counteroffer
  const { data: proposal, error: fetchError } = await supabase
    .from('proposal')
    .select('*')
    .eq('_id', proposalId)
    .single();

  if (fetchError) {
    throw new Error(`Failed to fetch proposal: ${fetchError.message}`);
  }

  if (!proposal['counter offer happened']) {
    throw new Error('Proposal does not have a counteroffer to accept');
  }

  // Update proposal status to accepted
  const now = new Date().toISOString();
  const updateData = {
    'Status': PROPOSAL_STATUSES.PROPOSAL_OR_COUNTEROFFER_ACCEPTED.key,
    'Modified Date': now,
    'Is Finalized': true
  };

  const { data, error } = await supabase
    .from('proposal')
    .update(updateData)
    .eq('_id', proposalId)
    .select()
    .single();

  if (error) {
    console.error('[counterofferWorkflow] Error accepting counteroffer:', error);
    throw new Error(`Failed to accept counteroffer: ${error.message}`);
  }

  console.log('[counterofferWorkflow] Counteroffer accepted successfully:', proposalId);

  // Note: In production, this would trigger the CORE-create-lease API workflow
  // That functionality would need to be implemented via Edge Functions or backend

  return data;
}

/**
 * Decline a counteroffer (equivalent to cancel proposal)
 *
 * @param {string} proposalId - Proposal ID
 * @param {string} reason - Reason for declining
 * @returns {Promise<Object>} Updated proposal data
 */
export async function declineCounteroffer(proposalId, reason = 'Counteroffer declined by guest') {
  if (!proposalId) {
    throw new Error('Proposal ID is required');
  }

  console.log('[counterofferWorkflow] Declining counteroffer for proposal:', proposalId);

  const now = new Date().toISOString();
  const updateData = {
    'Status': PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key,
    'Deleted': true,
    'Modified Date': now,
    'reason for cancellation': reason
  };

  const { data, error } = await supabase
    .from('proposal')
    .update(updateData)
    .eq('_id', proposalId)
    .select()
    .single();

  if (error) {
    console.error('[counterofferWorkflow] Error declining counteroffer:', error);
    throw new Error(`Failed to decline counteroffer: ${error.message}`);
  }

  console.log('[counterofferWorkflow] Counteroffer declined successfully:', proposalId);
  return data;
}

/**
 * Get comparison data between original terms and counteroffer
 *
 * @param {Object} proposal - Proposal object
 * @returns {Object} Comparison data
 */
export function getTermsComparison(proposal) {
  if (!proposal) {
    throw new Error('Proposal is required');
  }

  const originalTerms = {
    daysSelected: proposal.daysSelected || proposal['Days Selected'] || [],
    nightsPerWeek: proposal.nightsPerWeek || proposal['nights per week (num)'] || 0,
    reservationWeeks: proposal.reservationWeeks || proposal['Reservation Span (Weeks)'] || 0,
    checkInDay: proposal.checkInDay || proposal['check in day'] || null,
    checkOutDay: proposal.checkOutDay || proposal['check out day'] || null,
    totalPrice: proposal.totalPrice || proposal['Total Price for Reservation (guest)'] || 0,
    nightlyPrice: proposal.nightlyPrice || proposal['proposal nightly price'] || 0,
    damageDeposit: proposal.damageDeposit || proposal['damage deposit'] || 0,
    cleaningFee: proposal.cleaningFee || proposal['cleaning fee'] || 0
  };

  const counterofferTerms = {
    daysSelected: proposal.hcDaysSelected || proposal['hc days selected'] || originalTerms.daysSelected,
    nightsPerWeek: proposal.hcNightsPerWeek || proposal['hc nights per week'] || originalTerms.nightsPerWeek,
    reservationWeeks: proposal.hcReservationWeeks || proposal['hc reservation span (weeks)'] || originalTerms.reservationWeeks,
    checkInDay: proposal.hcCheckInDay || proposal['hc check in day'] || originalTerms.checkInDay,
    checkOutDay: proposal.hcCheckOutDay || proposal['hc check out day'] || originalTerms.checkOutDay,
    totalPrice: proposal.hcTotalPrice || proposal['hc total price'] || originalTerms.totalPrice,
    nightlyPrice: proposal.hcNightlyPrice || proposal['hc nightly price'] || originalTerms.nightlyPrice,
    damageDeposit: proposal.hcDamageDeposit || proposal['hc damage deposit'] || originalTerms.damageDeposit,
    cleaningFee: proposal.hcCleaningFee || proposal['hc cleaning fee'] || originalTerms.cleaningFee
  };

  // Build list of changes
  const changes = [];

  if (originalTerms.totalPrice !== counterofferTerms.totalPrice) {
    changes.push({
      field: 'totalPrice',
      label: 'Total Price',
      original: originalTerms.totalPrice,
      modified: counterofferTerms.totalPrice
    });
  }

  if (originalTerms.nightlyPrice !== counterofferTerms.nightlyPrice) {
    changes.push({
      field: 'nightlyPrice',
      label: 'Nightly Rate',
      original: originalTerms.nightlyPrice,
      modified: counterofferTerms.nightlyPrice
    });
  }

  if (originalTerms.reservationWeeks !== counterofferTerms.reservationWeeks) {
    changes.push({
      field: 'reservationWeeks',
      label: 'Duration (Weeks)',
      original: originalTerms.reservationWeeks,
      modified: counterofferTerms.reservationWeeks
    });
  }

  if (originalTerms.nightsPerWeek !== counterofferTerms.nightsPerWeek) {
    changes.push({
      field: 'nightsPerWeek',
      label: 'Nights per Week',
      original: originalTerms.nightsPerWeek,
      modified: counterofferTerms.nightsPerWeek
    });
  }

  if (JSON.stringify(originalTerms.daysSelected) !== JSON.stringify(counterofferTerms.daysSelected)) {
    changes.push({
      field: 'daysSelected',
      label: 'Weekly Schedule',
      original: originalTerms.daysSelected,
      modified: counterofferTerms.daysSelected
    });
  }

  return {
    originalTerms,
    counterofferTerms,
    changes,
    hasChanges: changes.length > 0
  };
}

/**
 * Proposal Status Configuration System
 *
 * Centralized configuration for all proposal statuses,
 * replacing hardcoded status mappings throughout the application.
 *
 * Each status includes:
 * - key: The exact status string from the database
 * - color: UI color theme for the status
 * - label: User-friendly display text
 * - stage: The progress stage number (1-6) or null if not in active flow
 * - actions: Available actions for this status
 */

export const PROPOSAL_STATUSES = {
  // ===== CANCELLED STATES =====
  CANCELLED_BY_GUEST: {
    key: 'Proposal Cancelled by Guest',
    color: 'red',
    label: 'Cancelled by You',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  CANCELLED_BY_SPLITLEASE: {
    key: 'Proposal Cancelled by Split Lease',
    color: 'red',
    label: 'Proposal Cancelled',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  // ===== REJECTED STATES =====
  REJECTED_BY_HOST: {
    key: 'Proposal Rejected by Host',
    color: 'red',
    label: 'Rejected by Host',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  // ===== ACTIVE PROPOSAL FLOW =====
  // usualOrder values match reference_table.os_proposal_status sort_order
  // sort_order: -1 = terminal (cancelled/rejected), 0-7 = active flow

  // usualOrder 0: Proposal Submitted, Awaiting Rental Application
  PROPOSAL_SUBMITTED_AWAITING_RENTAL_APP: {
    key: 'Proposal Submitted by guest - Awaiting Rental Application',
    color: 'blue',
    label: 'Submit Rental Application',
    stage: 1,
    usualOrder: 0,
    actions: ['submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  // Suggested proposal by Split Lease agent - awaiting rental application (sort_order 0)
  // NEW MEANING: Guest has CONFIRMED but rental app not yet submitted
  SUGGESTED_PROPOSAL_AWAITING_RENTAL_APP: {
    key: 'Proposal Submitted for guest by Split Lease - Awaiting Rental Application',
    color: 'purple',
    label: 'Suggested Proposal - Awaiting Rental App',
    stage: 1,
    usualOrder: 0,
    isSuggestedBySL: true,
    guestConfirmed: true,
    actions: ['submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  // Suggested proposal by Split Lease agent - pending confirmation (sort_order 0)
  // NEW MEANING: Initial state - guest hasn't confirmed yet
  SUGGESTED_PROPOSAL_PENDING_CONFIRMATION: {
    key: 'Proposal Submitted for guest by Split Lease - Pending Confirmation',
    color: 'purple',
    label: 'Suggested Proposal - Pending Confirmation',
    stage: 1,
    usualOrder: 0,
    isSuggestedBySL: true,
    guestConfirmed: false,
    actions: ['confirm_proposal', 'submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  PENDING_CONFIRMATION: {
    key: 'Pending Confirmation',
    color: 'blue',
    label: 'Awaiting Confirmation',
    stage: 1,
    usualOrder: 0,
    actions: ['cancel_proposal', 'request_vm', 'send_message']
  },

  // Legacy pending status (treated as sort_order 0)
  PENDING: {
    key: 'Pending',
    color: 'blue',
    label: 'Pending',
    stage: 1,
    usualOrder: 0,
    actions: ['cancel_proposal', 'request_vm', 'send_message']
  },

  // usualOrder 1: Host Review (after rental app submitted)
  HOST_REVIEW: {
    key: 'Host Review',
    color: 'blue',
    label: 'Under Host Review',
    stage: 3,
    usualOrder: 1,
    actions: ['request_vm', 'cancel_proposal', 'send_message']
  },

  // Rental Application Submitted - transitional state before Host Review (sort_order 1)
  RENTAL_APP_SUBMITTED: {
    key: 'Rental Application Submitted',
    color: 'blue',
    label: 'Application Under Review',
    stage: 2,
    usualOrder: 1,
    actions: ['request_vm', 'cancel_proposal', 'send_message']
  },

  // usualOrder 2: Host Counteroffer
  COUNTEROFFER_SUBMITTED_AWAITING_GUEST_REVIEW: {
    key: 'Host Counteroffer Submitted / Awaiting Guest Review',
    color: 'yellow',
    label: 'Review Host Counteroffer',
    stage: 3,
    usualOrder: 2,
    actions: ['review_counteroffer', 'compare_terms', 'accept_counteroffer', 'decline_counteroffer', 'request_vm', 'send_message']
  },

  // usualOrder 3: Accepted / Drafting
  PROPOSAL_OR_COUNTEROFFER_ACCEPTED: {
    key: 'Proposal or Counteroffer Accepted / Drafting Lease Documents',
    color: 'green',
    label: 'Accepted - Drafting Lease',
    stage: 4,
    usualOrder: 3,
    actions: ['request_vm', 'send_message']
  },

  // Reviewing Documents (variant of sort_order 3)
  REVIEWING_DOCUMENTS: {
    key: 'Reviewing Documents',
    color: 'blue',
    label: 'Reviewing Documents',
    stage: 4,
    usualOrder: 3,
    actions: ['review_documents', 'request_vm', 'send_message']
  },

  // usualOrder 4: Lease Documents Sent for Review
  LEASE_DOCUMENTS_SENT_FOR_REVIEW: {
    key: 'Lease Documents Sent for Review',
    color: 'blue',
    label: 'Review Lease Documents',
    stage: 4,
    usualOrder: 4,
    actions: ['review_documents', 'request_vm', 'send_message']
  },

  // usualOrder 5: Lease documents sent for legal signatures
  LEASE_DOCUMENTS_SENT_FOR_SIGNATURES: {
    key: 'Lease Documents Sent for Signatures',
    color: 'green',
    label: 'Sign Lease Documents',
    stage: 5,
    usualOrder: 5,
    actions: ['sign_documents', 'request_vm', 'send_message']
  },

  // usualOrder 6: Lease signed, awaiting payment (note: lowercase "payment" matches Bubble)
  LEASE_DOCUMENTS_SIGNED_AWAITING_PAYMENT: {
    key: 'Lease Documents Signed / Awaiting Initial payment',
    color: 'green',
    label: 'Submit Initial Payment',
    stage: 6,
    usualOrder: 6,
    actions: ['submit_payment', 'request_vm', 'send_message']
  },

  // Legacy key format (keeping for backwards compatibility) - sort_order 6
  LEASE_SIGNED_AWAITING_INITIAL_PAYMENT: {
    key: 'Lease Signed / Awaiting Initial Payment',
    color: 'green',
    label: 'Submit Initial Payment',
    stage: 6,
    usualOrder: 6,
    actions: ['submit_payment', 'request_vm', 'send_message']
  },

  // usualOrder 7: Initial Payment / Lease Activated
  INITIAL_PAYMENT_SUBMITTED_LEASE_ACTIVATED: {
    key: 'Initial Payment Submitted / Lease activated ',
    color: 'green',
    label: 'Lease Activated',
    stage: 6,
