/**
 * Check if selected days form a contiguous block.
 * Handles week wrap-around cases (e.g., Fri-Mon).
 * 
 * @.claude\plans\Deprecated\Context\Option Sets\ZEP - Curation Parameters(OS).md {number[]} selectedDays - Array of day indices (0-6).
 * @returns {boolean} True if days are contiguous.
 */
export function isContiguousSelection(selectedDays) {
  if (!selectedDays || selectedDays.length === 0) return false;
  if (selectedDays.length === 1) return true;
  if (selectedDays.length >= 6) return true;

  const sorted = [...selectedDays].sort((a, b) => a - b);
  const isStandardContiguous = sorted.every((day, i) => i === 0 || day === sorted[i - 1] + 1);
  if (isStandardContiguous) return true;

  // Wrap-around check
  const hasZero = sorted.includes(0);
  const hasSix = sorted.includes(6);
  if (hasZero && hasSix) {
    const allDays = [0, 1, 2, 3, 4, 5, 6];
    const notSelectedDays = allDays.filter(d => !sorted.includes(d));

    const minNotSelected = Math.min(...notSelectedDays);
    const maxNotSelected = Math.max(...notSelectedDays);

    return notSelectedDays.length === (maxNotSelected - minNotSelected + 1);
  }

  return false;
}

/**
 * Workflow: Load complete proposal details with all enrichments.
 *
 * @intent Orchestrate loading and processing of proposal data from Supabase.
 * @rule Separates infrastructure (fetching) from domain logic (processing).
 * @rule Fetches raw data, then transforms via processors.
 * @rule Returns processed, validated proposal object ready for UI consumption.
 *
 * This workflow replaces the mixed fetcher/transformer pattern from proposalDataFetcher.js
 * by explicitly separating:
 * - Data fetching (infrastructure - still uses Supabase client)
 * - Data processing (domain logic - uses Logic Core processors)
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.rawProposal - Base proposal object (minimal data).
 * @param {object} [params.processListingData] - Listing processor function.
 * @param {object} [params.processUserData] - User processor function.
 * @param {object} [params.processProposalData] - Proposal processor function.
 * @returns {Promise<object>} Fully enriched and processed proposal object.
 *
 * @throws {Error} If rawProposal is missing.
 * @throws {Error} If critical data cannot be loaded.
 *
 * @example
 * import { loadProposalDetailsWorkflow } from '../logic/workflows/booking/loadProposalDetailsWorkflow.js'
 * import { processProposalData } from '../logic/processors/proposal/processProposalData.js'
 * import { processUserData } from '../logic/processors/user/processUserData.js'
 * import { processListingData } from '../logic/processors/listing/processListingData.js'
 * import { supabase } from '../lib/supabase.js'
 *
 * const enrichedProposal = await loadProposalDetailsWorkflow({
 *   supabase,
 *   rawProposal: { _id: 'abc123', Listing: 'xyz', Guest: '456' },
 *   processProposalData,
 *   processUserData,
 *   processListingData
 * })
 */
export async function loadProposalDetailsWorkflow({
  supabase,
  rawProposal,
  processListingData,
  processUserData,
  processProposalData
}) {
  // Validation
  if (!rawProposal) {
    throw new Error('loadProposalDetailsWorkflow: rawProposal is required')
  }

  if (!supabase) {
    throw new Error('loadProposalDetailsWorkflow: supabase client is required')
  }

  // Step 1: Fetch listing data
  let processedListing = null
  if (rawProposal.Listing) {
    const { data: listingData, error: listingError } = await supabase
      .from('listing')
      .select('*')
      .eq('_id', rawProposal.Listing)
      .single()

    if (!listingError && listingData) {
      // Process listing if processor provided
      if (processListingData) {
        try {
          processedListing = processListingData({ rawListing: listingData })
        } catch (err) {
          console.warn('Warning: Failed to process listing data:', err.message)
          // Continue without processed listing - UI will handle gracefully
        }
      } else {
        // No processor provided, use raw data
        processedListing = listingData
      }
    }
  }

  // Step 2: Fetch guest user data
  let processedGuest = null
  if (rawProposal.Guest) {
    const { data: guestData, error: guestError } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', rawProposal.Guest)
      .single()

    if (!guestError && guestData) {
      // Process user if processor provided
      if (processUserData) {
        try {
          processedGuest = processUserData({ rawUser: guestData })
        } catch (err) {
          console.warn('Warning: Failed to process guest data:', err.message)
        }
      } else {
        processedGuest = guestData
      }
    }
  }

  // Step 3: Fetch host user data (from listing creator)
  let processedHost = null
  if (processedListing && processedListing['Created By']) {
    const { data: hostData, error: hostError } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', processedListing['Created By'])
      .single()

    if (!hostError && hostData) {
      // Process user if processor provided
      if (processUserData) {
        try {
          processedHost = processUserData({ rawUser: hostData })
        } catch (err) {
          console.warn('Warning: Failed to process host data:', err.message)
        }
      } else {
        processedHost = hostData
      }
    }
  }

  // Step 4: Fetch house rules (if any)
  let houseRules = null
  if (
    rawProposal['House Rules'] &&
    Array.isArray(rawProposal['House Rules']) &&
    rawProposal['House Rules'].length > 0
  ) {
    const { data: rulesData, error: rulesError } = await supabase
      .schema('reference_table')
      .from('zat_features_houserule')
      .select('_id, Name, Icon')
      .in('_id', rawProposal['House Rules'])

    if (!rulesError && rulesData) {
      houseRules = rulesData
    }
  }

  // Step 5: Fetch virtual meeting data (if any)
  let virtualMeeting = null
  if (rawProposal['virtual meeting']) {
    const { data: vmData, error: vmError } = await supabase
      .from('virtualmeetingschedulesandlinks')
      .select('*')
      .eq('_id', rawProposal['virtual meeting'])
      .single()

    if (!vmError && vmData) {
      virtualMeeting = vmData
    }
  }

  // Step 6: Process proposal with all enriched data
  let processedProposal
  if (processProposalData) {
    try {
      processedProposal = processProposalData({
        rawProposal,
        listing: processedListing,
        guest: processedGuest,
        host: processedHost
      })
    } catch (err) {
      // If processor throws, this is a critical error
      throw new Error(`Failed to process proposal data: ${err.message}`)
    }
  } else {
    // No processor provided, create basic structure
    processedProposal = {
      ...rawProposal,
      _listing: processedListing,
      _guest: processedGuest,
      _host: processedHost
    }
  }

  // Step 7: Attach additional enrichments
  if (houseRules) {
    processedProposal._houseRules = houseRules
  }

  if (virtualMeeting) {
    processedProposal._virtualMeeting = virtualMeeting
  }

  return processedProposal
}

/**
 * Process raw user data from Supabase into a clean, validated user object.
 *
 * @intent Transform raw user rows from Supabase into consistent, UI-ready format.
 * @rule NO FALLBACK - Throws explicit errors for missing critical fields.
 * @rule Sanitizes user data for privacy and security.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.rawUser - Raw user object from Supabase.
 * @param {boolean} [params.requireVerification=false] - Whether to enforce verification fields.
 * @returns {object} Clean, validated user object.
 *
 * @throws {Error} If rawUser is null/undefined.
 * @throws {Error} If critical _id field is missing.
 * @throws {Error} If Name - Full is missing.
 * @throws {Error} If verification is required but user not verified.
 *
 * @example
 * const user = processUserData({
 *   rawUser: {
 *     _id: '123',
 *     'Name - Full': 'Jane Doe',
 *     'Name - First': 'Jane',
 *     'Profile Photo': 'https://...',
 *     'About Me / Bio': 'Software engineer...',
 *     'user verified?': true
 *   }
 * })
 */
export function processUserData({ rawUser, requireVerification = false }) {
  // No Fallback: User data must exist
  if (!rawUser) {
    throw new Error('processUserData: rawUser cannot be null or undefined')
  }

  // Validate critical ID field
  if (!rawUser._id) {
    throw new Error('processUserData: User missing critical _id field')
  }

  // Name handling - derive from available fields
  // Some users may not have "Name - Full" populated yet
  let fullName = rawUser['Name - Full']
  const firstName = rawUser['Name - First']
  const lastName = rawUser['Name - Last']

  // Build fullName from parts if not directly available
  if (!fullName || typeof fullName !== 'string' || fullName.trim().length === 0) {
    if (firstName && lastName) {
      fullName = `${firstName} ${lastName}`
    } else if (firstName) {
      fullName = firstName
    } else if (lastName) {
      fullName = lastName
    } else {
      // Use a default for users without any name fields
      fullName = 'Guest User'
      console.warn(`processUserData: User ${rawUser._id} has no name fields, using default`)
    }
  }

  // Verification enforcement (if required)
  if (requireVerification && !rawUser['user verified?']) {
    throw new Error(
      `processUserData: User ${rawUser._id} is not verified (required for this operation)`
    )
  }

  // Derive display firstName if not already set
  const displayFirstName = firstName || fullName.split(' ')[0]

  return {
    id: rawUser._id,
    fullName: fullName.trim(),
    firstName: (displayFirstName || '').trim(),

    // Profile information
    profilePhoto: rawUser['Profile Photo'] || null,
    bio: rawUser['About Me / Bio'] || null,

    // Contact information (sensitive - only include if present)
    email: rawUser['email as text'] || null,
    phone: rawUser['Phone Number (as text)'] || null,

    // Verification status
    isVerified: rawUser['user verified?'] === true,
    isEmailConfirmed: rawUser['is email confirmed'] === true,
    isPhoneVerified: rawUser['Verify - Phone'] === true,
    linkedInId: rawUser['Verify - Linked In ID'] || null,

    // Privacy: First name only for display
    displayName: (displayFirstName || 'Guest').trim()
  }
}

/**
 * Determine if a guest can accept a proposal (or host counteroffer).
 *
 * @intent Enforce business rules for when proposal acceptance is allowed.
 * @rule Guest can only accept when host has countered (Stage 2).
 * @rule Original proposals don't need acceptance (they're sent to host).
 * @rule Once accepted, VM scheduled, or cancelled, acceptance is not applicable.
 * @rule Deleted proposals cannot be accepted.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can accept this proposal.
 *
 * @example
 * canAcceptProposal({ proposalStatus: 'Host Countered' })
 * // => true
 *
 * canAcceptProposal({ proposalStatus: 'Pending' })
 * // => false (waiting for host, not guest)
 *
 * canAcceptProposal({ proposalStatus: 'Accepted' })
 * // => false (already accepted)
 */
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js'

export function canAcceptProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be accepted
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()

  // Can only accept when host has countered
  // This is the only status where guest needs to make an "accept" decision
  return status === PROPOSAL_STATUSES.COUNTEROFFER_SUBMITTED_AWAITING_GUEST_REVIEW.key.trim()
}

/**
 * Determine if a guest can cancel their proposal.
 *
 * @intent Enforce business rules for when proposal cancellation is allowed.
 * @rule Guest can cancel proposals in stages 1-4 (before completion).
 * @rule Once completed, proposals cannot be cancelled (only disputes apply).
 * @rule Already cancelled/rejected/expired proposals cannot be re-cancelled.
 * @rule Deleted proposals cannot be cancelled.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can cancel this proposal.
 *
 * @example
 * canCancelProposal({ proposalStatus: 'Pending' })
 * // => true
 *
 * canCancelProposal({ proposalStatus: 'Completed' })
 * // => false
 *
 * canCancelProposal({ proposalStatus: 'Cancelled by Guest' })
 * // => false (already cancelled)
 */
import { isTerminalStatus, isCompletedStatus } from '../../constants/proposalStatuses.js'

export function canCancelProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be cancelled
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()

  if (isTerminalStatus(status) || isCompletedStatus(status)) {
    return false
  }

  // Can cancel if in any active state
  // Includes: Draft, Pending, Host Countered, VM Requested, VM Confirmed, Accepted, Verified
  return true
}

/**
 * Determine if a guest can edit their proposal.
 *
 * @intent Enforce business rules for when proposal editing is allowed.
 * @rule Guest can only edit proposals in early stages (1-2).
 * @rule Once accepted, VM scheduled, or cancelled, editing is locked.
 * @rule Deleted proposals cannot be edited.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can edit this proposal.
 *
 * @example
 * canEditProposal({ proposalStatus: 'Pending' })
 * // => true
 *
 * canEditProposal({ proposalStatus: 'Accepted' })
 * // => false
 */
import { getActionsForStatus } from '../../constants/proposalStatuses.js'

export function canEditProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be edited
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()
  const actions = getActionsForStatus(status)

  return actions.includes('modify_proposal') || actions.includes('edit_proposal')
}

/**
 * Workflow: Cancel a proposal with complex decision tree.
 *
 * @intent Orchestrate proposal cancellation following business rules.
 * @rule Implements complete cancellation decision tree (7 variations).
 * @rule Different actions based on: source, current status, usual order, house manual access.
 * @rule Updates Supabase with appropriate status and cancellation reason.
 *
 * Decision Tree (from WORKFLOW-PASS2-ASSIMILATION.md):
 * 1. Already cancelled → Show alert, no action
 * 2. Compare modal source + Usual Order ≤5 → Status: "Cancelled by Guest"
 * 3. Compare modal source + Usual Order >5 → Alert to call, no DB update
 * 4. Main page + Usual Order ≤5 + No house manual → Status: "Cancelled by Guest"
 * 5. Main page + Usual Order >5 + No house manual → Alert to call, no DB update
 * 6. Main page + Usual Order ≤5 + Has house manual → Alert to call, no DB update
 * 7. Main page + Usual Order >5 + Has house manual → Alert to call, no DB update
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.proposal - Processed proposal object.
 * @param {string} params.source - 'main' | 'compare-modal' | 'other'.
 * @param {function} params.canCancelProposal - Rule function to check if cancellable.
 * @returns {Promise<object>} Result object with success status and message.
 *
 * @throws {Error} If required parameters are missing.
 *
 * @example
 * const result = await cancelProposalWorkflow({
 *   supabase,
 *   proposal: processedProposal,
 *   source: 'main',
 *   canCancelProposal
 * })
 * // => { success: true, message: 'Proposal cancelled', updated: true }
 */
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js'

export async function cancelProposalWorkflow({
  supabase,
  proposal,
  source = 'main',
  canCancelProposal
}) {
  // Validation
  if (!supabase) {
    throw new Error('cancelProposalWorkflow: supabase client is required')
  }

  if (!proposal || !proposal.id) {
    throw new Error('cancelProposalWorkflow: proposal with id is required')
  }

  if (!canCancelProposal) {
    throw new Error('cancelProposalWorkflow: canCancelProposal rule function is required')
  }

  // Step 1: Check if cancellation is allowed
  const canCancel = canCancelProposal({
    proposalStatus: proposal.status,
    deleted: proposal.deleted
  })

  if (!canCancel) {
    return {
      success: false,
      message: 'This proposal cannot be cancelled (already in terminal state)',
      updated: false
    }
  }

  // Step 2: Extract decision factors
  const usualOrder = proposal.usualOrder || 0
  const hasAccessedHouseManual = proposal.houseManualAccessed === true
  const isFromCompareModal = source === 'compare-modal'

  // Step 3: Apply decision tree
  let shouldUpdateDatabase = false
  let alertMessage = null

  if (isFromCompareModal) {
    // Compare Modal Source
    if (usualOrder <= 5) {
      // Variation 2: Update database
      shouldUpdateDatabase = true
    } else {
      // Variation 3: Alert to call
      alertMessage = 'Please call Split Lease to cancel this proposal (Usual Order > 5)'
    }
  } else {
    // Main Page Source
    if (!hasAccessedHouseManual) {
      if (usualOrder <= 5) {
        // Variation 4: Update database
        shouldUpdateDatabase = true
      } else {
        // Variation 5: Alert to call
        alertMessage = 'Please call Split Lease to cancel this proposal (Usual Order > 5)'
      }
    } else {
      // Has accessed house manual - always alert to call (Variations 6 & 7)
      alertMessage = 'Please call Split Lease to cancel this proposal (House Manual accessed)'
    }
  }

  // Step 4: Execute action
  if (shouldUpdateDatabase) {
    try {
      const { error } = await supabase
        .from('proposal')
        .update({
          'Status': PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key,
          'reason for cancellation': 'Guest initiated cancellation',
          'Modified Date': new Date().toISOString()
        })
        .eq('_id', proposal.id)

      if (error) {
        throw error
      }

      return {
        success: true,
        message: 'Proposal cancelled successfully',
        updated: true
      }
    } catch (err) {
      return {
        success: false,
        message: `Failed to cancel proposal: ${err.message}`,
        updated: false,
        error: err
      }
    }
  } else {
    // Alert only, no database update
    return {
      success: true,
      message: alertMessage,
      updated: false,
      requiresPhoneCall: true
    }
  }
}

