/**
 * Calculate form completion percentage for review submission.
 *
 * @intent Show progress feedback during review form completion.
 * @rule Counts ratings with non-zero values.
 *
 * @param {object} params - Named parameters.
 * @param {Array<{value: number}>} params.ratings - Array of rating objects.
 * @param {number} params.totalCategories - Expected total categories (default 12).
 * @returns {number} Completion percentage (0-100).
 */
export function calculateFormCompletion({ ratings, totalCategories = 12 }) {
  if (!Array.isArray(ratings)) {
    return 0;
  }

  const completed = ratings.filter(r => r.value > 0).length;
  return Math.round((completed / totalCategories) * 100);
}

/**
 * Centralized pricing constants for the Split Lease platform.
 * These values affect guest-facing prices across the application.
 */
export const PRICING_CONSTANTS = {
  /** Full-time stay discount (7 nights/week) as decimal */
  FULL_TIME_DISCOUNT_RATE: 0.13,

  /** Site markup applied to all bookings as decimal */
  SITE_MARKUP_RATE: 0.17,

  /** Minimum nights for full-time discount eligibility */
  FULL_TIME_NIGHTS_THRESHOLD: 7,

  /** Valid night range for pricing calculations */
  MIN_NIGHTS: 2,
  MAX_NIGHTS: 7,

  /** Standard billing cycle in weeks */
  BILLING_CYCLE_WEEKS: 4
};

