   386→    return PROPOSAL_STATUSES.SUGGESTED_PROPOSAL_AWAITING_RENTAL_APP.key;
   387→  }
   388→  // Rental app already done → go straight to Host Review
   389→  return PROPOSAL_STATUSES.HOST_REVIEW.key;
   390→}
