import { getStatusConfig, PROPOSAL_STATUS } from '../../../config/proposalStatusConfig.js';

/**
 * Compute button states for a proposal card (pure function).
 * @param {object} params - Named parameters
 * @returns {object} Button states
 */
export function computeProposalButtonStates({ proposal, virtualMeeting, guest, listing, currentUserId }) {
  if (!proposal) {
    return {
      virtualMeeting: { visible: false },
      guestAction1: { visible: false },
      guestAction2: { visible: false },
      cancelProposal: { visible: false },
    };
  }

  const rawStatus = proposal.Status || proposal.status;
  const status = typeof rawStatus === 'string' ? rawStatus.trim() : rawStatus;
  const config = getStatusConfig(status);
  const vm = virtualMeeting;

  // ========== BUTTON 1: Virtual Meeting ==========
  const vmHiddenStatuses = [
    PROPOSAL_STATUS.REJECTED_BY_HOST,
    PROPOSAL_STATUS.CANCELLED_BY_SPLITLEASE,
    PROPOSAL_STATUS.INITIAL_PAYMENT_LEASE_ACTIVATED,
    PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_AWAITING_APPLICATION,
    PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_PENDING_CONFIRMATION,
  ].map(s => (typeof s === 'string' ? s.trim() : s));

  let vmButton = { visible: !vmHiddenStatuses.includes(status) };

  if (vmButton.visible) {
    if (vm?.['meeting declined']) {
      vmButton = {
        ...vmButton,
        label: 'Virtual Meeting Declined',
        fontColor: '#DB2E2E',
        bold: true,
        tooltip: 'click to request another one',
        disabled: false,
      };
    } else if (vm?.['booked date'] && vm?.['confirmedBySplitLease']) {
      vmButton = { ...vmButton, label: 'Meeting confirmed', disabled: true };
    } else if (vm?.['booked date']) {
      vmButton = { ...vmButton, label: 'Virtual Meeting Accepted', disabled: true };
    } else if (vm?.['requested by'] === currentUserId) {
      vmButton = { ...vmButton, label: 'Virtual Meeting Requested', disabled: true };
    } else if (vm?.['requested by']) {
      vmButton = { ...vmButton, label: 'Respond to Virtual Meeting Request', disabled: false };
    } else {
      vmButton = { ...vmButton, label: 'Request Virtual Meeting', disabled: false };
    }
  }

  // ========== BUTTON 2: Guest Action 1 ==========
  let ga1Button = { visible: !!status && config.guestAction1 !== 'Invisible' };

  if (ga1Button.visible) {
    // Reminder limit check
    if (config.guestAction1 === 'Remind Split Lease' && (proposal['remindersByGuest (number)'] || 0) > 3) {
      ga1Button.visible = false;
    }
    // Lease docs finalized check
    if (status === PROPOSAL_STATUS.LEASE_DOCUMENTS_SENT && !proposal['guest documents review finalized?']) {
      ga1Button.visible = false;
    }
  }

  if (ga1Button.visible) {
    if (status === PROPOSAL_STATUS.REJECTED_BY_HOST) {
      ga1Button = { ...ga1Button, label: 'Delete Proposal', backgroundColor: '#EF4444' };
    } else {
      ga1Button = { ...ga1Button, label: config.guestAction1 };
    }
  }

  // ========== BUTTON 3: Guest Action 2 ==========
  let ga2Button = { visible: config.guestAction2 !== 'Invisible' };

  if (ga2Button.visible) {
    // ID docs submitted check
    if (status === PROPOSAL_STATUS.LEASE_DOCUMENTS_SENT && !guest?.['ID documents submitted?']) {
      ga2Button.visible = false;
    }
  }

  if (ga2Button.visible) {
    ga2Button = { ...ga2Button, label: config.guestAction2 };
  }

  // ========== BUTTON 4: Cancel Proposal ==========
  const cancelHiddenStatuses = [
    PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_AWAITING_APPLICATION,
    PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_PENDING_CONFIRMATION,
  ].map(s => (typeof s === 'string' ? s.trim() : s));

  let cancelButton = { visible: !cancelHiddenStatuses.includes(status) };

  if (cancelButton.visible) {
    const terminalStatuses = [
      PROPOSAL_STATUS.CANCELLED_BY_GUEST,
      PROPOSAL_STATUS.CANCELLED_BY_SPLITLEASE,
      PROPOSAL_STATUS.REJECTED_BY_HOST,
    ].map(s => (typeof s === 'string' ? s.trim() : s));

    if (terminalStatuses.includes(status)) {
      cancelButton = { ...cancelButton, label: 'Delete Proposal' };
    } else if (status === PROPOSAL_STATUS.HOST_COUNTEROFFER) {
      cancelButton = { ...cancelButton, label: 'Reject Modified Terms' };
    } else if (config.usualOrder > 5 && listing?.['House manual']) {
      cancelButton = { ...cancelButton, label: 'See House Manual', backgroundColor: '#6D31C2' };
    } else if (config.usualOrder > 5) {
      cancelButton.visible = true;
      cancelButton.label = 'Cancel Proposal';
    } else {
      cancelButton = { ...cancelButton, label: 'Cancel Proposal' };
    }
  }

  return {
    virtualMeeting: vmButton,
    guestAction1: ga1Button,
    guestAction2: ga2Button,
    cancelProposal: cancelButton,
  };
}

/**
 * Determine if a guest can cancel their proposal.
 *
 * @intent Enforce business rules for when proposal cancellation is allowed.
 * @rule Guest can cancel proposals in stages 1-4 (before completion).
 * @rule Once completed, proposals cannot be cancelled (only disputes apply).
 * @rule Already cancelled/rejected/expired proposals cannot be re-cancelled.
 * @rule Deleted proposals cannot be cancelled.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can cancel this proposal.
 *
 * @example
 * canCancelProposal({ proposalStatus: 'Pending' })
 * // => true
 *
 * canCancelProposal({ proposalStatus: 'Completed' })
 * // => false
 *
 * canCancelProposal({ proposalStatus: 'Cancelled by Guest' })
 * // => false (already cancelled)
 */
import { isTerminalStatus, isCompletedStatus } from '../../constants/proposalStatuses.js'

export function canCancelProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be cancelled
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()

  if (isTerminalStatus(status) || isCompletedStatus(status)) {
    return false
  }

  // Can cancel if in any active state
  // Includes: Draft, Pending, Host Countered, VM Requested, VM Confirmed, Accepted, Verified
  return true
}

/**
 * Determine if a guest can accept a proposal (or host counteroffer).
 *
 * @intent Enforce business rules for when proposal acceptance is allowed.
 * @rule Guest can only accept when host has countered (Stage 2).
 * @rule Original proposals don't need acceptance (they're sent to host).
 * @rule Once accepted, VM scheduled, or cancelled, acceptance is not applicable.
 * @rule Deleted proposals cannot be accepted.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can accept this proposal.
 *
 * @example
 * canAcceptProposal({ proposalStatus: 'Host Countered' })
 * // => true
 *
 * canAcceptProposal({ proposalStatus: 'Pending' })
 * // => false (waiting for host, not guest)
 *
 * canAcceptProposal({ proposalStatus: 'Accepted' })
 * // => false (already accepted)
 */
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js'

export function canAcceptProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be accepted
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()

  // Can only accept when host has countered
  // This is the only status where guest needs to make an "accept" decision
  return status === PROPOSAL_STATUSES.COUNTEROFFER_SUBMITTED_AWAITING_GUEST_REVIEW.key.trim()
}

/**
 * Determine if a guest can edit their proposal.
 *
 * @intent Enforce business rules for when proposal editing is allowed.
 * @rule Guest can only edit proposals in early stages (1-2).
 * @rule Once accepted, VM scheduled, or cancelled, editing is locked.
 * @rule Deleted proposals cannot be edited.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can edit this proposal.
 *
 * @example
 * canEditProposal({ proposalStatus: 'Pending' })
 * // => true
 *
 * canEditProposal({ proposalStatus: 'Accepted' })
 * // => false
 */
import { getActionsForStatus } from '../../constants/proposalStatuses.js'

export function canEditProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be edited
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()
  const actions = getActionsForStatus(status)

  return actions.includes('modify_proposal') || actions.includes('edit_proposal')
}

/**
 * Calculate guest-facing price per night after markup and discounts.
 * This is the price displayed on listing cards in search results.
 *
 * @intent Calculate final guest-facing price from host compensation rate.
 * @rule Apply 13% full-time discount for 7 nights only.
 * @rule Apply 17% site markup to all prices.
 * @rule Return price per night (total price / nights).
 *
 * Formula:
 * 1. Base price = host rate × nights
 * 2. Full-time discount = base price × 0.13 (only if 7 nights)
 * 3. Price after discounts = base price - discount
 * 4. Site markup = price after discounts × 0.17
 * 5. Total price = base price - discount + markup
 * 6. Price per night = total price / nights
 *
 * @param {object} params - Named parameters.
 * @param {number} params.hostNightlyRate - Host compensation rate per night.
 * @param {number} params.nightsCount - Number of nights selected (2-7).
 * @returns {number} Guest-facing price per night.
 *
 * @throws {Error} If hostNightlyRate is not a valid positive number.
 * @throws {Error} If nightsCount is not between 2-7.
 *
 * @example
 * const price = calculateGuestFacingPrice({ hostNightlyRate: 100, nightsCount: 5 })
 * // => 117 (per night, after markup)
 *
 * const fullWeekPrice = calculateGuestFacingPrice({ hostNightlyRate: 100, nightsCount: 7 })
 * // => 102.35 (per night, with 13% discount + 17% markup)
 */
import { PRICING_CONSTANTS } from '../../constants/pricingConstants.js';

export function calculateGuestFacingPrice({ hostNightlyRate, nightsCount }) {
  // No Fallback: Strict validation
  if (
    typeof hostNightlyRate !== 'number' ||
    isNaN(hostNightlyRate) ||
    hostNightlyRate < 0
  ) {
    throw new Error(
      `calculateGuestFacingPrice: hostNightlyRate must be a positive number, got ${hostNightlyRate}`
    )
  }

  if (
    typeof nightsCount !== 'number' ||
    isNaN(nightsCount) ||
    nightsCount < 2 ||
    nightsCount > 7
  ) {
    throw new Error(
      `calculateGuestFacingPrice: nightsCount must be between 2-7, got ${nightsCount}`
    )
  }

  // Step 1: Calculate base price (host rate × nights)
  const basePrice = hostNightlyRate * nightsCount

  // Step 2: Apply full-time discount (only for 7 nights)
  const fullTimeDiscount = nightsCount === PRICING_CONSTANTS.FULL_TIME_NIGHTS_THRESHOLD
    ? basePrice * PRICING_CONSTANTS.FULL_TIME_DISCOUNT_RATE
    : 0;

  // Step 3: Price after discounts
  const priceAfterDiscounts = basePrice - fullTimeDiscount;

  // Step 4: Apply site markup
  const siteMarkup = priceAfterDiscounts * PRICING_CONSTANTS.SITE_MARKUP_RATE;

  // Step 5: Calculate total price
  const totalPrice = basePrice - fullTimeDiscount + siteMarkup

  // Step 6: Calculate price per night (guest-facing price)
  const pricePerNight = totalPrice / nightsCount

  return pricePerNight
}

