  const status = proposal.status || proposal.Status;
  if (status === PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key || ...) return false;
  if (isCompletedStatus(status)) return false;
  if (status === PROPOSAL_STATUSES.EXPIRED.key) return false;
  return true;
}
