/**
 * Calculates the baseline rent for a standard 4-week period.
 *
 * @intent Determine the recurring monthly cost basis before fees.
 * @rule Four weeks is the standard billing cycle for split lease.
 *
 * @param {object} params - Named parameters for clarity.
 * @param {number} params.nightlyRate - The base cost per night in USD.
 * @param {number} params.frequency - The number of nights per week (2-7).
 * @returns {number} The total rent for a 4-week cycle.
 *
 * @throws {Error} If nightlyRate or frequency is not a number.
 * @throws {Error} If nightlyRate is negative or NaN.
 * @throws {Error} If frequency is outside the valid range (2-7).
 *
 * @example
 * const rent = calculateFourWeekRent({ nightlyRate: 100, frequency: 4 })
 * // => 1600 (100 * 4 * 4)
 */
export function calculateFourWeekRent({ nightlyRate, frequency }) {
  // No Fallback: Strict type validation
  if (typeof nightlyRate !== 'number' || isNaN(nightlyRate)) {
    throw new Error(
      `calculateFourWeekRent: nightlyRate must be a number, got ${typeof nightlyRate}`
    )
  }

  if (typeof frequency !== 'number' || isNaN(frequency)) {
    throw new Error(
      `calculateFourWeekRent: frequency must be a number, got ${typeof frequency}`
    )
  }

  // Business rule validation
  if (nightlyRate < 0) {
    throw new Error(
      `calculateFourWeekRent: nightlyRate cannot be negative, got ${nightlyRate}`
    )
  }

  if (frequency < 2 || frequency > 7) {
    throw new Error(
      `calculateFourWeekRent: frequency must be between 2-7 nights, got ${frequency}`
    )
  }

  // Pure calculation
  return nightlyRate * frequency * 4
}

import { getNightlyRateByFrequency } from './getNightlyRateByFrequency.js'
import { calculateFourWeekRent } from './calculateFourWeekRent.js'
import { calculateReservationTotal } from './calculateReservationTotal.js'

/**
 * Calculate complete pricing breakdown for a listing rental.
 *
 * @intent Provide comprehensive price calculation including all fees.
 * @rule Combines nightly rate, 4-week rent, reservation total, and fees.
 * @rule All calculations must succeed or throw - no partial results.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.listing - Listing object with all pricing fields.
 * @param {number} params.nightsPerWeek - Nights selected per week (2-7).
 * @param {number} params.reservationWeeks - Total reservation span in weeks.
 * @returns {object} Complete pricing breakdown with all calculated values.
 *
 * @throws {Error} If any required parameter is missing or invalid.
 * @throws {Error} If any calculation in the chain fails.
 *
 * @example
 * const breakdown = calculatePricingBreakdown({
 *   listing: { 'ðŸ’°Nightly Host Rate for 4 nights': 100, 'ðŸ’°Cleaning Cost / Maintenance Fee': 50 },
 *   nightsPerWeek: 4,
 *   reservationWeeks: 13
 * })
 * // => { nightlyPrice: 100, fourWeekRent: 1600, reservationTotal: 5200, ... }
 */
export function calculatePricingBreakdown({ listing, nightsPerWeek, reservationWeeks }) {
  // No Fallback: Validate all inputs
  if (!listing || typeof listing !== 'object') {
    throw new Error(
      'calculatePricingBreakdown: listing must be a valid object'
    )
  }

  if (typeof nightsPerWeek !== 'number' || isNaN(nightsPerWeek)) {
    throw new Error(
      `calculatePricingBreakdown: nightsPerWeek must be a number, got ${typeof nightsPerWeek}`
    )
  }

  if (typeof reservationWeeks !== 'number' || isNaN(reservationWeeks)) {
    throw new Error(
      `calculatePricingBreakdown: reservationWeeks must be a number, got ${typeof reservationWeeks}`
    )
  }

  // Get nightly rate (throws if not found)
  const nightlyPrice = getNightlyRateByFrequency({
    listing,
    nightsSelected: nightsPerWeek
  })

  // Calculate 4-week rent (throws on invalid input)
  const fourWeekRent = calculateFourWeekRent({
    nightlyRate: nightlyPrice,
    frequency: nightsPerWeek
  })

  // Calculate reservation total (throws on invalid input)
  const reservationTotal = calculateReservationTotal({
    fourWeekRent,
    totalWeeks: reservationWeeks
  })

  // Extract fees (with explicit validation)
  const cleaningFee = extractFee(
    listing['ðŸ’°Cleaning Cost / Maintenance Fee'],
    'Cleaning Fee'
  )

  const damageDeposit = extractFee(
    listing['ðŸ’°Damage Deposit'],
    'Damage Deposit'
  )

  // Calculate grand total
  const grandTotal = reservationTotal + cleaningFee

  return {
    nightlyPrice,
    fourWeekRent,
    reservationTotal,
    cleaningFee,
    damageDeposit,
    grandTotal,
    valid: true
  }
}

/**
 * Extract and validate a fee value from listing.
 * Returns 0 for explicitly missing optional fees (null/undefined).
 * Throws for invalid fee values.
 *
 * @param {any} feeValue - The fee value from listing
 * @param {string} feeName - Name of fee for error messages
 * @returns {number} Validated fee amount (0 if optional and missing)
 * @throws {Error} If fee value is invalid (e.g., negative, NaN)
 */
function extractFee(feeValue, feeName) {
  // Optional fees can be null/undefined - default to 0
  if (feeValue === null || feeValue === undefined) {
    return 0
  }

  const fee = Number(feeValue)

  if (isNaN(fee)) {
    throw new Error(
      `calculatePricingBreakdown: ${feeName} has invalid value ${feeValue}`
    )
  }

  if (fee < 0) {
    throw new Error(
      `calculatePricingBreakdown: ${feeName} cannot be negative, got ${fee}`
    )
  }

  return fee
}

/**
 * Calculate estimated reservation total for the full stay period.
 *
 * @intent Determine the total cost across all weeks of the reservation.
 * @rule Total is calculated as (4-week rent) * (total weeks / 4).
 *
 * @param {object} params - Named parameters for clarity.
 * @param {number} params.fourWeekRent - The calculated 4-week rent amount.
 * @param {number} params.totalWeeks - Total reservation span in weeks.
 * @returns {number} Estimated total cost for the entire reservation.
 *
 * @throws {Error} If fourWeekRent or totalWeeks is not a number.
 * @throws {Error} If fourWeekRent is negative or NaN.
 * @throws {Error} If totalWeeks is less than or equal to 0.
 *
 * @example
 * const total = calculateReservationTotal({ fourWeekRent: 1600, totalWeeks: 13 })
 * // => 5200 (1600 * 13 / 4)
 */
export function calculateReservationTotal({ fourWeekRent, totalWeeks }) {
  // No Fallback: Strict type validation
  if (typeof fourWeekRent !== 'number' || isNaN(fourWeekRent)) {
    throw new Error(
      `calculateReservationTotal: fourWeekRent must be a number, got ${typeof fourWeekRent}`
    )
  }

  if (typeof totalWeeks !== 'number' || isNaN(totalWeeks)) {
    throw new Error(
      `calculateReservationTotal: totalWeeks must be a number, got ${typeof totalWeeks}`
    )
  }

  // Business rule validation
  if (fourWeekRent < 0) {
    throw new Error(
      `calculateReservationTotal: fourWeekRent cannot be negative, got ${fourWeekRent}`
    )
  }

  if (totalWeeks <= 0) {
    throw new Error(
      `calculateReservationTotal: totalWeeks must be positive, got ${totalWeeks}`
    )
  }

  // Pure calculation
  return fourWeekRent * (totalWeeks / 4)
}

/**
 * Get nightly price based on number of nights selected.
 * Matches Bubble logic for price field selection.
 *
 * @intent Retrieve the appropriate nightly rate tier based on frequency.
 * @rule Price override takes precedence over frequency-based rates.
 * @rule Frequency-specific rates (2, 3, 4, 5, 7 nights) map to specific price fields.
 * @rule No fallback to default rate - throws if no valid rate found.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.listing - Listing object with price fields.
 * @param {number} params.nightsSelected - Number of nights per week (2-7).
 * @returns {number} Nightly price for the selected frequency.
 *
 * @throws {Error} If listing is missing or invalid.
 * @throws {Error} If nightsSelected is not a number or out of range.
 * @throws {Error} If no valid price found for the selected nights.
 *
 * @example
 * const rate = getNightlyRateByFrequency({
 *   listing: { 'ðŸ’°Nightly Host Rate for 4 nights': 100 },
 *   nightsSelected: 4
 * })
 * // => 100
 */
export function getNightlyRateByFrequency({ listing, nightsSelected }) {
  // No Fallback: Validate listing exists
  if (!listing || typeof listing !== 'object') {
    throw new Error(
      'getNightlyRateByFrequency: listing must be a valid object'
    )
  }

  // No Fallback: Validate nightsSelected
  if (typeof nightsSelected !== 'number' || isNaN(nightsSelected)) {
    throw new Error(
      `getNightlyRateByFrequency: nightsSelected must be a number, got ${typeof nightsSelected}`
    )
  }

  if (nightsSelected < 2 || nightsSelected > 7) {
    throw new Error(
      `getNightlyRateByFrequency: nightsSelected must be between 2-7, got ${nightsSelected}`
    )
  }

  // Price override takes precedence
  if (listing['ðŸ’°Price Override']) {
    const overridePrice = Number(listing['ðŸ’°Price Override'])
    if (isNaN(overridePrice) || overridePrice < 0) {
      throw new Error(
        `getNightlyRateByFrequency: Invalid price override value ${listing['ðŸ’°Price Override']}`
      )
    }
    return overridePrice
  }

  // Map nights to price fields
  const priceFieldMap = {
    1: 'ðŸ’°Nightly Host Rate for 1 night',
    2: 'ðŸ’°Nightly Host Rate for 2 nights',
    3: 'ðŸ’°Nightly Host Rate for 3 nights',
    4: 'ðŸ’°Nightly Host Rate for 4 nights',
    5: 'ðŸ’°Nightly Host Rate for 5 nights',
    6: 'ðŸ’°Nightly Host Rate for 6 nights',
    7: 'ðŸ’°Nightly Host Rate for 7 nights'
  }

  const fieldName = priceFieldMap[nightsSelected]

  // No Fallback: Exact match required
  if (!fieldName || !listing[fieldName]) {
    throw new Error(
      `getNightlyRateByFrequency: No price found for ${nightsSelected} nights in listing`
    )
  }

  const rate = Number(listing[fieldName])

  if (isNaN(rate) || rate < 0) {
    throw new Error(
      `getNightlyRateByFrequency: Invalid rate value ${listing[fieldName]} for ${nightsSelected} nights`
    )
  }

  return rate
}

/**
 * Calculate guest-facing price per night after markup and discounts.
 * This is the price displayed on listing cards in search results.
 *
 * @intent Calculate final guest-facing price from host compensation rate.
 * @rule Apply 13% full-time discount for 7 nights only.
 * @rule Apply 17% site markup to all prices.
 * @rule Return price per night (total price / nights).
 *
 * Formula:
 * 1. Base price = host rate Ã— nights
 * 2. Full-time discount = base price Ã— 0.13 (only if 7 nights)
 * 3. Price after discounts = base price - discount
 * 4. Site markup = price after discounts Ã— 0.17
 * 5. Total price = base price - discount + markup
 * 6. Price per night = total price / nights
 *
 * @param {object} params - Named parameters.
 * @param {number} params.hostNightlyRate - Host compensation rate per night.
 * @param {number} params.nightsCount - Number of nights selected (2-7).
 * @returns {number} Guest-facing price per night.
 *
 * @throws {Error} If hostNightlyRate is not a valid positive number.
 * @throws {Error} If nightsCount is not between 2-7.
 *
 * @example
 * const price = calculateGuestFacingPrice({ hostNightlyRate: 100, nightsCount: 5 })
 * // => 117 (per night, after markup)
 *
 * const fullWeekPrice = calculateGuestFacingPrice({ hostNightlyRate: 100, nightsCount: 7 })
 * // => 102.35 (per night, with 13% discount + 17% markup)
 */
import { PRICING_CONSTANTS } from '../../constants/pricingConstants.js';

export function calculateGuestFacingPrice({ hostNightlyRate, nightsCount }) {
  // No Fallback: Strict validation
  if (
    typeof hostNightlyRate !== 'number' ||
    isNaN(hostNightlyRate) ||
    hostNightlyRate < 0
  ) {
    throw new Error(
      `calculateGuestFacingPrice: hostNightlyRate must be a positive number, got ${hostNightlyRate}`
    )
  }

  if (
    typeof nightsCount !== 'number' ||
    isNaN(nightsCount) ||
    nightsCount < 2 ||
    nightsCount > 7
  ) {
    throw new Error(
      `calculateGuestFacingPrice: nightsCount must be between 2-7, got ${nightsCount}`
    )
  }

  // Step 1: Calculate base price (host rate Ã— nights)
  const basePrice = hostNightlyRate * nightsCount

  // Step 2: Apply full-time discount (only for 7 nights)
  const fullTimeDiscount = nightsCount === PRICING_CONSTANTS.FULL_TIME_NIGHTS_THRESHOLD
    ? basePrice * PRICING_CONSTANTS.FULL_TIME_DISCOUNT_RATE
    : 0;

  // Step 3: Price after discounts
  const priceAfterDiscounts = basePrice - fullTimeDiscount;

  // Step 4: Apply site markup
  const siteMarkup = priceAfterDiscounts * PRICING_CONSTANTS.SITE_MARKUP_RATE;

  // Step 5: Calculate total price
  const totalPrice = basePrice - fullTimeDiscount + siteMarkup

  // Step 6: Calculate price per night (guest-facing price)
  const pricePerNight = totalPrice / nightsCount

  return pricePerNight
}

/**
 * Centralized pricing constants for the Split Lease platform.
 * These values affect guest-facing prices across the application.
 */
export const PRICING_CONSTANTS = {
  /** Full-time stay discount (7 nights/week) as decimal */
  FULL_TIME_DISCOUNT_RATE: 0.13,

  /** Site markup applied to all bookings as decimal */
  SITE_MARKUP_RATE: 0.17,

  /** Minimum nights for full-time discount eligibility */
  FULL_TIME_NIGHTS_THRESHOLD: 7,

  /** Valid night range for pricing calculations */
  MIN_NIGHTS: 2,
  MAX_NIGHTS: 7,

  /** Standard billing cycle in weeks */
  BILLING_CYCLE_WEEKS: 4
};

import { DAY_NAMES } from '../../../lib/constants.js'

/**
 * Calculate check-in and check-out days from selected days.
 * Check-in is the first selected day, check-out is the day AFTER the last selected day.
 *
 * @intent Determine the boundary days for a weekly split lease schedule.
 * @rule Check-in is the first selected day in the sequence.
 * @rule Check-out is the day AFTER the last selected day (handles wrap-around).
 * @rule For wrap-around cases (e.g., Fri-Mon), identify the gap to determine boundaries.
 *
 * @param {object} params - Named parameters.
 * @param {number[]} params.selectedDays - Array of day indices (0-6, where 0=Sunday).
 * @returns {object} Check-in/check-out details with day indices and names.
 *
 * @throws {Error} If selectedDays is not an array.
 * @throws {Error} If selectedDays contains invalid day indices.
 * @throws {Error} If selectedDays is empty.
 *
 * @example
 * const result = calculateCheckInOutDays({ selectedDays: [1, 2, 3, 4, 5] })
 * // => { checkInDay: 1, checkOutDay: 6, checkInName: 'Monday', checkOutName: 'Saturday' }
 *
 * const wrapped = calculateCheckInOutDays({ selectedDays: [5, 6, 0, 1] })
 * // => { checkInDay: 5, checkOutDay: 2, checkInName: 'Friday', checkOutName: 'Tuesday' }
 */
export function calculateCheckInOutDays({ selectedDays }) {
  // No Fallback: Validate input
  if (!Array.isArray(selectedDays)) {
    throw new Error(
      `calculateCheckInOutDays: selectedDays must be an array, got ${typeof selectedDays}`
    )
  }

  if (selectedDays.length === 0) {
    throw new Error(
      'calculateCheckInOutDays: selectedDays cannot be empty'
    )
  }

  // Validate all day indices
  for (const day of selectedDays) {
    if (typeof day !== 'number' || isNaN(day) || day < 0 || day > 6) {
      throw new Error(
        `calculateCheckInOutDays: Invalid day index ${day}, must be 0-6`
      )
    }
  }

  const sorted = [...selectedDays].sort((a, b) => a - b)

  // Handle wrap-around case (selection includes both Sunday=0 and Saturday=6)
  const hasZero = sorted.includes(0)
  const hasSix = sorted.includes(6)

  if (hasZero && hasSix) {
    // Find gap to determine actual start/end
    let gapIndex = -1
    for (let i = 1; i < sorted.length; i++) {
      if (sorted[i] !== sorted[i - 1] + 1) {
        gapIndex = i
        break
      }
    }

    if (gapIndex !== -1) {
      // Wrapped selection: check-in is after the gap
      const checkInDay = sorted[gapIndex] // First day after gap
      const lastSelectedDay = sorted[gapIndex - 1] // Last day before gap

      // Check-out is the day AFTER the last selected day
      const checkOutDay = (lastSelectedDay + 1) % 7

      return {
        checkInDay,
        checkOutDay,
        checkInName: DAY_NAMES[checkInDay],
        checkOutName: DAY_NAMES[checkOutDay]
      }
    }
  }

  // Standard case: first selected day to day after last selected day
  const checkInDay = sorted[0]
  const lastSelectedDay = sorted[sorted.length - 1]

  // Check-out is the day AFTER the last selected day (wraps around week if needed)
  const checkOutDay = (lastSelectedDay + 1) % 7

  return {
    checkInDay,
    checkOutDay,
    checkInName: DAY_NAMES[checkInDay],
    checkOutName: DAY_NAMES[checkOutDay]
  }
}

/**
 * Calculate number of nights from selected days.
 *
 * @intent Determine the number of nights in a split lease stay.
 * @rule In split lease, nights = days selected (continuous selection).
 * @rule A stay from Monday to Friday (5 days) = 5 nights.
 *
 * @param {object} params - Named parameters.
 * @param {number[]} params.selectedDays - Array of day indices (0-6).
 * @returns {number} Number of nights in the stay.
 *
 * @throws {Error} If selectedDays is not an array.
 * @throws {Error} If selectedDays contains invalid values.
 *
 * @example
 * const nights = calculateNightsFromDays({ selectedDays: [1, 2, 3, 4, 5] })
 * // => 5
 */
export function calculateNightsFromDays({ selectedDays }) {
  // No Fallback: Validate input
  if (!Array.isArray(selectedDays)) {
    throw new Error(
      `calculateNightsFromDays: selectedDays must be an array, got ${typeof selectedDays}`
    )
  }

  // Validate all entries are numbers in valid range
  for (const day of selectedDays) {
    if (typeof day !== 'number' || isNaN(day) || day < 0 || day > 6) {
      throw new Error(
        `calculateNightsFromDays: Invalid day index ${day}, must be 0-6`
      )
    }
  }

  // In split lease, nights = days selected
  return selectedDays.length
}

