     1â†’/**
     2â†’ * Virtual Meeting Business Rules
     3â†’ *
     4â†’ * PILLAR II: Rule Engines (The "Conditional" Layer)
     5â†’ *
     6â†’ * This module encapsulates the predicate functions for virtual meetings.
     7â†’ * Based on Bubble.io's 5-state virtual meeting workflow.
     8â†’ *
     9â†’ * Virtual Meeting States:
    10â†’ * 1. No VM exists -> Show "Request Virtual Meeting" button
    11â†’ * 2. VM requested by host -> Show "Respond to Virtual Meeting"
    12â†’ * 3. VM booked but not confirmed -> Show meeting details, await confirmation
    13â†’ * 4. VM confirmed by Split Lease -> Show meeting link and join button
    14â†’ * 5. VM declined -> Show "Request Alternative Meeting" button
    15â†’ */
    16â†’
    17â†’/**
    18â†’ * Virtual Meeting State Enum
    19â†’ *
    20â†’ * IMPORTANT: State names are PERSPECTIVE-NEUTRAL
    21â†’ * - REQUESTED_BY_ME: Current user requested, waiting for other party's response
    22â†’ * - REQUESTED_BY_OTHER: Other party requested, current user should respond
    23â†’ *
    24â†’ * Legacy aliases are provided for backward compatibility with existing code.
    25â†’ */
    26â†’export const VM_STATES = {
    27â†’  NO_MEETING: 'no_meeting',
    28â†’  REQUESTED_BY_ME: 'requested_by_me',           // Current user requested
    29â†’  REQUESTED_BY_OTHER: 'requested_by_other',     // Other party requested
    30â†’  BOOKED_AWAITING_CONFIRMATION: 'booked_awaiting_confirmation',
    31â†’  CONFIRMED: 'confirmed',
    32â†’  DECLINED: 'declined',
    33â†’  EXPIRED: 'expired',                           // All suggested dates passed without booking
    34â†’  // Legacy aliases (for backward compatibility - will be removed in future)
    35â†’  REQUESTED_BY_GUEST: 'requested_by_me',        // Alias â†’ REQUESTED_BY_ME
    36â†’  REQUESTED_BY_HOST: 'requested_by_other'       // Alias â†’ REQUESTED_BY_OTHER
    37â†’};
    38â†’
    39â†’/**
    40â†’ * Parse suggested dates - handles both array and JSON string formats
    41â†’ * @param {Array|string} suggestedDates - Dates as array or JSON string
    42â†’ * @returns {Array} Array of date strings
    43â†’ */
    44â†’function parseSuggestedDates(suggestedDates) {
    45â†’  if (!suggestedDates) return [];
    46â†’
    47â†’  // If already an array, return as-is
    48â†’  if (Array.isArray(suggestedDates)) return suggestedDates;
    49â†’
    50â†’  // If it's a string, try to parse it as JSON
    51â†’  if (typeof suggestedDates === 'string') {
    52â†’    try {
    53â†’      const parsed = JSON.parse(suggestedDates);
    54â†’      return Array.isArray(parsed) ? parsed : [];
    55â†’    } catch (e) {
    56â†’      return [];
    57â†’    }
    58â†’  }
    59â†’
    60â†’  return [];
    61â†’}
    62â†’
    63â†’/**
    64â†’ * Check if all suggested dates have expired (are in the past)
    65â†’ *
    66â†’ * @param {Object} virtualMeeting - Virtual meeting object (normalized)
    67â†’ * @returns {boolean} True if all dates are expired
    68â†’ */
    69â†’export function areAllDatesExpired(virtualMeeting) {
    70â†’  if (!virtualMeeting) return false;
    71â†’
    72â†’  const now = new Date();
    73â†’  const bookedDate = virtualMeeting.bookedDate || virtualMeeting['booked date'];
    74â†’  const rawSuggestedDates = virtualMeeting.suggestedDates || virtualMeeting['suggested dates and times'];
    75â†’
    76â†’  // Parse suggested dates (handles JSON string or array)
    77â†’  const suggestedDates = parseSuggestedDates(rawSuggestedDates);
    78â†’
    79â†’  // If there's a booked date, check if it's in the past
    80â†’  if (bookedDate) {
    81â†’    return new Date(bookedDate) < now;
    82â†’  }
    83â†’
    84â†’  // If no suggested dates, not expired (just pending)
    85â†’  if (suggestedDates.length === 0) {
    86â†’    return false;
    87â†’  }
    88â†’
    89â†’  // Check if ALL suggested dates are in the past
    90â†’  return suggestedDates.every(dateStr => new Date(dateStr) < now);
    91â†’}
    92â†’
    93â†’/**
    94â†’ * Determine the current state of a virtual meeting
    95â†’ *
    96â†’ * @param {Object} virtualMeeting - Virtual meeting object
    97â†’ * @param {string} currentUserId - Current user's ID
    98â†’ * @returns {string} VM_STATES value
    99â†’ */
   100â†’export function getVirtualMeetingState(virtualMeeting, currentUserId) {
   101â†’  // State 1: No VM exists
   102â†’  if (!virtualMeeting) {
   103â†’    return VM_STATES.NO_MEETING;
   104â†’  }
   105â†’
   106â†’  // State 5: VM declined
   107â†’  if (virtualMeeting.meetingDeclined) {
   108â†’    return VM_STATES.DECLINED;
   109â†’  }
   110â†’
   111â†’  // State 4: VM confirmed by Split Lease
   112â†’  if (virtualMeeting.bookedDate && virtualMeeting.confirmedBySplitlease) {
   113â†’    return VM_STATES.CONFIRMED;
   114â†’  }
   115â†’
   116â†’  // State 3: VM booked but not confirmed
   117â†’  if (virtualMeeting.bookedDate && !virtualMeeting.confirmedBySplitlease) {
   118â†’    return VM_STATES.BOOKED_AWAITING_CONFIRMATION;
   119â†’  }
   120â†’
   121â†’  // State 6: All suggested dates expired without booking
   122â†’  // Check AFTER booked states but BEFORE requested states
   123â†’  if (areAllDatesExpired(virtualMeeting)) {
   124â†’    return VM_STATES.EXPIRED;
   125â†’  }
   126â†’
   127â†’  // State 2: VM requested but no booked date yet
   128â†’  if (virtualMeeting.requestedBy === currentUserId) {
   129â†’    return VM_STATES.REQUESTED_BY_ME;
   130â†’  }
   131â†’
   132â†’  // Other party requested, current user should respond
   133â†’  return VM_STATES.REQUESTED_BY_OTHER;
   134â†’}
   135â†’
   136â†’/**
   137â†’ * Check if a new VM can be requested
   138â†’ *
   139â†’ * @param {Object} virtualMeeting - Virtual meeting object (or null)
   140â†’ * @returns {boolean} True if new request can be made
   141â†’ */
   142â†’export function canRequestNewMeeting(virtualMeeting) {
   143â†’  if (!virtualMeeting) {
   144â†’    return true;
   145â†’  }
   146â†’
   147â†’  // Can request new meeting if previous was declined
   148â†’  if (virtualMeeting.meetingDeclined) {
   149â†’    return true;
   150â†’  }
   151â†’
   152â†’  // Can request new meeting if all dates expired
   153â†’  if (areAllDatesExpired(virtualMeeting)) {
   154â†’    return true;
   155â†’  }
   156â†’
   157â†’  return false;
   158â†’}
   159â†’
   160â†’/**
   161â†’ * Check if guest can respond to a VM request
   162â†’ *
   163â†’ * @param {Object} virtualMeeting - Virtual meeting object
   164â†’ * @param {string} currentUserId - Current user's ID
   165â†’ * @returns {boolean} True if guest can respond
   166â†’ */
   167â†’export function canRespondToMeeting(virtualMeeting, currentUserId) {
   168â†’  if (!virtualMeeting) {
   169â†’    return false;
   170â†’  }
   171â†’
   172â†’  // Can respond if host requested and no booked date yet
   173â†’  return (
   174â†’    virtualMeeting.requestedBy !== currentUserId &&
   175â†’    !virtualMeeting.bookedDate &&
   176â†’    !virtualMeeting.meetingDeclined
   177â†’  );
   178â†’}
   179â†’
   180â†’/**
   181â†’ * Check if VM button should be disabled
   182â†’ *
   183â†’ * @param {Object} virtualMeeting - Virtual meeting object
   184â†’ * @param {string} currentUserId - Current user's ID
   185â†’ * @returns {boolean} True if button should be disabled
   186â†’ */
   187â†’export function isVMButtonDisabled(virtualMeeting, currentUserId) {
   188â†’  if (!virtualMeeting) {
   189â†’    return false; // Can request new meeting
   190â†’  }
   191â†’
   192â†’  // Disable if guest requested and waiting for host response
   193â†’  if (
   194â†’    virtualMeeting.requestedBy === currentUserId &&
   195â†’    !virtualMeeting.bookedDate &&
   196â†’    !virtualMeeting.meetingDeclined
   197â†’  ) {
   198â†’    return true;
   199â†’  }
   200â†’
   201â†’  return false;
   202â†’}
   203â†’
   204â†’/**
   205â†’ * Check if meeting can be joined
   206â†’ *
   207â†’ * @param {Object} virtualMeeting - Virtual meeting object
   208â†’ * @returns {boolean} True if meeting can be joined
   209â†’ */
   210â†’export function canJoinMeeting(virtualMeeting) {
   211â†’  if (!virtualMeeting) {
   212â†’    return false;
   213â†’  }
   214â†’
   215â†’  return (
   216â†’    virtualMeeting.bookedDate &&
   217â†’    virtualMeeting.confirmedBySplitlease &&
   218â†’    virtualMeeting.meetingLink
   219â†’  );
   220â†’}
   221â†’
   222â†’/**
   223â†’ * Check if meeting details can be viewed
   224â†’ *
   225â†’ * @param {Object} virtualMeeting - Virtual meeting object
   226â†’ * @returns {boolean} True if meeting details can be viewed
   227â†’ */
