    35â†’    status === PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key ||
    36â†’    status === PROPOSAL_STATUSES.CANCELLED_BY_SPLITLEASE.key ||
    37â†’    status === PROPOSAL_STATUSES.REJECTED_BY_HOST.key
    38â†’  ) {
