    48â†’  if (status === PROPOSAL_STATUSES.EXPIRED.key) {
    49â†’    return false;
    50â†’  }
    51â†’
    52â†’  // Otherwise, can cancel
    53â†’  return true;
    54â†’}
    55â†’
    56â†’/**
    57â†’ * Check if a proposal can be modified/edited by the guest
    58â†’ * Only allowed in early stages before rental application submission
    59â†’ *
    60â†’ * @param {Object} proposal - Proposal object
