   101â†’}
   102â†’
   103â†’/**
   104â†’ * Check if guest can decline a counteroffer
   105â†’ *
   106â†’ * @param {Object} proposal - Proposal object
   107â†’ * @returns {boolean} True if counteroffer can be declined
   108â†’ */
   109â†’export function canDeclineCounteroffer(proposal) {
