   119â†’export function canSubmitRentalApplication(proposal) {
   120â†’  if (!proposal) {
   121â†’    return false;
   122â†’  }
   123â†’
   124â†’  const status = proposal.status || proposal.Status;
   125â†’
   126â†’  return status === PROPOSAL_STATUSES.PROPOSAL_SUBMITTED_AWAITING_RENTAL_APP.key;
