   210â†’  }
   211â†’
   212â†’  const status = proposal.status || proposal.Status;
   213â†’
   214â†’  return (
   215â†’    status === PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key ||
   216â†’    status === PROPOSAL_STATUSES.CANCELLED_BY_SPLITLEASE.key
