/**
 * Compute button states for a proposal card (pure function).
 * @param {object} params - Named parameters
 * @returns {object} Button states
 */
export function computeProposalButtonStates({ proposal, virtualMeeting, guest, listing, currentUserId }) {
  if (!proposal) {
    return {
      virtualMeeting: { visible: false },
      guestAction1: { visible: false },
      guestAction2: { visible: false },
      cancelProposal: { visible: false },
    };
  }

  const rawStatus = proposal.Status || proposal.status;
  const status = typeof rawStatus === 'string' ? rawStatus.trim() : rawStatus;
  const config = getStatusConfig(status);
  const vm = virtualMeeting;

  // ========== BUTTON 1: Virtual Meeting ==========
  const vmHiddenStatuses = [
    PROPOSAL_STATUS.REJECTED_BY_HOST,
    PROPOSAL_STATUS.CANCELLED_BY_SPLITLEASE,
    PROPOSAL_STATUS.INITIAL_PAYMENT_LEASE_ACTIVATED,
    PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_AWAITING_APPLICATION,
    PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_PENDING_CONFIRMATION,
  ].map(s => (typeof s === 'string' ? s.trim() : s));

  let vmButton = { visible: !vmHiddenStatuses.includes(status) };

  if (vmButton.visible) {
    if (vm?.['meeting declined']) {
      vmButton = {
        ...vmButton,
        label: 'Virtual Meeting Declined',
        fontColor: '#DB2E2E',
        bold: true,
        tooltip: 'click to request another one',
        disabled: false,
      };
    } else if (vm?.['booked date'] && vm?.['confirmedBySplitLease']) {
      vmButton = { ...vmButton, label: 'Meeting confirmed', disabled: true };
    } else if (vm?.['booked date']) {
      vmButton = { ...vmButton, label: 'Virtual Meeting Accepted', disabled: true };
    } else if (vm?.['requested by'] === currentUserId) {
      vmButton = { ...vmButton, label: 'Virtual Meeting Requested', disabled: true };
    } else if (vm?.['requested by']) {
      vmButton = { ...vmButton, label: 'Respond to Virtual Meeting Request', disabled: false };
    } else {
      vmButton = { ...vmButton, label: 'Request Virtual Meeting', disabled: false };
    }
  }

  // ========== BUTTON 2: Guest Action 1 ==========
  let ga1Button = { visible: !!status && config.guestAction1 !== 'Invisible' };

  if (ga1Button.visible) {
    // Reminder limit check
    if (config.guestAction1 === 'Remind Split Lease' && (proposal['remindersByGuest (number)'] || 0) > 3) {
      ga1Button.visible = false;
    }
    // Lease docs finalized check
    if (status === PROPOSAL_STATUS.LEASE_DOCUMENTS_SENT && !proposal['guest documents review finalized?']) {
      ga1Button.visible = false;
    }
  }

  if (ga1Button.visible) {
    if (status === PROPOSAL_STATUS.REJECTED_BY_HOST) {
      ga1Button = { ...ga1Button, label: 'Delete Proposal', backgroundColor: '#EF4444' };
    } else {
      ga1Button = { ...ga1Button, label: config.guestAction1 };
    }
  }

  // ========== BUTTON 3: Guest Action 2 ==========
  let ga2Button = { visible: config.guestAction2 !== 'Invisible' };

  if (ga2Button.visible) {
    // ID docs submitted check
    if (status === PROPOSAL_STATUS.LEASE_DOCUMENTS_SENT && !guest?.['ID documents submitted?']) {
      ga2Button.visible = false;
    }
  }

  if (ga2Button.visible) {
    ga2Button = { ...ga2Button, label: config.guestAction2 };
  }

  // ========== BUTTON 4: Cancel Proposal ==========
  const cancelHiddenStatuses = [
    PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_AWAITING_APPLICATION,
    PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_PENDING_CONFIRMATION,
  ].map(s => (typeof s === 'string' ? s.trim() : s));

  let cancelButton = { visible: !cancelHiddenStatuses.includes(status) };

  if (cancelButton.visible) {
    const terminalStatuses = [
      PROPOSAL_STATUS.CANCELLED_BY_GUEST,
      PROPOSAL_STATUS.CANCELLED_BY_SPLITLEASE,
      PROPOSAL_STATUS.REJECTED_BY_HOST,
    ].map(s => (typeof s === 'string' ? s.trim() : s));

    if (terminalStatuses.includes(status)) {
      cancelButton = { ...cancelButton, label: 'Delete Proposal' };
    } else if (status === PROPOSAL_STATUS.HOST_COUNTEROFFER) {
      cancelButton = { ...cancelButton, label: 'Reject Modified Terms' };
    } else if (config.usualOrder > 5 && listing?.['House manual']) {
      cancelButton = { ...cancelButton, label: 'See House Manual', backgroundColor: '#6D31C2' };
    } else if (config.usualOrder > 5) {
      cancelButton.visible = true;
      cancelButton.label = 'Cancel Proposal';
    } else {
      cancelButton = { ...cancelButton, label: 'Cancel Proposal' };
    }
  }

  return {
    virtualMeeting: vmButton,
    guestAction1: ga1Button,
    guestAction2: ga2Button,
    cancelProposal: cancelButton,
  };
}

/**
 * Check if a listing has photos.
 *
 * @intent Validate that a listing has at least one photo for search display.
 * @rule Listings without photos must not appear in search results.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.listing - The listing object to check.
 * @returns {boolean} True if listing has at least one photo.
 *
 * @throws {Error} If listing is null or undefined.
 *
 * @example
 * const valid = hasListingPhotos({ listing: { 'Features - Photos': ['photo1'] } })
 * // => true
 *
 * const invalid = hasListingPhotos({ listing: { 'Features - Photos': [] } })
 * // => false
 */
export function hasListingPhotos({ listing }) {
  // No Fallback: Validate input
  if (listing === null || listing === undefined) {
    throw new Error('hasListingPhotos: listing cannot be null or undefined')
  }

  const photos = listing['Features - Photos']

  // Check for null, undefined, or empty array
  if (!photos) {
    return false
  }

  // Handle string representation (JSON array from database)
  if (typeof photos === 'string') {
    try {
      const parsed = JSON.parse(photos)
      return Array.isArray(parsed) && parsed.length > 0
    } catch {
      return false
    }
  }

  // Handle array directly
  if (Array.isArray(photos)) {
    return photos.length > 0
  }

  return false
}

import { VALID_PRICE_TIERS } from '../../constants/searchConstants.js';

/**
 * Check if price tier filter value is valid.
 *
 * @intent Validate price tier selection for search filters.
 * @rule Valid tiers: 'under-200', '200-350', '350-500', '500-plus', 'all'.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.priceTier - Price tier value to validate.
 * @returns {boolean} True if valid price tier.
 *
 * @throws {Error} If priceTier is not a string.
 *
 * @example
 * const valid = isValidPriceTier({ priceTier: 'under-200' })
 * // => true
 *
 * const invalid = isValidPriceTier({ priceTier: 'invalid' })
 * // => false
 */
export function isValidPriceTier({ priceTier }) {
  // No Fallback: Validate input
  if (typeof priceTier !== 'string') {
    throw new Error(
      `isValidPriceTier: priceTier must be a string, got ${typeof priceTier}`
    )
  }

  return VALID_PRICE_TIERS.includes(priceTier)
}

import { VALID_SORT_OPTIONS } from '../../constants/searchConstants.js';

/**
 * Check if sort option value is valid.
 *
 * @intent Validate sort option selection for search results.
 * @rule Valid options: 'recommended', 'price-low', 'most-viewed', 'recent'.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.sortBy - Sort option value to validate.
 * @returns {boolean} True if valid sort option.
 *
 * @throws {Error} If sortBy is not a string.
 *
 * @example
 * const valid = isValidSortOption({ sortBy: 'price-low' })
 * // => true
 *
 * const invalid = isValidSortOption({ sortBy: 'invalid' })
 * // => false
 */
export function isValidSortOption({ sortBy }) {
  // No Fallback: Validate input
  if (typeof sortBy !== 'string') {
    throw new Error(
      `isValidSortOption: sortBy must be a string, got ${typeof sortBy}`
    )
  }

  return VALID_SORT_OPTIONS.includes(sortBy)
}

import { VALID_WEEK_PATTERNS } from '../../constants/searchConstants.js';

/**
 * Check if week pattern filter value is valid.
 *
 * @intent Validate week pattern selection for search filters.
 * @rule Valid patterns: 'every-week', 'one-on-off', 'two-on-off', 'one-three-off'.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.weekPattern - Week pattern value to validate.
 * @returns {boolean} True if valid week pattern.
 *
 * @throws {Error} If weekPattern is not a string.
 *
 * @example
 * const valid = isValidWeekPattern({ weekPattern: 'one-on-off' })
 * // => true
 *
 * const invalid = isValidWeekPattern({ weekPattern: 'invalid' })
 * // => false
 */
export function isValidWeekPattern({ weekPattern }) {
  // No Fallback: Validate input
  if (typeof weekPattern !== 'string') {
    throw new Error(
      `isValidWeekPattern: weekPattern must be a string, got ${typeof weekPattern}`
    )
  }

  return VALID_WEEK_PATTERNS.includes(weekPattern)
}

