     3â†’ * Boolean predicates for reminder form validation and permissions.
     4â†’ */
     5â†’
     6â†’/**
     7â†’ * Check if a user can create reminders for a house manual.
     8â†’ *
     9â†’ * @param {object} params - Named parameters.
    10â†’ * @param {string} params.houseManualId - House manual ID.
    11â†’ * @param {string} params.creatorId - ID of the user creating the reminder.
    12â†’ * @returns {boolean} True if user can create reminders.
    13â†’ */
    14â†’export function canCreateReminder({ houseManualId, creatorId }) {
    15â†’  // Must have both IDs
    16â†’  if (!houseManualId || !creatorId) {
    17â†’    return false;
    18â†’  }
    19â†’
    20â†’  return true;
    21â†’}
    22â†’
    23â†’/**
    24â†’ * Check if a user can update a reminder.
    25â†’ *
    26â†’ * @param {object} params - Named parameters.
    27â†’ * @param {object} params.reminder - Reminder object.
    28â†’ * @param {string} params.userId - ID of the user attempting to update.
    29â†’ * @returns {boolean} True if user can update the reminder.
    30â†’ */
    31â†’export function canUpdateReminder({ reminder, userId }) {
    32â†’  if (!reminder || !userId) {
    33â†’    return false;
    34â†’  }
    35â†’
    36â†’  // Only the creator can update
    37â†’  const creatorId = reminder['Created By'] || reminder.createdBy || reminder.creator_id;
    38â†’  return creatorId === userId;
    39â†’}
    40â†’
    41â†’/**
    42â†’ * Check if a user can delete (cancel) a reminder.
    43â†’ *
    44â†’ * @param {object} params - Named parameters.
    45â†’ * @param {object} params.reminder - Reminder object.
    46â†’ * @param {string} params.userId - ID of the user attempting to delete.
    47â†’ * @returns {boolean} True if user can delete the reminder.
    48â†’ */
    49â†’export function canDeleteReminder({ reminder, userId }) {
    50â†’  if (!reminder || !userId) {
    51â†’    return false;
    52â†’  }
    53â†’
    54â†’  // Can't delete already sent or cancelled reminders
    55â†’  const status = reminder.status;
    56â†’  if (status === 'sent' || status === 'cancelled') {
    57â†’    return false;
    58â†’  }
    59â†’
    60â†’  // Only the creator can delete
    61â†’  const creatorId = reminder['Created By'] || reminder.createdBy || reminder.creator_id;
    62â†’  return creatorId === userId;
    63â†’}
    64â†’
    65â†’/**
    66â†’ * Check if reminder form can be submitted.
    67â†’ *
    68â†’ * @param {object} params - Named parameters.
    69â†’ * @param {string} params.message - Reminder message.
    70â†’ * @param {string} params.scheduledDateTime - Scheduled date/time.
    71â†’ * @param {boolean} params.isEmailReminder - Email enabled flag.
    72â†’ * @param {boolean} params.isSmsReminder - SMS enabled flag.
    73â†’ * @param {boolean} [params.isSubmitting=false] - Whether submission is in progress.
    74â†’ * @returns {boolean} True if form can be submitted.
    75â†’ */
    76â†’export function canSubmitReminder({
    77â†’  message,
    78â†’  scheduledDateTime,
    79â†’  isEmailReminder,
    80â†’  isSmsReminder,
    81â†’  isSubmitting = false,
    82â†’}) {
    83â†’  if (isSubmitting) {
    84â†’    return false;
    85â†’  }
    86â†’
    87â†’  // Message required
    88â†’  if (!message || message.trim().length === 0) {
    89â†’    return false;
    90â†’  }
    91â†’
    92â†’  // Scheduled time required
    93â†’  if (!scheduledDateTime) {
    94â†’    return false;
    95â†’  }
    96â†’
    97â†’  // At least one notification channel required
    98â†’  if (!isEmailReminder && !isSmsReminder) {
    99â†’    return false;
   100â†’  }
   101â†’
   102â†’  return true;
   103â†’}
   104â†’
   105â†’/**
   106â†’ * Check if a reminder message is valid.
   107â†’ *
   108â†’ * @param {object} params - Named parameters.
   109â†’ * @param {string} params.message - The message to validate.
   110â†’ * @param {number} [params.maxLength=1000] - Maximum message length.
