     1â†’/**
     2â†’ * Virtual Meetings Workflow Module
     3â†’ *
     4â†’ * PILLAR IV: Workflow Orchestrators (The "Flow" Layer)
     5â†’ *
     6â†’ * Implements the 5 virtual meeting workflows from Bubble.io:
     7â†’ * - crkdt5: VM empty, REQUEST (When virtual meeting is empty)
     8â†’ * - crpWM2: REQUEST ALT (When virtual meeting meeting declined is yes)
     9â†’ * - crpVt2: RESPOND to VM (When requested by host, no booked date)
    10â†’ * - cuvLq5: RESPOND to VM (When booked date exists, confirmed)
    11â†’ * - crkfZ5: Populate & Display respond-request-cancel-vm reusable element
    12â†’ */
    13â†’
    14â†’import { supabase } from '../../../lib/supabase.js';
    15â†’
    16â†’/**
    17â†’ * Create a new virtual meeting request
    18â†’ * Implements workflow crkdt5: "B: Request Virtual Meeting new is clicked VM empty, REQUEST"
    19â†’ *
    20â†’ * @param {string} proposalId - Proposal ID
    21â†’ * @param {string} guestId - Guest user ID who is requesting the meeting
    22â†’ * @returns {Promise<Object>} Created virtual meeting object
    23â†’ */
    24â†’export async function requestVirtualMeeting(proposalId, guestId) {
    25â†’  if (!proposalId || !guestId) {
    26â†’    throw new Error('Proposal ID and Guest ID are required');
    27â†’  }
    28â†’
    29â†’  console.log('[virtualMeetingWorkflow] Requesting virtual meeting for proposal:', proposalId);
    30â†’
    31â†’  // Generate unique ID for the meeting
    32â†’  const uniqueId = `VM-${Date.now()}-${proposalId.slice(0, 8)}`;
    33â†’
    34â†’  const vmData = {
    35â†’    proposal: proposalId,
    36â†’    'requested by': guestId,
    37â†’    'booked date': null,
    38â†’    'confirmedBySplitLease': false,
    39â†’    'meeting declined': false,
    40â†’    'meeting link': null,
    41â†’    'unique_id': uniqueId,
    42â†’    'Created Date': new Date().toISOString(),
    43â†’    'Modified Date': new Date().toISOString()
    44â†’  };
    45â†’
    46â†’  const { data, error } = await supabase
    47â†’    .from('virtualmeetingschedulesandlinks')
    48â†’    .insert(vmData)
    49â†’    .select()
    50â†’    .single();
    51â†’
    52â†’  if (error) {
    53â†’    console.error('[virtualMeetingWorkflow] Error requesting virtual meeting:', error);
    54â†’    throw new Error(`Failed to request virtual meeting: ${error.message}`);
    55â†’  }
    56â†’
    57â†’  // Update proposal to link virtual meeting
    58â†’  await supabase
    59â†’    .from('proposal')
    60â†’    .update({
    61â†’      'virtual meeting': data._id,
    62â†’      'Modified Date': new Date().toISOString()
    63â†’    })
    64â†’    .eq('_id', proposalId);
    65â†’
    66â†’  console.log('[virtualMeetingWorkflow] Virtual meeting requested:', data._id);
    67â†’  return data;
    68â†’}
    69â†’
    70â†’/**
    71â†’ * Request alternative meeting after decline
    72â†’ * Implements workflow crpWM2: "B: Request Virtual Meeting new is clicked REQUEST ALT"
    73â†’ *
    74â†’ * @param {string} existingVmId - ID of the declined virtual meeting
    75â†’ * @param {string} proposalId - Proposal ID
    76â†’ * @param {string} guestId - Guest user ID
    77â†’ * @returns {Promise<Object>} New virtual meeting object
    78â†’ */
    79â†’export async function requestAlternativeMeeting(existingVmId, proposalId, guestId) {
    80â†’  if (!existingVmId || !proposalId || !guestId) {
    81â†’    throw new Error('Existing VM ID, Proposal ID, and Guest ID are required');
    82â†’  }
    83â†’
    84â†’  console.log('[virtualMeetingWorkflow] Requesting alternative meeting (previous declined)');
    85â†’
    86â†’  // Mark old VM as superseded (keep declined flag)
    87â†’  await supabase
    88â†’    .from('virtualmeetingschedulesandlinks')
    89â†’    .update({
    90â†’      'meeting declined': true,
    91â†’      'Modified Date': new Date().toISOString()
    92â†’    })
    93â†’    .eq('_id', existingVmId);
    94â†’
    95â†’  // Create new VM request
    96â†’  return await requestVirtualMeeting(proposalId, guestId);
    97â†’}
    98â†’
    99â†’/**
   100â†’ * Respond to virtual meeting request by booking a date
   101â†’ * Implements workflow crpVt2: "B: Request Virtual Meeting new is clicked RESPOND to VM"
   102â†’ *
   103â†’ * @param {string} vmId - Virtual meeting ID
   104â†’ * @param {string} bookedDate - ISO timestamp of the booked date
   105â†’ * @returns {Promise<Object>} Updated virtual meeting object
   106â†’ */
   107â†’export async function respondToVirtualMeeting(vmId, bookedDate) {
   108â†’  if (!vmId || !bookedDate) {
   109â†’    throw new Error('Virtual meeting ID and booked date are required');
   110â†’  }
   111â†’
   112â†’  console.log('[virtualMeetingWorkflow] Responding to virtual meeting:', vmId);
   113â†’
   114â†’  const { data, error } = await supabase
   115â†’    .from('virtualmeetingschedulesandlinks')
   116â†’    .update({
   117â†’      'booked date': bookedDate,
   118â†’      'confirmedBySplitLease': false, // Needs admin confirmation after booking
   119â†’      'Modified Date': new Date().toISOString()
   120â†’    })
   121â†’    .eq('_id', vmId)
   122â†’    .select()
   123â†’    .single();
   124â†’
   125â†’  if (error) {
   126â†’    console.error('[virtualMeetingWorkflow] Error responding to virtual meeting:', error);
   127â†’    throw new Error(`Failed to respond to virtual meeting: ${error.message}`);
   128â†’  }
   129â†’
   130â†’  console.log('[virtualMeetingWorkflow] Virtual meeting date booked:', data._id);
   131â†’  return data;
   132â†’}
   133â†’
   134â†’/**
   135â†’ * Decline a virtual meeting request
   136â†’ *
   137â†’ * @param {string} vmId - Virtual meeting ID
   138â†’ * @returns {Promise<Object>} Updated virtual meeting object
   139â†’ */
   140â†’export async function declineVirtualMeeting(vmId) {
   141â†’  if (!vmId) {
   142â†’    throw new Error('Virtual meeting ID is required');
   143â†’  }
   144â†’
   145â†’  console.log('[virtualMeetingWorkflow] Declining virtual meeting:', vmId);
   146â†’
   147â†’  const { data, error } = await supabase
   148â†’    .from('virtualmeetingschedulesandlinks')
   149â†’    .update({
   150â†’      'meeting declined': true,
   151â†’      'Modified Date': new Date().toISOString()
   152â†’    })
   153â†’    .eq('_id', vmId)
   154â†’    .select()
   155â†’    .single();
   156â†’
   157â†’  if (error) {
   158â†’    console.error('[virtualMeetingWorkflow] Error declining virtual meeting:', error);
   159â†’    throw new Error(`Failed to decline virtual meeting: ${error.message}`);
   160â†’  }
   161â†’
   162â†’  console.log('[virtualMeetingWorkflow] Virtual meeting declined:', data._id);
   163â†’  return data;
   164â†’}
   165â†’
   166â†’/**
