     1â†’/**
     2â†’ * Counteroffer Workflow Module
     3â†’ *
     4â†’ * PILLAR IV: Workflow Orchestrators (The "Flow" Layer)
     5â†’ *
     6â†’ * Implements the counteroffer acceptance workflow from Bubble.io:
     7â†’ *
     8â†’ * 7 Steps (Accept Counteroffer):
     9â†’ * 1. Show success alert (48-hour timeline)
    10â†’ * 2. Calculate lease numbering format (based on count)
    11â†’ * 3. Set state: Number of zeros
    12â†’ * 4. Calculate 4-week compensation (original proposal)
    13â†’ * 5. Update proposal status -> "Drafting Lease Documents"
    14â†’ * 6. Calculate 4-week rent (counteroffer terms)
    15â†’ * 7. Schedule API workflow: CORE-create-lease (+15 seconds)
    16â†’ */
    17â†’
    18â†’import { supabase } from '../../../lib/supabase.js';
    19â†’import { hasReviewableCounteroffer } from '../../rules/proposals/proposalRules.js';
    20â†’import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js';
    21â†’
    22â†’/**
    23â†’ * Accept a counteroffer
    24â†’ * Updates proposal status and prepares for lease creation
    25â†’ *
    26â†’ * @param {string} proposalId - Proposal ID
    27â†’ * @returns {Promise<Object>} Updated proposal data
    28â†’ */
    29â†’export async function acceptCounteroffer(proposalId) {
    30â†’  if (!proposalId) {
    31â†’    throw new Error('Proposal ID is required');
    32â†’  }
    33â†’
    34â†’  console.log('[counterofferWorkflow] Accepting counteroffer for proposal:', proposalId);
    35â†’
    36â†’  // First, fetch the proposal to validate it has a counteroffer
    37â†’  const { data: proposal, error: fetchError } = await supabase
    38â†’    .from('proposal')
    39â†’    .select('*')
    40â†’    .eq('_id', proposalId)
    41â†’    .single();
    42â†’
    43â†’  if (fetchError) {
    44â†’    throw new Error(`Failed to fetch proposal: ${fetchError.message}`);
    45â†’  }
    46â†’
    47â†’  if (!proposal['counter offer happened']) {
    48â†’    throw new Error('Proposal does not have a counteroffer to accept');
    49â†’  }
    50â†’
    51â†’  // Update proposal status to accepted
    52â†’  const now = new Date().toISOString();
    53â†’  const updateData = {
    54â†’    'Status': PROPOSAL_STATUSES.PROPOSAL_OR_COUNTEROFFER_ACCEPTED.key,
    55â†’    'Modified Date': now,
    56â†’    'Is Finalized': true
    57â†’  };
    58â†’
    59â†’  const { data, error } = await supabase
    60â†’    .from('proposal')
    61â†’    .update(updateData)
    62â†’    .eq('_id', proposalId)
    63â†’    .select()
    64â†’    .single();
    65â†’
    66â†’  if (error) {
    67â†’    console.error('[counterofferWorkflow] Error accepting counteroffer:', error);
    68â†’    throw new Error(`Failed to accept counteroffer: ${error.message}`);
    69â†’  }
    70â†’
