   246→
   247→  const status = proposal.status || proposal.Status;
   248→
   249→  return isCompletedStatus(status);
   250→}
   251→
   252→/**
   253→ * Check if cancellation requires special confirmation
   254→ * Based on "Usual Order > 5 and House manual not empty" condition
   255→ *
   256→ * @param {Object} proposal - Proposal object
   257→ * @returns {boolean} True if special confirmation is needed
   258→ */
   259→export function requiresSpecialCancellationConfirmation(proposal) {
   260→  if (!proposal) {
   261→    return false;
   262→  }
   263→
   264→  const usualOrder = proposal['Usual Order'] || 0;
   265→  const houseManualNotEmpty = proposal.listing?.['House Manual'] || false;
   266→
   267→  return usualOrder > 5 && houseManualNotEmpty;
   268→}
   269→
   270→/**
   271→ * Get the appropriate cancel button text based on proposal status
   272→ *
   273→ * @param {Object} proposal - Proposal object
   274→ * @returns {string} Button text
   275→ */
   276→export function getCancelButtonText(proposal) {
