   291→/**
   292→ * Get available cancellation reason options for guests
   293→ * Fetches from cached reference data (initialized via dataLookups.js)
   294→ * Falls back to hardcoded values if cache is empty (for resilience during initialization)
   295→ *
   296→ * @returns {Array<string>} Array of reason option strings
   297→ */
   298→export function getCancellationReasonOptions() {
   299→  const cachedReasons = getGuestCancellationReasons();
   300→
   301→  if (cachedReasons.length > 0) {
   302→    return cachedReasons.map(r => r.reason);
   303→  }
   304→
   305→  // Fallback for initial render before cache is populated
   306→  console.warn('[getCancellationReasonOptions] Cache empty, using fallback values');
   307→  return [
   308→    'Found another property',
   309→    'Changed move-in dates',
   310→    'Changed budget',
   311→    'Changed location preference',
   312→    'No longer need housing',
   313→    'Host not responsive',
   314→    'Terms not acceptable',
