   329→ */
   330→export function needsRentalApplicationSubmission(proposal) {
   331→  if (!proposal) return false;
   332→
   333→  // No rental application linked at all
   334→  if (!proposal['rental application'] && !proposal.rentalApplication) {
   335→    return true;
   336→  }
   337→
   338→  // Has rental application reference but no data joined (shouldn't happen, but handle it)
   339→  if (proposal['rental application'] && !proposal.rentalApplication) {
   340→    return true;
   341→  }
   342→
   343→  // Has rental application but not submitted
   344→  if (proposal.rentalApplication && !proposal.rentalApplication.submitted) {
   345→    return true;
   346→  }
   347→
   348→  return false;
   349→}
   350→
   351→// Re-export isSuggestedProposal for backward compatibility
   352→export { isSuggestedProposal as isSLSuggestedProposal };
   353→
   354→/**
   355→ * Check if guest can confirm a Split Lease suggested proposal
   356→ * Guest can confirm if in "Pending Confirmation" status
   357→ * @param {Object} proposal - Proposal object
   358→ * @returns {boolean} True if can confirm
   359→ */
   360→export function canConfirmSuggestedProposal(proposal) {
   361→  if (!proposal) return false;
   362→  const status = proposal.status || proposal.Status;
   363→
   364→  // Can confirm if in "Pending Confirmation" status
   365→  return status === PROPOSAL_STATUSES.SUGGESTED_PROPOSAL_PENDING_CONFIRMATION.key;
   366→}
   367→
   368→/**
   369→ * Determine next status when guest confirms an SL-suggested proposal
   370→ * @param {Object} proposal - Proposal with rentalApplication data
   371→ * @returns {string} Next status key
   372→ */
   373→export function getNextStatusAfterConfirmation(proposal) {
   374→  if (needsRentalApplicationSubmission(proposal)) {
   375→    // Guest confirmed but rental app not done → intermediate status
   376→    return PROPOSAL_STATUSES.SUGGESTED_PROPOSAL_AWAITING_RENTAL_APP.key;
   377→  }
   378→  // Rental app already done → go straight to Host Review
   379→  return PROPOSAL_STATUSES.HOST_REVIEW.key;
   380→}
