/**
 * Formats user's display name for UI presentation.
 *
 * @function processUserDisplayName
 * @intent Transform user name data into formatted display string
 * @rule If showFull is true and lastName exists, return "FirstName LastName"
 * @rule If showFull is false or no lastName, return "FirstName"
 * @rule NO FALLBACK: firstName is required, throw if missing
 * @rule Trims whitespace and ensures proper capitalization
 *
 * @param {object} params - Named parameters
 * @param {string} params.firstName - User's first name (required)
 * @param {string|null|undefined} params.lastName - User's last name (optional)
 * @param {boolean} params.showFull - Whether to show full name (first + last)
 * @returns {string} Formatted display name
 * @throws {Error} If firstName is missing or invalid
 *
 * @example
 * processUserDisplayName({ firstName: 'John', lastName: 'Doe', showFull: true }) // "John Doe"
 * processUserDisplayName({ firstName: 'John', lastName: 'Doe', showFull: false }) // "John"
 * processUserDisplayName({ firstName: 'Jane', lastName: null, showFull: true }) // "Jane"
 * processUserDisplayName({ firstName: '', lastName: 'Doe', showFull: true }) // throws Error
 */
export function processUserDisplayName({ firstName, lastName, showFull }) {
  // NO FALLBACK: firstName is mandatory for user identity
  if (typeof firstName !== 'string' || firstName.trim().length === 0) {
    throw new Error('processUserDisplayName requires a valid firstName. Cannot display user without name.');
  }

  // Validate showFull is a boolean
  if (typeof showFull !== 'boolean') {
    throw new Error('processUserDisplayName requires showFull to be a boolean');
  }

  // Normalize firstName (trim whitespace)
  const normalizedFirstName = firstName.trim();

  // If not showing full name or no lastName, return first name only
  if (!showFull || !lastName) {
    return normalizedFirstName;
  }

  // Validate lastName if we're trying to use it
  if (typeof lastName !== 'string' || lastName.trim().length === 0) {
    return normalizedFirstName;
  }

  // Return full name with space separator
  const normalizedLastName = lastName.trim();
  return `${normalizedFirstName} ${normalizedLastName}`;
}

/**
 * Generates user initials from first and last name for avatar display.
 *
 * @function processUserInitials
 * @intent Create 1-2 letter initials for avatar when no profile photo available
 * @rule If both names provided, use first letter of each (uppercase)
 * @rule If only firstName, use first letter (uppercase)
 * @rule NO FALLBACK: firstName is required, throw if missing
 *
 * @param {object} params - Named parameters
 * @param {string} params.firstName - User's first name (required)
 * @param {string|null|undefined} params.lastName - User's last name (optional)
 * @returns {string} 1-2 letter initials (e.g., "JD" or "J")
 * @throws {Error} If firstName is missing or invalid
 *
 * @example
 * processUserInitials({ firstName: 'John', lastName: 'Doe' }) // "JD"
 * processUserInitials({ firstName: 'Jane', lastName: null }) // "J"
 * processUserInitials({ firstName: 'Bob', lastName: '' }) // "B"
 * processUserInitials({ firstName: '', lastName: 'Smith' }) // throws Error
 */
export function processUserInitials({ firstName, lastName }) {
  // NO FALLBACK: firstName is mandatory for user identity
  if (typeof firstName !== 'string' || firstName.trim().length === 0) {
    throw new Error('processUserInitials requires a valid firstName. Cannot generate initials without user name.');
  }

  // Get first letter of firstName (uppercase)
  const firstInitial = firstName.trim().charAt(0).toUpperCase();

  // If lastName exists and is valid, add its first letter
  if (lastName && typeof lastName === 'string' && lastName.trim().length > 0) {
    const lastInitial = lastName.trim().charAt(0).toUpperCase();
    return firstInitial + lastInitial;
  }

  // Return just first initial if no valid lastName
  return firstInitial;
}

/**
 * Format full host name to "FirstName L." format for privacy.
 *
 * @intent Format host name for display with privacy (show first name + last initial).
 * @rule NO FALLBACK: Throws error if name is invalid.
 * @rule Single name: Return as-is (e.g., "John" → "John").
 * @rule Multiple names: Return "FirstName L." (e.g., "John Smith" → "John S.").
 *
 * @param {object} params - Named parameters.
 * @param {string} params.fullName - Full name of the host.
 * @returns {string} Formatted name with last initial.
 *
 * @throws {Error} If fullName is not a string or is empty.
 *
 * @example
 * const formatted = formatHostName({ fullName: 'John Smith' })
 * // => "John S."
 *
 * const single = formatHostName({ fullName: 'John' })
 * // => "John"
 */
export function formatHostName({ fullName }) {
  // No Fallback: Strict validation
  if (typeof fullName !== 'string') {
    throw new Error(
      `formatHostName: fullName must be a string, got ${typeof fullName}`
    )
  }

  const trimmedName = fullName.trim()

  if (!trimmedName || trimmedName.length === 0) {
    throw new Error('formatHostName: fullName cannot be empty or whitespace')
  }

  // Split name into parts
  const nameParts = trimmedName.split(/\s+/)

  // Single name: return as-is
  if (nameParts.length === 1) {
    return nameParts[0]
  }

  // Multiple names: "FirstName L."
  const firstName = nameParts[0]
  const lastInitial = nameParts[nameParts.length - 1].charAt(0).toUpperCase()

  return `${firstName} ${lastInitial}.`
}

import { DAY_NAMES } from '../../../lib/constants.js'

/**
 * Calculate check-in and check-out days from selected days.
 * Check-in is the first selected day, check-out is the day AFTER the last selected day.
 *
 * @intent Determine the boundary days for a weekly split lease schedule.
 * @rule Check-in is the first selected day in the sequence.
 * @rule Check-out is the day AFTER the last selected day (handles wrap-around).
 * @rule For wrap-around cases (e.g., Fri-Mon), identify the gap to determine boundaries.
 *
 * @param {object} params - Named parameters.
 * @param {number[]} params.selectedDays - Array of day indices (0-6, where 0=Sunday).
 * @returns {object} Check-in/check-out details with day indices and names.
 *
 * @throws {Error} If selectedDays is not an array.
 * @throws {Error} If selectedDays contains invalid day indices.
 * @throws {Error} If selectedDays is empty.
 *
 * @example
 * const result = calculateCheckInOutDays({ selectedDays: [1, 2, 3, 4, 5] })
 * // => { checkInDay: 1, checkOutDay: 6, checkInName: 'Monday', checkOutName: 'Saturday' }
 *
 * const wrapped = calculateCheckInOutDays({ selectedDays: [5, 6, 0, 1] })
 * // => { checkInDay: 5, checkOutDay: 2, checkInName: 'Friday', checkOutName: 'Tuesday' }
 */
export function calculateCheckInOutDays({ selectedDays }) {
  // No Fallback: Validate input
  if (!Array.isArray(selectedDays)) {
    throw new Error(
      `calculateCheckInOutDays: selectedDays must be an array, got ${typeof selectedDays}`
    )
  }

  if (selectedDays.length === 0) {
    throw new Error(
      'calculateCheckInOutDays: selectedDays cannot be empty'
    )
  }

  // Validate all day indices
  for (const day of selectedDays) {
    if (typeof day !== 'number' || isNaN(day) || day < 0 || day > 6) {
      throw new Error(
        `calculateCheckInOutDays: Invalid day index ${day}, must be 0-6`
      )
    }
  }

  const sorted = [...selectedDays].sort((a, b) => a - b)

  // Handle wrap-around case (selection includes both Sunday=0 and Saturday=6)
  const hasZero = sorted.includes(0)
  const hasSix = sorted.includes(6)

  if (hasZero && hasSix) {
    // Find gap to determine actual start/end
    let gapIndex = -1
    for (let i = 1; i < sorted.length; i++) {
      if (sorted[i] !== sorted[i - 1] + 1) {
        gapIndex = i
        break
      }
    }

    if (gapIndex !== -1) {
      // Wrapped selection: check-in is after the gap
      const checkInDay = sorted[gapIndex] // First day after gap
      const lastSelectedDay = sorted[gapIndex - 1] // Last day before gap

      // Check-out is the day AFTER the last selected day
      const checkOutDay = (lastSelectedDay + 1) % 7

      return {
        checkInDay,
        checkOutDay,
        checkInName: DAY_NAMES[checkInDay],
        checkOutName: DAY_NAMES[checkOutDay]
      }
    }
  }

  // Standard case: first selected day to day after last selected day
  const checkInDay = sorted[0]
  const lastSelectedDay = sorted[sorted.length - 1]

  // Check-out is the day AFTER the last selected day (wraps around week if needed)
  const checkOutDay = (lastSelectedDay + 1) % 7

  return {
    checkInDay,
    checkOutDay,
    checkInName: DAY_NAMES[checkInDay],
    checkOutName: DAY_NAMES[checkOutDay]
  }
}

