/**
 * Calculate check-in and check-out days from selected day indices
 * Handles wrap-around scenarios (e.g., Fri-Mon selection)
 *
 * @param {number[]} selectedDays - Array of day indices (0=Sun, 6=Sat)
 * @returns {{ checkIn: number, checkOut: number }}
 * @throws {Error} If selectedDays is null/undefined or has less than 2 days
 */
export function calculateCheckInOutFromDays(selectedDays) {
  if (!selectedDays || selectedDays.length < 2) {
    throw new Error('calculateCheckInOutFromDays: selectedDays must contain at least 2 days');
  }

  const sortedDays = [...selectedDays].sort((a, b) => a - b);
  const hasSaturday = sortedDays.includes(6);
  const hasSunday = sortedDays.includes(0);
  const isWrapAround = hasSaturday && hasSunday && sortedDays.length < 7;

  if (isWrapAround) {
    // Find the gap to determine actual boundaries
    let gapIndex = -1;
    for (let i = 1; i < sortedDays.length; i++) {
      if (sortedDays[i] - sortedDays[i - 1] > 1) {
        gapIndex = i;
        break;
      }
    }

    if (gapIndex !== -1) {
      return {
        checkIn: sortedDays[gapIndex],
        checkOut: sortedDays[gapIndex - 1]
      };
    }
  }

  return {
    checkIn: sortedDays[0],
    checkOut: sortedDays[sortedDays.length - 1]
  };
}

/**
 * Calculate number of nights from selected days.
 *
 * @intent Determine the number of nights in a split lease stay.
 * @rule In split lease, nights = days selected (continuous selection).
 * @rule A stay from Monday to Friday (5 days) = 5 nights.
 *
 * @param {object} params - Named parameters.
 * @param {number[]} params.selectedDays - Array of day indices (0-6).
 * @returns {number} Number of nights in the stay.
 *
 * @throws {Error} If selectedDays is not an array.
 * @throws {Error} If selectedDays contains invalid values.
 *
 * @example
 * const nights = calculateNightsFromDays({ selectedDays: [1, 2, 3, 4, 5] })
 * // => 5
 */
export function calculateNightsFromDays({ selectedDays }) {
  // No Fallback: Validate input
  if (!Array.isArray(selectedDays)) {
    throw new Error(
      `calculateNightsFromDays: selectedDays must be an array, got ${typeof selectedDays}`
    )
  }

  // Validate all entries are numbers in valid range
  for (const day of selectedDays) {
    if (typeof day !== 'number' || isNaN(day) || day < 0 || day > 6) {
      throw new Error(
        `calculateNightsFromDays: Invalid day index ${day}, must be 0-6`
      )
    }
  }

  // In split lease, nights = days selected
  return selectedDays.length
}

