/**
 * Cancel Proposal Workflow
 *
 * PILLAR IV: Workflow Orchestrators (The "Flow" Layer)
 *
 * Implements the 7 variations of the cancel proposal workflow from Bubble.io:
 * - crkec5: Cancel Proposal (Condition 1) - Basic cancellation
 * - crswt2: Cancel Proposal (Condition 2) - Usual Order > 5 and House manual not empty
 * - crtCg2: Cancel Proposal (Condition 3) - Status is Cancelled or Rejected
 * - curuC4: Cancel Proposal (Condition 4) - Additional variation
 * - curuK4: Cancel Proposal (Condition 5) - Same as condition 2
 * - curua4: Cancel Proposal (Condition 6) - Same as condition 3
 * - crkZs5: Cancel Proposal in Compare Terms popup
 *
 * All cancellations result in:
 * - Status: 'Proposal Cancelled by Guest'
 * - Modified Date: current timestamp
 * - Optional: reason for cancellation
 */

import { supabase } from '../../../lib/supabase.js';
import { canCancelProposal, requiresSpecialCancellationConfirmation } from '../../rules/proposals/proposalRules.js';
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js';

/**
 * Evaluate which cancellation workflow condition applies
 *
 * @param {Object} proposal - Full proposal object
 * @returns {Object} Condition details with workflow info
 */
export function determineCancellationCondition(proposal) {
  if (!proposal) {
    return {
      condition: 'invalid',
      workflow: null,
      allowCancel: false,
      message: 'Invalid proposal data'
    };
  }

  const status = proposal.status || proposal.Status;

  // Condition 3 & 6: Already cancelled or rejected - just inform user
  if (
    status === PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key ||
    status === PROPOSAL_STATUSES.CANCELLED_BY_SPLITLEASE.key ||
    status === PROPOSAL_STATUSES.REJECTED_BY_HOST.key
  ) {
    return {
      condition: 'already_cancelled',
      workflow: 'crtCg2',
      allowCancel: false,
      message: 'This proposal is already cancelled or rejected'
    };
  }

  // Check if can cancel based on rules
  if (!canCancelProposal(proposal)) {
    return {
      condition: 'not_cancellable',
      workflow: null,
      allowCancel: false,
      message: 'This proposal cannot be cancelled at this stage'
    };
  }

  // Condition 2 & 5: Usual Order > 5 AND House manual not empty
  if (requiresSpecialCancellationConfirmation(proposal)) {
    return {
      condition: 'high_order_with_manual',
      workflow: 'crswt2',
      allowCancel: true,
      requiresConfirmation: true,
      confirmationMessage: 'You have an active rental history. Are you sure you want to cancel? This may affect your standing with the host and future rental opportunities.'
    };
  }

  // Condition 1, 4, and default: Standard cancellation
  return {
    condition: 'standard',
    workflow: 'crkec5',
    allowCancel: true,
    requiresConfirmation: true,
    confirmationMessage: 'Are you sure you want to cancel this proposal? This action cannot be undone.'
  };
}

/**
 * Execute proposal cancellation in database
 *
 * @param {string} proposalId - Proposal ID to cancel
 * @param {string} reason - Optional reason for cancellation
 * @returns {Promise<Object>} Updated proposal data
 */
export async function executeCancelProposal(proposalId, reason = null) {
  if (!proposalId) {
    throw new Error('Proposal ID is required');
  }

  const now = new Date().toISOString();

  const updateData = {
    'Status': PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key,
    'Modified Date': now
  };

  // Add reason if provided
  if (reason) {
    updateData['reason for cancellation'] = reason;
  }

  console.log('[cancelProposalWorkflow] Cancelling proposal:', proposalId);

  const { data, error } = await supabase
    .from('proposal')
    .update(updateData)
    .eq('_id', proposalId)
    .select()
    .single();

  if (error) {
    console.error('[cancelProposalWorkflow] Error cancelling proposal:', error);
    throw new Error(`Failed to cancel proposal: ${error.message}`);
  }

  console.log('[cancelProposalWorkflow] Proposal cancelled successfully:', proposalId);
  return data;
}

/**
 * Cancel proposal from Compare Terms modal (workflow crkZs5)
 * Same as regular cancellation but triggered from different UI location
 *
 * @param {string} proposalId - Proposal ID to cancel
 * @param {string} reason - Reason for cancellation
 * @returns {Promise<Object>} Updated proposal data
 */
export async function cancelProposalFromCompareTerms(proposalId, reason = 'Counteroffer declined') {
  console.log('[cancelProposalWorkflow] Cancel triggered from Compare Terms modal (workflow crkZs5)');
  return executeCancelProposal(proposalId, reason);
}

/**
 * Soft-delete a proposal (hide from user's list)
 *
 * Used for already-cancelled/rejected proposals where the guest just wants
 * to remove it from their view. Sets deleted = true without changing status.
 *
 * @param {string} proposalId - Proposal ID to delete
 * @returns {Promise<Object>} Updated proposal data
 */
export async function executeDeleteProposal(proposalId) {
  if (!proposalId) {
    throw new Error('Proposal ID is required');
  }

  const now = new Date().toISOString();

  console.log('[cancelProposalWorkflow] Soft-deleting proposal:', proposalId);

  const { error } = await supabase
    .from('proposal')
    .update({
      'Deleted': true,
      'Modified Date': now
    })
    .eq('_id', proposalId);

  if (error) {
    console.error('[cancelProposalWorkflow] Error deleting proposal:', error);
    throw new Error(`Failed to delete proposal: ${error.message}`);
  }

  console.log('[cancelProposalWorkflow] Proposal deleted successfully:', proposalId);
}

/**
 * Workflow: Cancel a proposal with complex decision tree.
 *
 * @intent Orchestrate proposal cancellation following business rules.
 * @rule Implements complete cancellation decision tree (7 variations).
 * @rule Different actions based on: source, current status, usual order, house manual access.
 * @rule Updates Supabase with appropriate status and cancellation reason.
 *
 * Decision Tree (from WORKFLOW-PASS2-ASSIMILATION.md):
 * 1. Already cancelled → Show alert, no action
 * 2. Compare modal source + Usual Order ≤5 → Status: "Cancelled by Guest"
 * 3. Compare modal source + Usual Order >5 → Alert to call, no DB update
 * 4. Main page + Usual Order ≤5 + No house manual → Status: "Cancelled by Guest"
 * 5. Main page + Usual Order >5 + No house manual → Alert to call, no DB update
 * 6. Main page + Usual Order ≤5 + Has house manual → Alert to call, no DB update
 * 7. Main page + Usual Order >5 + Has house manual → Alert to call, no DB update
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.proposal - Processed proposal object.
 * @param {string} params.source - 'main' | 'compare-modal' | 'other'.
 * @param {function} params.canCancelProposal - Rule function to check if cancellable.
 * @returns {Promise<object>} Result object with success status and message.
 *
 * @throws {Error} If required parameters are missing.
 *
 * @example
 * const result = await cancelProposalWorkflow({
 *   supabase,
 *   proposal: processedProposal,
 *   source: 'main',
 *   canCancelProposal
 * })
 * // => { success: true, message: 'Proposal cancelled', updated: true }
 */
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js'

export async function cancelProposalWorkflow({
  supabase,
  proposal,
  source = 'main',
  canCancelProposal
}) {
  // Validation
  if (!supabase) {
    throw new Error('cancelProposalWorkflow: supabase client is required')
  }

  if (!proposal || !proposal.id) {
    throw new Error('cancelProposalWorkflow: proposal with id is required')
  }

  if (!canCancelProposal) {
    throw new Error('cancelProposalWorkflow: canCancelProposal rule function is required')
  }

  // Step 1: Check if cancellation is allowed
  const canCancel = canCancelProposal({
    proposalStatus: proposal.status,
    deleted: proposal.deleted
  })

  if (!canCancel) {
    return {
      success: false,
      message: 'This proposal cannot be cancelled (already in terminal state)',
      updated: false
    }
  }

  // Step 2: Extract decision factors
  const usualOrder = proposal.usualOrder || 0
  const hasAccessedHouseManual = proposal.houseManualAccessed === true
  const isFromCompareModal = source === 'compare-modal'

  // Step 3: Apply decision tree
  let shouldUpdateDatabase = false
  let alertMessage = null

  if (isFromCompareModal) {
    // Compare Modal Source
    if (usualOrder <= 5) {
      // Variation 2: Update database
      shouldUpdateDatabase = true
    } else {
      // Variation 3: Alert to call
      alertMessage = 'Please call Split Lease to cancel this proposal (Usual Order > 5)'
    }
  } else {
    // Main Page Source
    if (!hasAccessedHouseManual) {
      if (usualOrder <= 5) {
        // Variation 4: Update database
        shouldUpdateDatabase = true
      } else {
        // Variation 5: Alert to call
        alertMessage = 'Please call Split Lease to cancel this proposal (Usual Order > 5)'
      }
    } else {
      // Has accessed house manual - always alert to call (Variations 6 & 7)
      alertMessage = 'Please call Split Lease to cancel this proposal (House Manual accessed)'
    }
  }

  // Step 4: Execute action
  if (shouldUpdateDatabase) {
    try {
      const { error } = await supabase
        .from('proposal')
        .update({
          'Status': PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key,
          'reason for cancellation': 'Guest initiated cancellation',
          'Modified Date': new Date().toISOString()
        })
        .eq('_id', proposal.id)

      if (error) {
        throw error
      }

      return {
        success: true,
        message: 'Proposal cancelled successfully',
        updated: true
      }
    } catch (err) {
      return {
        success: false,
        message: `Failed to cancel proposal: ${err.message}`,
        updated: false,
        error: err
      }
    }
  } else {
    // Alert only, no database update
    return {
      success: true,
      message: alertMessage,
      updated: false,
      requiresPhoneCall: true
    }
  }
}

/**
 * Proposal Status Configuration System
 *
 * Centralized configuration for all proposal statuses,
 * replacing hardcoded status mappings throughout the application.
 *
 * Each status includes:
 * - key: The exact status string from the database
 * - color: UI color theme for the status
 * - label: User-friendly display text
 * - stage: The progress stage number (1-6) or null if not in active flow
 * - actions: Available actions for this status
 */

export const PROPOSAL_STATUSES = {
  // ===== CANCELLED STATES =====
  CANCELLED_BY_GUEST: {
    key: 'Proposal Cancelled by Guest',
    color: 'red',
    label: 'Cancelled by You',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  CANCELLED_BY_SPLITLEASE: {
    key: 'Proposal Cancelled by Split Lease',
    color: 'red',
    label: 'Proposal Cancelled',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  // ===== REJECTED STATES =====
  REJECTED_BY_HOST: {
    key: 'Proposal Rejected by Host',
    color: 'red',
    label: 'Rejected by Host',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  // ===== ACTIVE PROPOSAL FLOW =====
  // usualOrder values match reference_table.os_proposal_status sort_order
  // sort_order: -1 = terminal (cancelled/rejected), 0-7 = active flow

  // usualOrder 0: Proposal Submitted, Awaiting Rental Application
  PROPOSAL_SUBMITTED_AWAITING_RENTAL_APP: {
    key: 'Proposal Submitted by guest - Awaiting Rental Application',
    color: 'blue',
    label: 'Submit Rental Application',
    stage: 1,
    usualOrder: 0,
    actions: ['submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  // Suggested proposal by Split Lease agent - awaiting rental application (sort_order 0)
  // NEW MEANING: Guest has CONFIRMED but rental app not yet submitted
  SUGGESTED_PROPOSAL_AWAITING_RENTAL_APP: {
    key: 'Proposal Submitted for guest by Split Lease - Awaiting Rental Application',
    color: 'purple',
    label: 'Suggested Proposal - Awaiting Rental App',
    stage: 1,
    usualOrder: 0,
    isSuggestedBySL: true,
    guestConfirmed: true,
    actions: ['submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  // Suggested proposal by Split Lease agent - pending confirmation (sort_order 0)
  // NEW MEANING: Initial state - guest hasn't confirmed yet
  SUGGESTED_PROPOSAL_PENDING_CONFIRMATION: {
    key: 'Proposal Submitted for guest by Split Lease - Pending Confirmation',
    color: 'purple',
    label: 'Suggested Proposal - Pending Confirmation',
    stage: 1,
    usualOrder: 0,
    isSuggestedBySL: true,
    guestConfirmed: false,
    actions: ['confirm_proposal', 'submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  PENDING_CONFIRMATION: {
    key: 'Pending Confirmation',
    color: 'blue',
    label: 'Awaiting Confirmation',
    stage: 1,
    usualOrder: 0,
    actions: ['cancel_proposal', 'request_vm', 'send_message']
  },

  // Legacy pending status (treated as sort_order 0)
  PENDING: {
    key: 'Pending',
    color: 'blue',
    label: 'Pending',
    stage: 1,
    usualOrder: 0,
    actions: ['cancel_proposal', 'request_vm', 'send_message']
  },

  // usualOrder 1: Host Review (after rental app submitted)
  HOST_REVIEW: {
    key: 'Host Review',
    color: 'blue',
    label: 'Under Host Review',
    stage: 3,
    usualOrder: 1,
    actions: ['request_vm', 'cancel_proposal', 'send_message']
  },

  // Rental Application Submitted - transitional state before Host Review (sort_order 1)
  RENTAL_APP_SUBMITTED: {
    key: 'Rental Application Submitted',
    color: 'blue',
    label: 'Application Under Review',
    stage: 2,
    usualOrder: 1,
    actions: ['request_vm', 'cancel_proposal', 'send_message']
  },

  // usualOrder 2: Host Counteroffer
  COUNTEROFFER_SUBMITTED_AWAITING_GUEST_REVIEW: {
    key: 'Host Counteroffer Submitted / Awaiting Guest Review',
    color: 'yellow',
    label: 'Review Host Counteroffer',
    stage: 3,
    usualOrder: 2,
    actions: ['review_counteroffer', 'compare_terms', 'accept_counteroffer', 'decline_counteroffer', 'request_vm', 'send_message']
  },

  // usualOrder 3: Accepted / Drafting
  PROPOSAL_OR_COUNTEROFFER_ACCEPTED: {
    key: 'Proposal or Counteroffer Accepted / Drafting Lease Documents',
    color: 'green',
    label: 'Accepted - Drafting Lease',
    stage: 4,
    usualOrder: 3,
    actions: ['request_vm', 'send_message']
  },

  // Reviewing Documents (variant of sort_order 3)
  REVIEWING_DOCUMENTS: {
    key: 'Reviewing Documents',
    color: 'blue',
    label: 'Reviewing Documents',
    stage: 4,
    usualOrder: 3,
    actions: ['review_documents', 'request_vm', 'send_message']
  },

  // usualOrder 4: Lease Documents Sent for Review
  LEASE_DOCUMENTS_SENT_FOR_REVIEW: {
    key: 'Lease Documents Sent for Review',
    color: 'blue',
    label: 'Review Lease Documents',
    stage: 4,
    usualOrder: 4,
    actions: ['review_documents', 'request_vm', 'send_message']
  },

  // usualOrder 5: Lease documents sent for legal signatures
  LEASE_DOCUMENTS_SENT_FOR_SIGNATURES: {
    key: 'Lease Documents Sent for Signatures',
    color: 'green',
    label: 'Sign Lease Documents',
    stage: 5,
    usualOrder: 5,
    actions: ['sign_documents', 'request_vm', 'send_message']
  },

  // usualOrder 6: Lease signed, awaiting payment (note: lowercase "payment" matches Bubble)
  LEASE_DOCUMENTS_SIGNED_AWAITING_PAYMENT: {
    key: 'Lease Documents Signed / Awaiting Initial payment',
    color: 'green',
    label: 'Submit Initial Payment',
    stage: 6,
    usualOrder: 6,
    actions: ['submit_payment', 'request_vm', 'send_message']
  },

  // Legacy key format (keeping for backwards compatibility) - sort_order 6
  LEASE_SIGNED_AWAITING_INITIAL_PAYMENT: {
    key: 'Lease Signed / Awaiting Initial Payment',
    color: 'green',
    label: 'Submit Initial Payment',
    stage: 6,
    usualOrder: 6,
    actions: ['submit_payment', 'request_vm', 'send_message']
  },

  // usualOrder 7: Initial Payment / Lease Activated
  INITIAL_PAYMENT_SUBMITTED_LEASE_ACTIVATED: {
    key: 'Initial Payment Submitted / Lease activated ',
    color: 'green',
    label: 'Lease Activated',
    stage: 6,
    usualOrder: 7,
    actions: ['view_lease', 'view_house_manual', 'send_message']
  },

  // ===== SPECIAL STATES =====

  DRAFT: {
    key: 'Draft',
    color: 'gray',
    label: 'Draft',
    stage: null,
    usualOrder: 0,
    actions: ['edit_proposal', 'submit_proposal', 'delete_proposal']
  },

  EXPIRED: {
    key: 'Expired',
    color: 'gray',
    label: 'Expired',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  }
};

/**
 * Normalize status key by trimming whitespace
 * Database values from Bubble may have trailing spaces
 * @param {string} statusKey - The raw status string
 * @returns {string} Trimmed status string
 */
function normalizeStatusKey(statusKey) {
  return typeof statusKey === 'string' ? statusKey.trim() : statusKey;
}

/**
 * Get status configuration by status key
 * @param {string} statusKey - The status string from the database
 * @returns {Object} Status configuration object with color, label, stage, actions
 */
export function getStatusConfig(statusKey) {
  if (!statusKey) {
    return {
      key: 'Unknown',
      color: 'gray',
      label: 'Unknown Status',
      stage: null,
      usualOrder: 0,
      actions: []
    };
  }

  // Normalize status key to handle trailing spaces from Bubble data
  const normalizedKey = normalizeStatusKey(statusKey);

  const config = Object.values(PROPOSAL_STATUSES).find(
    s => normalizeStatusKey(s.key) === normalizedKey
  );

  return config || {
    key: normalizedKey,
    color: 'gray',
    label: normalizedKey,
    stage: null,
    usualOrder: 0,
    actions: []
  };
}

/**
 * Get the progress stage number from a status key
 * @param {string} statusKey - The status string from the database
 * @returns {number|null} Stage number (1-6) or null if not in active flow
 */
export function getStageFromStatus(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.stage;
}

/**
 * Get the usual order number from a status key (Bubble ordering)
 * @param {string} statusKey - The status string from the database
 * @returns {number} Usual order number (1-7 for active, 99 for terminal, 0 for unknown)
 */
export function getUsualOrder(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.usualOrder ?? 0;
}

/**
 * Check if status banner should be visible
 * Banner shows when usualOrder >= 3 OR status is "Proposal Submitted by guest - Awaiting Rental Application"
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if banner should be shown
 */
export function shouldShowStatusBanner(statusKey) {
  if (!statusKey) return false;

  const usualOrder = getUsualOrder(statusKey);
  const normalizedKey = normalizeStatusKey(statusKey);
  const isProposalSubmitted = normalizedKey === normalizeStatusKey(PROPOSAL_STATUSES.PROPOSAL_SUBMITTED_AWAITING_RENTAL_APP.key);

  return usualOrder >= 3 || isProposalSubmitted;
}

/**
 * Get available actions for a status
 * @param {string} statusKey - The status string from the database
 * @returns {Array<string>} Array of action identifiers
 */
export function getActionsForStatus(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.actions || [];
}

/**
 * Check if a status is in an active proposal flow
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if status has a stage number (active flow)
 */
export function isActiveStatus(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.stage !== null;
}

/**
 * Check if a status is cancelled or rejected
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if status is cancelled or rejected
 */
export function isTerminalStatus(statusKey) {
  const config = getStatusConfig(statusKey);
  const normalizedKey = normalizeStatusKey(statusKey) || '';
  return config.color === 'red' || normalizedKey.includes('Cancelled') || normalizedKey.includes('Rejected');
}

/**
 * Check if a status is completed (lease activated)
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if lease is activated
 */
export function isCompletedStatus(statusKey) {
  const normalizedKey = normalizeStatusKey(statusKey);
  return normalizedKey === normalizeStatusKey(PROPOSAL_STATUSES.INITIAL_PAYMENT_SUBMITTED_LEASE_ACTIVATED.key);
}

/**
 * Check if a status is a suggested proposal (created by Split Lease agent)
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if proposal was suggested by Split Lease
 */
export function isSuggestedProposal(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.isSuggestedBySL === true;
}

/**
 * Get all statuses by color category
 * @param {string} color - Color category ('red', 'yellow', 'blue', 'green', 'gray')
 * @returns {Array<Object>} Array of status configurations
 */
export function getStatusesByColor(color) {
  return Object.values(PROPOSAL_STATUSES).filter(s => s.color === color);
}

/**
 * Get all statuses for a specific stage
 * @param {number} stageNumber - Stage number (1-6)
 * @returns {Array<Object>} Array of status configurations
 */
export function getStatusesByStage(stageNumber) {
  return Object.values(PROPOSAL_STATUSES).filter(s => s.stage === stageNumber);
}

/**
 * Check if a status is a suggested proposal pending guest confirmation
 * (Guest hasn't confirmed the Split Lease suggestion yet)
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if pending confirmation
 */
export function isPendingConfirmationProposal(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.isSuggestedBySL === true && config.guestConfirmed === false;
}

/**
 * Process raw user data from Supabase into a clean, validated user object.
 *
 * @intent Transform raw user rows from Supabase into consistent, UI-ready format.
 * @rule NO FALLBACK - Throws explicit errors for missing critical fields.
 *
 * @param {object} rawUser - Raw user object from Supabase.
 * @returns {object} Clean, validated user object.
 *
 * @throws {Error} If rawUser is null/undefined.
 * @throws {Error} If critical _id field is missing.
 *
 * @example
 * const user = processUserData({
 *   _id: '123',
 *   'Name - Full': 'Jane Doe',
 *   'Name - First': 'Jane',
 *   'Profile Photo': 'https://...',
 *   'About Me / Bio': 'Software engineer...',
 *   'user verified?': true
 * })
 */
export function processUserData(rawUser) {
  if (!rawUser) {
    throw new Error('processUserData: User data is required');
  }

  if (!rawUser._id) {
    throw new Error('processUserData: User ID (_id) is required');
  }

  return {
    id: rawUser._id,
    firstName: rawUser['Name - First'] || null,
    lastName: rawUser['Name - Last'] || null,
    fullName: rawUser['Name - Full'] || null,
    profilePhoto: rawUser['Profile Photo'] || null,
    bio: rawUser['About Me / Bio'] || null,
    linkedInVerified: rawUser['Verify - Linked In ID'] || false,
    phoneVerified: rawUser['Verify - Phone'] || false,
    userVerified: rawUser['user verified?'] || false,
    proposalsList: rawUser['Proposals List'] || []
  };
}

/**
 * Determine if a guest can edit their proposal.
 *
 * @intent Enforce business rules for when proposal editing is allowed.
 * @rule Guest can only edit proposals in early stages (1-2).
 * @rule Once accepted, VM scheduled, or cancelled, editing is locked.
 * @rule Deleted proposals cannot be edited.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can edit this proposal.
 *
 * @example
 * canEditProposal({ proposalStatus: 'Pending' })
 * // => true
 *
 * canEditProposal({ proposalStatus: 'Accepted' })
 * // => false
 */
import { getActionsForStatus } from '../../constants/proposalStatuses.js'

export function canEditProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be edited
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()
  const actions = getActionsForStatus(status)

  return actions.includes('modify_proposal') || actions.includes('edit_proposal')
}

/**
 * Determine if a guest can cancel their proposal.
 *
 * @intent Enforce business rules for when proposal cancellation is allowed.
 * @rule Guest can cancel proposals in stages 1-4 (before completion).
 * @rule Once completed, proposals cannot be cancelled (only disputes apply).
 * @rule Already cancelled/rejected/expired proposals cannot be re-cancelled.
 * @rule Deleted proposals cannot be cancelled.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can cancel this proposal.
 *
 * @example
 * canCancelProposal({ proposalStatus: 'Pending' })
 * // => true
 *
 * canCancelProposal({ proposalStatus: 'Completed' })
 * // => false
 *
 * canCancelProposal({ proposalStatus: 'Cancelled by Guest' })
 * // => false (already cancelled)
 */
import { isTerminalStatus, isCompletedStatus } from '../../constants/proposalStatuses.js'

export function canCancelProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be cancelled
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()

  if (isTerminalStatus(status) || isCompletedStatus(status)) {
    return false
  }

  // Can cancel if in any active state
  // Includes: Draft, Pending, Host Countered, VM Requested, VM Confirmed, Accepted, Verified
  return true
}

/**
 * Determine the current stage (1-6) of a proposal in the guest journey.
 *
 * @intent Map proposal status to visual progress tracker stage.
 * @rule Implements the 6-stage proposal journey:
 *   Stage 1: Proposal Sent (Draft, Pending)
 *   Stage 2: Host Countered (Host Countered)
 *   Stage 3: Virtual Meeting Scheduled (VM Requested, VM Confirmed)
 *   Stage 4: Accepted (Accepted, Verified)
 *   Stage 5: Completed (Completed)
 *   Stage 6: Cancelled (Cancelled by Guest, Cancelled by Host, Rejected, Expired)
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {number} Stage number (1-6).
 *
 * @throws {Error} If proposalStatus is missing.
 *
 * @example
 * const stage = determineProposalStage({ proposalStatus: 'Host Countered' })
 * // => 2
 */
import { getStageFromStatus, isTerminalStatus } from '../../constants/proposalStatuses.js'

export function determineProposalStage({ proposalStatus, deleted = false }) {
  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    throw new Error('determineProposalStage: proposalStatus is required and must be a string')
  }

  // Deleted proposals are always stage 6 (Cancelled)
  if (deleted) {
    return 6
  }

  const status = proposalStatus.trim()

  if (isTerminalStatus(status)) {
    return 6
  }

  const stage = getStageFromStatus(status)
  return stage ?? 1
}

