     1→/**
     2→ * Proposal Business Rules
     3→ *
     4→ * PILLAR II: Rule Engines (The "Conditional" Layer)
     5→ *
     6→ * This module encapsulates the "Predicate Functions" of the proposal domain.
     7→ * These are functions that return a strict boolean (true or false) indicating
     8→ * whether a specific condition is met. They represent business rules, permissions,
     9→ * and validity checks.
    10→ *
    11→ * Conventions:
    12→ * - Function names begin with predicate verbs: should, can, is, has, allows
    13→ * - Rules never perform actions - they only provide verdicts
    14→ */
    15→
    16→import { PROPOSAL_STATUSES, isTerminalStatus, isCompletedStatus, getActionsForStatus, isSuggestedProposal } from '../../constants/proposalStatuses.js';
    17→import { getGuestCancellationReasons } from '../../../lib/dataLookups.js';
    18→
    19→/**
    20→ * Check if a proposal can be cancelled by the guest
    21→ * Based on Bubble.io workflows crkec5, crswt2, crtCg2, curuC4, curuK4, curua4
    22→ *
    23→ * @param {Object} proposal - Proposal object
    24→ * @returns {boolean} True if proposal can be cancelled
    25→ */
    26→export function canCancelProposal(proposal) {
    27→  if (!proposal) {
    28→    return false;
    29→  }
    30→
    31→  const status = proposal.status || proposal.Status;
    32→
    33→  // Can't cancel if already cancelled or rejected
    34→  if (
    35→    status === PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key ||
    36→    status === PROPOSAL_STATUSES.CANCELLED_BY_SPLITLEASE.key ||
    37→    status === PROPOSAL_STATUSES.REJECTED_BY_HOST.key
    38→  ) {
    39→    return false;
    40→  }
    41→
    42→  // Can't cancel if lease is already activated
    43→  if (isCompletedStatus(status)) {
    44→    return false;
    45→  }
    46→
    47→  // Can't cancel if expired
    48→  if (status === PROPOSAL_STATUSES.EXPIRED.key) {
    49→    return false;
    50→  }
    51→
    52→  // Otherwise, can cancel
    53→  return true;
    54→}
    55→
    56→/**
    57→ * Check if a proposal can be modified/edited by the guest
    58→ * Only allowed in early stages before rental application submission
    59→ *
    60→ * @param {Object} proposal - Proposal object
    61→ * @returns {boolean} True if proposal can be modified
    62→ */
    63→export function canModifyProposal(proposal) {
    64→  if (!proposal) {
    65→    return false;
    66→  }
    67→
    68→  const status = proposal.status || proposal.Status;
    69→
    70→  // Can only modify if in initial submission stage
    71→  return status === PROPOSAL_STATUSES.PROPOSAL_SUBMITTED_AWAITING_RENTAL_APP.key;
    72→}
    73→
    74→/**
    75→ * Check if a proposal has a counteroffer that can be reviewed
    76→ *
    77→ * @param {Object} proposal - Proposal object
    78→ * @returns {boolean} True if proposal has reviewable counteroffer
    79→ */
    80→export function hasReviewableCounteroffer(proposal) {
    81→  if (!proposal) {
    82→    return false;
    83→  }
    84→
    85→  const status = proposal.status || proposal.Status;
    86→
    87→  return (
    88→    status === PROPOSAL_STATUSES.COUNTEROFFER_SUBMITTED_AWAITING_GUEST_REVIEW.key &&
    89→    proposal.counterOfferHappened === true
    90→  );
    91→}
    92→
    93→/**
    94→ * Check if guest can accept a counteroffer
    95→ *
    96→ * @param {Object} proposal - Proposal object
    97→ * @returns {boolean} True if counteroffer can be accepted
    98→ */
    99→export function canAcceptCounteroffer(proposal) {
   100→  return hasReviewableCounteroffer(proposal);
   101→}
   102→
   103→/**
   104→ * Check if guest can decline a counteroffer
   105→ *
   106→ * @param {Object} proposal - Proposal object
   107→ * @returns {boolean} True if counteroffer can be declined
   108→ */
   109→export function canDeclineCounteroffer(proposal) {
   110→  return hasReviewableCounteroffer(proposal);
   111→}
   112→
   113→/**
   114→ * Check if guest can submit rental application
   115→ *
   116→ * @param {Object} proposal - Proposal object
   117→ * @returns {boolean} True if rental application can be submitted
   118→ */
   119→export function canSubmitRentalApplication(proposal) {
   120→  if (!proposal) {
   121→    return false;
   122→  }
   123→
   124→  const status = proposal.status || proposal.Status;
   125→
   126→  return status === PROPOSAL_STATUSES.PROPOSAL_SUBMITTED_AWAITING_RENTAL_APP.key;
   127→}
   128→
   129→/**
   130→ * Check if guest can review documents
   131→ *
   132→ * @param {Object} proposal - Proposal object
   133→ * @returns {boolean} True if documents can be reviewed
   134→ */
   135→export function canReviewDocuments(proposal) {
   136→  if (!proposal) {
   137→    return false;
   138→  }
   139→
   140→  const status = proposal.status || proposal.Status;
   141→  const actions = getActionsForStatus(status);
   142→
   143→  return actions.includes('review_documents');
   144→}
   145→
   146→/**
   147→ * Check if guest can request a virtual meeting
   148→ *
   149→ * @param {Object} proposal - Proposal object
   150→ * @returns {boolean} True if VM can be requested
   151→ */
   152→export function canRequestVirtualMeeting(proposal) {
   153→  if (!proposal) {
   154→    return false;
   155→  }
