     1→/**
     2→ * Proposal Progress Stage Configuration System
     3→ *
     4→ * This module defines the 6 stages of the proposal-to-lease workflow,
     5→ * replacing hardcoded stage arrays with rich configuration objects.
     6→ *
     7→ * Each stage includes:
     8→ * - id: Stage number (1-6)
     9→ * - name: Full stage name
    10→ * - shortName: Abbreviated name for compact displays
    11→ * - icon: Emoji or icon identifier
    12→ * - description: User-friendly explanation of the stage
    13→ * - helpText: Additional guidance for guests
    14→ */
    15→
    16→export const PROPOSAL_STAGES = [
    17→  {
    18→    id: 1,
    19→    name: 'Proposal Submitted',
    20→    shortName: 'Submitted',
    21→    icon: '1',
    22→    description: 'Your proposal has been submitted to the host',
    23→    helpText: 'The next step is to complete your rental application so the host can review your profile.'
    24→  },
    25→  {
    26→    id: 2,
    27→    name: 'Rental App Submitted',
    28→    shortName: 'Application',
    29→    icon: '2',
    30→    description: 'Your rental application is complete',
    31→    helpText: 'The host is reviewing your application and will respond soon.'
    32→  },
    33→  {
    34→    id: 3,
    35→    name: 'Host Review',
    36→    shortName: 'Review',
    37→    icon: '3',
    38→    description: 'Host is reviewing your proposal',
    39→    helpText: 'The host may accept your proposal, request changes, or propose a counteroffer.'
    40→  },
    41→  {
    42→    id: 4,
    43→    name: 'Review Documents',
    44→    shortName: 'Documents',
    45→    icon: '4',
    46→    description: 'Review proposal documents and terms',
    47→    helpText: 'Carefully review all documents before proceeding to the lease agreement.'
    48→  },
    49→  {
    50→    id: 5,
    51→    name: 'Lease Documents',
    52→    shortName: 'Lease',
    53→    icon: '5',
    54→    description: 'Review and sign lease documents',
    55→    helpText: 'Read the lease agreement thoroughly and sign when ready.'
    56→  },
    57→  {
    58→    id: 6,
    59→    name: 'Initial Payment',
    60→    shortName: 'Payment',
    61→    icon: '6',
    62→    description: 'Submit initial payment to activate lease',
    63→    helpText: 'Submit your first payment to activate the lease and finalize your reservation.'
    64→  }
    65→];
    66→
    67→/**
    68→ * Get stage configuration by ID
    69→ * @param {number} stageId - Stage ID (1-6)
    70→ * @returns {Object|null} Stage configuration object or null if not found
    71→ */
    72→export function getStageById(stageId) {
    73→  if (!stageId || stageId < 1 || stageId > 6) {
    74→    return null;
    75→  }
    76→  return PROPOSAL_STAGES.find(s => s.id === stageId) || null;
    77→}
    78→
    79→/**
    80→ * Get stage configuration by name (case-insensitive)
    81→ * @param {string} stageName - Stage name
    82→ * @returns {Object|null} Stage configuration object or null if not found
    83→ */
    84→export function getStageByName(stageName) {
    85→  if (!stageName) {
    86→    return null;
    87→  }
    88→  const normalizedName = stageName.toLowerCase();
    89→  return PROPOSAL_STAGES.find(
    90→    s => s.name.toLowerCase() === normalizedName || s.shortName.toLowerCase() === normalizedName
    91→  ) || null;
    92→}
    93→
    94→/**
    95→ * Calculate progress information for a proposal
    96→ * @param {number} currentStage - Current stage ID (1-6)
    97→ * @param {Array<number>} completedStages - Array of completed stage IDs
    98→ * @returns {Object} Progress information including percentage and next stage
    99→ */
   100→export function getStageProgress(currentStage, completedStages = []) {
   101→  const totalStages = PROPOSAL_STAGES.length;
   102→  const completed = completedStages.length;
   103→  const percentage = Math.round((completed / totalStages) * 100);
   104→  const nextStage = PROPOSAL_STAGES.find(s => s.id === currentStage + 1) || null;
   105→
   106→  return {
   107→    current: currentStage,
   108→    completed: completedStages,
   109→    percentage,
   110→    nextStage,
   111→    isComplete: completed === totalStages,
   112→    remainingStages: totalStages - completed
   113→  };
   114→}
   115→
   116→/**
   117→ * Get all stages up to and including the current stage
   118→ * @param {number} currentStage - Current stage ID (1-6)
   119→ * @returns {Array<Object>} Array of stage configurations
   120→ */
   121→export function getCompletedStages(currentStage) {
   122→  if (!currentStage) {
   123→    return [];
   124→  }
   125→  return PROPOSAL_STAGES.filter(s => s.id <= currentStage);
   126→}
   127→
   128→/**
   129→ * Get all stages after the current stage
   130→ * @param {number} currentStage - Current stage ID (1-6)
   131→ * @returns {Array<Object>} Array of stage configurations
   132→ */
   133→export function getRemainingStages(currentStage) {
   134→  if (!currentStage) {
   135→    return PROPOSAL_STAGES;
   136→  }
   137→  return PROPOSAL_STAGES.filter(s => s.id > currentStage);
   138→}
   139→
   140→/**
   141→ * Check if a stage is completed
   142→ * @param {number} stageId - Stage ID to check
   143→ * @param {number} currentStage - Current stage ID
   144→ * @returns {boolean} True if stage is completed
   145→ */
   146→export function isStageCompleted(stageId, currentStage) {
   147→  if (!stageId || !currentStage) {
   148→    return false;
   149→  }
   150→  return stageId < currentStage;
   151→}
   152→
   153→/**
   154→ * Check if a stage is the current active stage
   155→ * @param {number} stageId - Stage ID to check
   156→ * @param {number} currentStage - Current stage ID
   157→ * @returns {boolean} True if stage is current
   158→ */
   159→export function isCurrentStage(stageId, currentStage) {
   160→  if (!stageId || !currentStage) {
   161→    return false;
   162→  }
   163→  return stageId === currentStage;
   164→}
   165→
   166→/**
   167→ * Check if a stage is pending (not yet reached)
   168→ * @param {number} stageId - Stage ID to check
   169→ * @param {number} currentStage - Current stage ID
   170→ * @returns {boolean} True if stage is pending
   171→ */
   172→export function isStagePending(stageId, currentStage) {
   173→  if (!stageId || !currentStage) {
   174→    return true;
   175→  }
   176→  return stageId > currentStage;
   177→}
   178→
   179→/**
   180→ * Get the previous stage from a given stage
   181→ * @param {number} stageId - Current stage ID
   182→ * @returns {Object|null} Previous stage configuration or null if first stage
   183→ */
   184→export function getPreviousStage(stageId) {
   185→  if (!stageId || stageId <= 1) {
   186→    return null;
   187→  }
   188→  return getStageById(stageId - 1);
   189→}
   190→
   191→/**
   192→ * Get the next stage from a given stage
   193→ * @param {number} stageId - Current stage ID
   194→ * @returns {Object|null} Next stage configuration or null if last stage
   195→ */
   196→export function getNextStage(stageId) {
   197→  if (!stageId || stageId >= PROPOSAL_STAGES.length) {
   198→    return null;
   199→  }
   200→  return getStageById(stageId + 1);
   201→}
   202→
   203→/**
   204→ * Format stage for display with status indicator
   205→ * @param {number} stageId - Stage ID
   206→ * @param {number} currentStage - Current stage ID
   207→ * @returns {Object} Formatted stage object with status
   208→ */
   209→export function formatStageDisplay(stageId, currentStage) {
   210→  const stage = getStageById(stageId);
   211→  if (!stage) {
   212→    return null;
   213→  }
