/**
 * Proposal Progress Stage Configuration System
 *
 * This module defines the 6 stages of the proposal-to-lease workflow,
 * replacing hardcoded stage arrays with rich configuration objects.
 *
 * Each stage includes:
 * - id: Stage number (1-6)
 * - name: Full stage name
 * - shortName: Abbreviated name for compact displays
 * - icon: Emoji or icon identifier
 * - description: User-friendly explanation of the stage
 * - helpText: Additional guidance for guests
 */

export const PROPOSAL_STAGES = [
  {
    id: 1,
    name: 'Proposal Submitted',
    shortName: 'Submitted',
    icon: '1',
    description: 'Your proposal has been submitted to the host',
    helpText: 'The next step is to complete your rental application so the host can review your profile.'
  },
  {
    id: 2,
    name: 'Rental App Submitted',
    shortName: 'Application',
    icon: '2',
    description: 'Your rental application is complete',
    helpText: 'The host is reviewing your application and will respond soon.'
  },
  {
    id: 3,
    name: 'Host Review',
    shortName: 'Review',
    icon: '3',
    description: 'Host is reviewing your proposal',
    helpText: 'The host may accept your proposal, request changes, or propose a counteroffer.'
  },
  {
    id: 4,
    name: 'Review Documents',
    shortName: 'Documents',
    icon: '4',
    description: 'Review proposal documents and terms',
    helpText: 'Carefully review all documents before proceeding to the lease agreement.'
  },
  {
    id: 5,
    name: 'Lease Documents',
    shortName: 'Lease',
    icon: '5',
    description: 'Review and sign lease documents',
    helpText: 'Read the lease agreement thoroughly and sign when ready.'
  },
  {
    id: 6,
    name: 'Initial Payment',
    shortName: 'Payment',
    icon: '6',
    description: 'Submit initial payment to activate lease',
    helpText: 'Submit your first payment to activate the lease and finalize your reservation.'
  }
];

/**
 * Get stage configuration by ID
 * @param {number} stageId - Stage ID (1-6)
 * @returns {Object|null} Stage configuration object or null if not found
 */
export function getStageById(stageId) {
  if (!stageId || stageId < 1 || stageId > 6) {
    return null;
  }
  return PROPOSAL_STAGES.find(s => s.id === stageId) || null;
}

/**
 * Get stage configuration by name (case-insensitive)
 * @param {string} stageName - Stage name
 * @returns {Object|null} Stage configuration object or null if not found
 */
export function getStageByName(stageName) {
  if (!stageName) {
    return null;
  }
  const normalizedName = stageName.toLowerCase();
  return PROPOSAL_STAGES.find(
    s => s.name.toLowerCase() === normalizedName || s.shortName.toLowerCase() === normalizedName
  ) || null;
}

/**
 * Calculate progress information for a proposal
 * @param {number} currentStage - Current stage ID (1-6)
 * @param {Array<number>} completedStages - Array of completed stage IDs
 * @returns {Object} Progress information including percentage and next stage
 */
export function getStageProgress(currentStage, completedStages = []) {
  const totalStages = PROPOSAL_STAGES.length;
  const completed = completedStages.length;
  const percentage = Math.round((completed / totalStages) * 100);
  const nextStage = PROPOSAL_STAGES.find(s => s.id === currentStage + 1) || null;

  return {
    current: currentStage,
    completed: completedStages,
    percentage,
    nextStage,
    isComplete: completed === totalStages,
    remainingStages: totalStages - completed
  };
}

/**
 * Get all stages up to and including the current stage
 * @param {number} currentStage - Current stage ID (1-6)
 * @returns {Array<Object>} Array of stage configurations
 */
export function getCompletedStages(currentStage) {
  if (!currentStage) {
    return [];
  }
  return PROPOSAL_STAGES.filter(s => s.id <= currentStage);
}

/**
 * Get all stages after the current stage
 * @param {number} currentStage - Current stage ID (1-6)
 * @returns {Array<Object>} Array of stage configurations
 */
export function getRemainingStages(currentStage) {
  if (!currentStage) {
    return PROPOSAL_STAGES;
  }
  return PROPOSAL_STAGES.filter(s => s.id > currentStage);
}

/**
 * Check if a stage is completed
 * @param {number} stageId - Stage ID to check
 * @param {number} currentStage - Current stage ID
 * @returns {boolean} True if stage is completed
 */
export function isStageCompleted(stageId, currentStage) {
  if (!stageId || !currentStage) {
    return false;
  }
  return stageId < currentStage;
}

/**
 * Check if a stage is the current active stage
 * @param {number} stageId - Stage ID to check
 * @param {number} currentStage - Current stage ID
 * @returns {boolean} True if stage is current
 */
export function isCurrentStage(stageId, currentStage) {
  if (!stageId || !currentStage) {
    return false;
  }
  return stageId === currentStage;
}

/**
 * Check if a stage is pending (not yet reached)
 * @param {number} stageId - Stage ID to check
 * @param {number} currentStage - Current stage ID
 * @returns {boolean} True if stage is pending
 */
export function isStagePending(stageId, currentStage) {
  if (!stageId || !currentStage) {
    return true;
  }
  return stageId > currentStage;
}

/**
 * Get the previous stage from a given stage
 * @param {number} stageId - Current stage ID
 * @returns {Object|null} Previous stage configuration or null if first stage
 */
export function getPreviousStage(stageId) {
  if (!stageId || stageId <= 1) {
    return null;
  }
  return getStageById(stageId - 1);
}

/**
 * Get the next stage from a given stage
 * @param {number} stageId - Current stage ID
 * @returns {Object|null} Next stage configuration or null if last stage
 */
export function getNextStage(stageId) {
  if (!stageId || stageId >= PROPOSAL_STAGES.length) {
    return null;
  }
  return getStageById(stageId + 1);
}

/**
 * Format stage for display with status indicator
 * @param {number} stageId - Stage ID
 * @param {number} currentStage - Current stage ID
 * @returns {Object} Formatted stage object with status
 */
export function formatStageDisplay(stageId, currentStage) {
  const stage = getStageById(stageId);
  if (!stage) {
    return null;
  }

  let status = 'pending';
  let statusLabel = 'Pending';

  if (isStageCompleted(stageId, currentStage)) {
    status = 'completed';
    statusLabel = 'Completed';
  } else if (isCurrentStage(stageId, currentStage)) {
    status = 'current';
    statusLabel = 'In Progress';
  }

  return {
    ...stage,
    status,
    statusLabel,
    isCompleted: status === 'completed',
    isCurrent: status === 'current',
    isPending: status === 'pending'
  };
}

/**
 * Get all stages formatted for display
 * @param {number} currentStage - Current stage ID
 * @returns {Array<Object>} Array of formatted stage objects
 */
export function getAllStagesFormatted(currentStage) {
  return PROPOSAL_STAGES.map(stage => formatStageDisplay(stage.id, currentStage));
}

/**
 * Proposal Status Configuration System
 *
 * Centralized configuration for all proposal statuses,
 * replacing hardcoded status mappings throughout the application.
 *
 * Each status includes:
 * - key: The exact status string from the database
 * - color: UI color theme for the status
 * - label: User-friendly display text
 * - stage: The progress stage number (1-6) or null if not in active flow
 * - actions: Available actions for this status
 */

export const PROPOSAL_STATUSES = {
  // ===== CANCELLED STATES =====
  CANCELLED_BY_GUEST: {
    key: 'Proposal Cancelled by Guest',
    color: 'red',
    label: 'Cancelled by You',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  CANCELLED_BY_SPLITLEASE: {
    key: 'Proposal Cancelled by Split Lease',
    color: 'red',
    label: 'Proposal Cancelled',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  // ===== REJECTED STATES =====
  REJECTED_BY_HOST: {
    key: 'Proposal Rejected by Host',
    color: 'red',
    label: 'Rejected by Host',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  },

  // ===== ACTIVE PROPOSAL FLOW =====
  // usualOrder values match reference_table.os_proposal_status sort_order
  // sort_order: -1 = terminal (cancelled/rejected), 0-7 = active flow

  // usualOrder 0: Proposal Submitted, Awaiting Rental Application
  PROPOSAL_SUBMITTED_AWAITING_RENTAL_APP: {
    key: 'Proposal Submitted by guest - Awaiting Rental Application',
    color: 'blue',
    label: 'Submit Rental Application',
    stage: 1,
    usualOrder: 0,
    actions: ['submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  // Suggested proposal by Split Lease agent - awaiting rental application (sort_order 0)
  // NEW MEANING: Guest has CONFIRMED but rental app not yet submitted
  SUGGESTED_PROPOSAL_AWAITING_RENTAL_APP: {
    key: 'Proposal Submitted for guest by Split Lease - Awaiting Rental Application',
    color: 'purple',
    label: 'Suggested Proposal - Awaiting Rental App',
    stage: 1,
    usualOrder: 0,
    isSuggestedBySL: true,
    guestConfirmed: true,
    actions: ['submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  // Suggested proposal by Split Lease agent - pending confirmation (sort_order 0)
  // NEW MEANING: Initial state - guest hasn't confirmed yet
  SUGGESTED_PROPOSAL_PENDING_CONFIRMATION: {
    key: 'Proposal Submitted for guest by Split Lease - Pending Confirmation',
    color: 'purple',
    label: 'Suggested Proposal - Pending Confirmation',
    stage: 1,
    usualOrder: 0,
    isSuggestedBySL: true,
    guestConfirmed: false,
    actions: ['confirm_proposal', 'submit_rental_app', 'cancel_proposal', 'request_vm', 'send_message']
  },

  PENDING_CONFIRMATION: {
    key: 'Pending Confirmation',
    color: 'blue',
    label: 'Awaiting Confirmation',
    stage: 1,
    usualOrder: 0,
    actions: ['cancel_proposal', 'request_vm', 'send_message']
  },

  // Legacy pending status (treated as sort_order 0)
  PENDING: {
    key: 'Pending',
    color: 'blue',
    label: 'Pending',
    stage: 1,
    usualOrder: 0,
    actions: ['cancel_proposal', 'request_vm', 'send_message']
  },

  // usualOrder 1: Host Review (after rental app submitted)
  HOST_REVIEW: {
    key: 'Host Review',
    color: 'blue',
    label: 'Under Host Review',
    stage: 3,
    usualOrder: 1,
    actions: ['request_vm', 'cancel_proposal', 'send_message']
  },

  // Rental Application Submitted - transitional state before Host Review (sort_order 1)
  RENTAL_APP_SUBMITTED: {
    key: 'Rental Application Submitted',
    color: 'blue',
    label: 'Application Under Review',
    stage: 2,
    usualOrder: 1,
    actions: ['request_vm', 'cancel_proposal', 'send_message']
  },

  // usualOrder 2: Host Counteroffer
  COUNTEROFFER_SUBMITTED_AWAITING_GUEST_REVIEW: {
    key: 'Host Counteroffer Submitted / Awaiting Guest Review',
    color: 'yellow',
    label: 'Review Host Counteroffer',
    stage: 3,
    usualOrder: 2,
    actions: ['review_counteroffer', 'compare_terms', 'accept_counteroffer', 'decline_counteroffer', 'request_vm', 'send_message']
  },

  // usualOrder 3: Accepted / Drafting
  PROPOSAL_OR_COUNTEROFFER_ACCEPTED: {
    key: 'Proposal or Counteroffer Accepted / Drafting Lease Documents',
    color: 'green',
    label: 'Accepted - Drafting Lease',
    stage: 4,
    usualOrder: 3,
    actions: ['request_vm', 'send_message']
  },

  // Reviewing Documents (variant of sort_order 3)
  REVIEWING_DOCUMENTS: {
    key: 'Reviewing Documents',
    color: 'blue',
    label: 'Reviewing Documents',
    stage: 4,
    usualOrder: 3,
    actions: ['review_documents', 'request_vm', 'send_message']
  },

  // usualOrder 4: Lease Documents Sent for Review
  LEASE_DOCUMENTS_SENT_FOR_REVIEW: {
    key: 'Lease Documents Sent for Review',
    color: 'blue',
    label: 'Review Lease Documents',
    stage: 4,
    usualOrder: 4,
    actions: ['review_documents', 'request_vm', 'send_message']
  },

  // usualOrder 5: Lease documents sent for legal signatures
  LEASE_DOCUMENTS_SENT_FOR_SIGNATURES: {
    key: 'Lease Documents Sent for Signatures',
    color: 'green',
    label: 'Sign Lease Documents',
    stage: 5,
    usualOrder: 5,
    actions: ['sign_documents', 'request_vm', 'send_message']
  },

  // usualOrder 6: Lease signed, awaiting payment (note: lowercase "payment" matches Bubble)
  LEASE_DOCUMENTS_SIGNED_AWAITING_PAYMENT: {
    key: 'Lease Documents Signed / Awaiting Initial payment',
    color: 'green',
    label: 'Submit Initial Payment',
    stage: 6,
    usualOrder: 6,
    actions: ['submit_payment', 'request_vm', 'send_message']
  },

  // Legacy key format (keeping for backwards compatibility) - sort_order 6
  LEASE_SIGNED_AWAITING_INITIAL_PAYMENT: {
    key: 'Lease Signed / Awaiting Initial Payment',
    color: 'green',
    label: 'Submit Initial Payment',
    stage: 6,
    usualOrder: 6,
    actions: ['submit_payment', 'request_vm', 'send_message']
  },

  // usualOrder 7: Initial Payment / Lease Activated
  INITIAL_PAYMENT_SUBMITTED_LEASE_ACTIVATED: {
    key: 'Initial Payment Submitted / Lease activated ',
    color: 'green',
    label: 'Lease Activated',
    stage: 6,
    usualOrder: 7,
    actions: ['view_lease', 'view_house_manual', 'send_message']
  },

  // ===== SPECIAL STATES =====

  DRAFT: {
    key: 'Draft',
    color: 'gray',
    label: 'Draft',
    stage: null,
    usualOrder: 0,
    actions: ['edit_proposal', 'submit_proposal', 'delete_proposal']
  },

  EXPIRED: {
    key: 'Expired',
    color: 'gray',
    label: 'Expired',
    stage: null,
    usualOrder: 99,
    actions: ['view_listing', 'explore_rentals']
  }
};

/**
 * Normalize status key by trimming whitespace
 * Database values from Bubble may have trailing spaces
 * @param {string} statusKey - The raw status string
 * @returns {string} Trimmed status string
 */
function normalizeStatusKey(statusKey) {
  return typeof statusKey === 'string' ? statusKey.trim() : statusKey;
}

/**
 * Get status configuration by status key
 * @param {string} statusKey - The status string from the database
 * @returns {Object} Status configuration object with color, label, stage, actions
 */
export function getStatusConfig(statusKey) {
  if (!statusKey) {
    return {
      key: 'Unknown',
      color: 'gray',
      label: 'Unknown Status',
      stage: null,
      usualOrder: 0,
      actions: []
    };
  }

  // Normalize status key to handle trailing spaces from Bubble data
  const normalizedKey = normalizeStatusKey(statusKey);

  const config = Object.values(PROPOSAL_STATUSES).find(
    s => normalizeStatusKey(s.key) === normalizedKey
  );

  return config || {
    key: normalizedKey,
    color: 'gray',
    label: normalizedKey,
    stage: null,
    usualOrder: 0,
    actions: []
  };
}

/**
 * Get the progress stage number from a status key
 * @param {string} statusKey - The status string from the database
 * @returns {number|null} Stage number (1-6) or null if not in active flow
 */
export function getStageFromStatus(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.stage;
}

/**
 * Get the usual order number from a status key (Bubble ordering)
 * @param {string} statusKey - The status string from the database
 * @returns {number} Usual order number (1-7 for active, 99 for terminal, 0 for unknown)
 */
export function getUsualOrder(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.usualOrder ?? 0;
}

/**
 * Check if status banner should be visible
 * Banner shows when usualOrder >= 3 OR status is "Proposal Submitted by guest - Awaiting Rental Application"
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if banner should be shown
 */
export function shouldShowStatusBanner(statusKey) {
  if (!statusKey) return false;

  const usualOrder = getUsualOrder(statusKey);
  const normalizedKey = normalizeStatusKey(statusKey);
  const isProposalSubmitted = normalizedKey === normalizeStatusKey(PROPOSAL_STATUSES.PROPOSAL_SUBMITTED_AWAITING_RENTAL_APP.key);

  return usualOrder >= 3 || isProposalSubmitted;
}

/**
 * Get available actions for a status
 * @param {string} statusKey - The status string from the database
 * @returns {Array<string>} Array of action identifiers
 */
export function getActionsForStatus(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.actions || [];
}

/**
 * Check if a status is in an active proposal flow
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if status has a stage number (active flow)
 */
export function isActiveStatus(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.stage !== null;
}

/**
 * Check if a status is cancelled or rejected
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if status is cancelled or rejected
 */
export function isTerminalStatus(statusKey) {
  const config = getStatusConfig(statusKey);
  const normalizedKey = normalizeStatusKey(statusKey) || '';
  return config.color === 'red' || normalizedKey.includes('Cancelled') || normalizedKey.includes('Rejected');
}

/**
 * Check if a status is completed (lease activated)
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if lease is activated
 */
export function isCompletedStatus(statusKey) {
  const normalizedKey = normalizeStatusKey(statusKey);
  return normalizedKey === normalizeStatusKey(PROPOSAL_STATUSES.INITIAL_PAYMENT_SUBMITTED_LEASE_ACTIVATED.key);
}

/**
 * Check if a status is a suggested proposal (created by Split Lease agent)
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if proposal was suggested by Split Lease
 */
export function isSuggestedProposal(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.isSuggestedBySL === true;
}

/**
 * Get all statuses by color category
 * @param {string} color - Color category ('red', 'yellow', 'blue', 'green', 'gray')
 * @returns {Array<Object>} Array of status configurations
 */
export function getStatusesByColor(color) {
  return Object.values(PROPOSAL_STATUSES).filter(s => s.color === color);
}

/**
 * Get all statuses for a specific stage
 * @param {number} stageNumber - Stage number (1-6)
 * @returns {Array<Object>} Array of status configurations
 */
export function getStatusesByStage(stageNumber) {
  return Object.values(PROPOSAL_STATUSES).filter(s => s.stage === stageNumber);
}

/**
 * Check if a status is a suggested proposal pending guest confirmation
 * (Guest hasn't confirmed the Split Lease suggestion yet)
 * @param {string} statusKey - The status string from the database
 * @returns {boolean} True if pending confirmation
 */
export function isPendingConfirmationProposal(statusKey) {
  const config = getStatusConfig(statusKey);
  return config.isSuggestedBySL === true && config.guestConfirmed === false;
}

/**
 * Review category constants.
 * Defines the 12 behavioral dimensions for guest reviews.
 */

export const REVIEW_CATEGORY_COUNT = 12;

export const REVIEW_CATEGORIES = [
  {
    id: 'check_in_out',
    title: 'Check-in/Check-out Etiquette',
    question: 'How well did the guest respect check-in and check-out times?'
  },
  {
    id: 'communication',
    title: 'Communication',
    question: 'How responsive and clear was the guest in communication?'
  },
  {
    id: 'cleanliness',
    title: 'Cleanliness',
    question: 'How clean was the property left after the stay?'
  },
  {
    id: 'payment',
    title: 'Payment Reliability',
    question: 'How reliable was the guest with payments?'
  },
  {
    id: 'house_rules',
    title: 'House Rules Compliance',
    question: 'How well did the guest follow house rules?'
  },
  {
    id: 'noise',
    title: 'Noise Consideration',
    question: 'How considerate was the guest regarding noise levels?'
  },
  {
    id: 'amenity_usage',
    title: 'Amenity Usage',
    question: 'How responsibly did the guest use amenities?'
  },
  {
    id: 'trash',
    title: 'Trash & Recycling',
    question: 'How well did the guest handle trash and recycling?'
  },
  {
    id: 'neighbor_respect',
    title: 'Neighbor Respect',
    question: 'How respectful was the guest to neighbors?'
  },
  {
    id: 'property_care',
    title: 'Property Care',
    question: 'How well did the guest care for the property?'
  },
  {
    id: 'guest_behavior',
    title: 'Guest Behavior',
    question: 'How appropriate was the guest\'s overall behavior?'
  },
  {
    id: 'recommendation',
    title: 'Would Recommend',
    question: 'Would you recommend this guest to other hosts?'
  }
];

export const RATING_SCALE_LABELS = [
  '', // Index 0 unused
  'Very poor',
  'Poor',
  'Average',
  'Good',
  'Excellent'
];

export const PRICE_TIERS = Object.freeze({
  UNDER_200: 'under-200',
  TIER_200_350: '200-350',
  TIER_350_500: '350-500',
  OVER_500: '500-plus',
  ALL: 'all'
});

export const VALID_PRICE_TIERS = Object.freeze(Object.values(PRICE_TIERS));

export const SORT_OPTIONS = Object.freeze({
  RECOMMENDED: 'recommended',
  PRICE_LOW: 'price-low',
  MOST_VIEWED: 'most-viewed',
  RECENT: 'recent'
});

export const VALID_SORT_OPTIONS = Object.freeze(Object.values(SORT_OPTIONS));

export const WEEK_PATTERNS = Object.freeze({
  EVERY_WEEK: 'every-week',
  ONE_ON_OFF: 'one-on-off',
  TWO_ON_OFF: 'two-on-off',
  ONE_THREE_OFF: 'one-three-off'
});

export const VALID_WEEK_PATTERNS = Object.freeze(Object.values(WEEK_PATTERNS));

/**
 * Format full host name to "FirstName L." format for privacy.
 *
 * @intent Format host name for display with privacy (show first name + last initial).
 * @rule NO FALLBACK: Throws error if name is invalid.
 * @rule Single name: Return as-is (e.g., "John" → "John").
 * @rule Multiple names: Return "FirstName L." (e.g., "John Smith" → "John S.").
 *
 * @param {object} params - Named parameters.
 * @param {string} params.fullName - Full name of the host.
 * @returns {string} Formatted name with last initial.
 *
 * @throws {Error} If fullName is not a string or is empty.
 *
 * @example
 * const formatted = formatHostName({ fullName: 'John Smith' })
 * // => "John S."
 *
 * const single = formatHostName({ fullName: 'John' })
 * // => "John"
 */
export function formatHostName({ fullName }) {
  // No Fallback: Strict validation
  if (typeof fullName !== 'string') {
    throw new Error(
      `formatHostName: fullName must be a string, got ${typeof fullName}`
    )
  }

  const trimmedName = fullName.trim()

  if (!trimmedName || trimmedName.length === 0) {
    throw new Error('formatHostName: fullName cannot be empty or whitespace')
  }

  // Split name into parts
  const nameParts = trimmedName.split(/\s+/)

  // Single name: return as-is
  if (nameParts.length === 1) {
    return nameParts[0]
  }

  // Multiple names: "FirstName L."
  const firstName = nameParts[0]
  const lastInitial = nameParts[nameParts.length - 1].charAt(0).toUpperCase()

  return `${firstName} ${lastInitial}.`
}

/**
 * Extract coordinates from listing's JSONB location fields.
 * Priority: "Location - slightly different address" (privacy) → "Location - Address" (main).
 *
 * @intent Extract lat/lng coordinates from listing location data with privacy prioritization.
 * @rule NO FALLBACK: Returns null if no valid coordinates found (listings without coordinates must be filtered out).
 * @rule Priority 1: Use "Location - slightly different address" for privacy/pin separation.
 * @rule Priority 2: Use "Location - Address" as fallback.
 *
 * @param {object} params - Named parameters.
 * @param {object|string|null} params.locationSlightlyDifferent - JSONB field for privacy-adjusted address.
 * @param {object|string|null} params.locationAddress - JSONB field for main address.
 * @param {string} params.listingId - Listing ID for error logging.
 * @returns {object|null} Coordinates object { lat, lng, source } or null if invalid.
 *
 * @throws {Error} If listingId is not provided.
 *
 * @example
 * const coords = extractListingCoordinates({
 *   locationSlightlyDifferent: { lat: 40.7128, lng: -74.0060 },
 *   locationAddress: { lat: 40.7127, lng: -74.0061 },
 *   listingId: 'listing_123'
 * })
 * // => { lat: 40.7128, lng: -74.0060, source: 'slightly-different-address' }
 */
export function extractListingCoordinates({
  locationSlightlyDifferent,
  locationAddress,
  listingId
}) {
  // No Fallback: Validate listing ID
  if (!listingId || typeof listingId !== 'string') {
    throw new Error(
      `extractListingCoordinates: listingId is required and must be a string`
    )
  }

  // Parse JSONB fields if they're strings
  let parsedSlightlyDifferent = locationSlightlyDifferent
  let parsedAddress = locationAddress

  if (typeof locationSlightlyDifferent === 'string') {
    try {
      parsedSlightlyDifferent = JSON.parse(locationSlightlyDifferent)
    } catch (error) {
      console.error(
        '❌ extractListingCoordinates: Failed to parse Location - slightly different address:',
        {
          listingId,
          rawValue: locationSlightlyDifferent,
          error: error.message
        }
      )
      parsedSlightlyDifferent = null
    }
  }

  if (typeof locationAddress === 'string') {
    try {
      parsedAddress = JSON.parse(locationAddress)
    } catch (error) {
      console.error('❌ extractListingCoordinates: Failed to parse Location - Address:', {
        listingId,
        rawValue: locationAddress,
        error: error.message
      })
      parsedAddress = null
    }
  }

  // Priority 1: Check slightly different address
  if (
    parsedSlightlyDifferent &&
    typeof parsedSlightlyDifferent.lat === 'number' &&
    typeof parsedSlightlyDifferent.lng === 'number'
  ) {
    return {
      lat: parsedSlightlyDifferent.lat,
      lng: parsedSlightlyDifferent.lng,
      source: 'slightly-different-address'
    }
  }

  // Priority 2: Check main address
  if (
    parsedAddress &&
    typeof parsedAddress.lat === 'number' &&
    typeof parsedAddress.lng === 'number'
  ) {
    return {
      lat: parsedAddress.lat,
      lng: parsedAddress.lng,
      source: 'main-address'
    }
  }

  // No Fallback: Return null if no valid coordinates found
  console.warn('⚠️ extractListingCoordinates: No valid coordinates found for listing:', {
    listingId,
    hasSlightlyDifferent: !!parsedSlightlyDifferent,
    hasMainAddress: !!parsedAddress
  })

  return null
}

/**
 * Parse JSONB field that may be double-encoded as JSON string.
 * Handles both native arrays and JSON-stringified arrays from Supabase.
 *
 * @intent Guarantee valid array data from Supabase JSONB fields.
 * @rule NO FALLBACK - throws explicit errors for invalid data.
 * @rule Fail loud with descriptive errors to surface data quality issues.
 *
 * This is a "Truth" layer processor that validates data shape before it reaches the UI.
 *
 * @param {object} params - Named parameters.
 * @param {any} params.field - JSONB field value from Supabase.
 * @param {string} params.fieldName - Name of field for error messages (e.g., "Features - Amenities").
 * @returns {Array} Parsed and validated array.
 *
 * @throws {Error} If field is null/undefined (data missing).
 * @throws {Error} If field is a string but cannot be parsed as JSON.
 * @throws {Error} If parsed result is not an array.
 * @throws {Error} If field is an unexpected type (not null, array, or string).
 *
 * @example
 * // Already an array
 * const result = parseJsonArrayField({
 *   field: ['amenity1', 'amenity2'],
 *   fieldName: 'Features - Amenities'
 * })
 * // => ['amenity1', 'amenity2']
 *
 * // JSON-encoded string (double-encoded)
 * const result = parseJsonArrayField({
 *   field: '["amenity1","amenity2"]',
 *   fieldName: 'Features - Amenities'
 * })
 * // => ['amenity1', 'amenity2']
 *
 * // Null field throws error
 * parseJsonArrayField({ field: null, fieldName: 'Features - Amenities' })
 * // => Error: parseJsonArrayField: Features - Amenities is null or undefined
 */
export function parseJsonArrayField({ field, fieldName }) {
  // No Fallback: Validate fieldName is provided
  if (!fieldName || typeof fieldName !== 'string') {
    throw new Error(
      'parseJsonArrayField: fieldName parameter is required and must be a string'
    )
  }

  // No Fallback: Null/undefined is an error (data missing)
  if (field === null || field === undefined) {
    throw new Error(
      `parseJsonArrayField: ${fieldName} is null or undefined`
    )
  }

  // Already an array - validate and return
  if (Array.isArray(field)) {
    return field
  }

  // String that needs parsing (double-encoded JSONB)
  if (typeof field === 'string') {
    let parsed
    try {
      parsed = JSON.parse(field)
    } catch (e) {
      throw new Error(
        `parseJsonArrayField: Failed to parse ${fieldName} as JSON - ${e.message}`
      )
    }

    // Ensure parsed result is an array
    if (!Array.isArray(parsed)) {
      throw new Error(
        `parseJsonArrayField: ${fieldName} parsed to ${typeof parsed}, expected array. Value: ${JSON.stringify(parsed)}`
      )
    }

    return parsed
  }

  // Unexpected type - fail loud
  throw new Error(
    `parseJsonArrayField: ${fieldName} has unexpected type ${typeof field}. Expected array or JSON string. Value: ${JSON.stringify(field)}`
  )
}

/**
 * Parse JSONB array field with fallback to empty array for optional fields.
 * Use this ONLY for truly optional fields where an empty array is a valid business state.
 *
 * @intent Parse optional JSONB arrays with explicit empty-array default.
 * @rule Use sparingly - prefer parseJsonArrayField for required fields.
 * @rule Still validates that provided data is correct format.
 *
 * @param {object} params - Named parameters.
 * @param {any} params.field - JSONB field value from Supabase.
 * @param {string} params.fieldName - Name of field for error messages.
 * @returns {Array} Parsed array or empty array if field is null/undefined.
 *
 * @throws {Error} If field is provided but invalid (not parseable or not an array).
 *
 * @example
 * // Optional field not provided - returns empty array
 * const result = parseJsonArrayFieldOptional({
 *   field: null,
 *   fieldName: 'Optional Amenities'
 * })
 * // => []
 */
export function parseJsonArrayFieldOptional({ field, fieldName }) {
  // Optional: Allow null/undefined/empty string with explicit empty array fallback
  if (field === null || field === undefined || field === '') {
    return []
  }

  // For non-null fields, use strict validation
  return parseJsonArrayField({ field, fieldName })
}

/**
 * Proposal Data Processor
 *
 * PILLAR III: Data Processors (The "Truth" Layer)
 *
 * This processor transforms raw Supabase/Bubble.io data into clean,
 * validated internal shapes. It enforces the "No Fallback" principle
 * by throwing explicit errors for missing critical data.
 *
 * Intent: Create safe, typed objects for UI consumption, enforcing data integrity.
 */

/**
 * Transform raw listing data from Bubble.io format
 * @param {Object} rawListing - Raw listing object from Supabase
 * @returns {Object} Transformed listing object
 * @throws {Error} If rawListing is null/undefined
 */
export function processListingData(rawListing) {
  if (!rawListing) {
    throw new Error('processListingData: Listing data is required');
  }

  // Extract address from JSONB structure
  const addressData = rawListing['Location - Address'];
  const addressString = typeof addressData === 'object' && addressData?.address
    ? addressData.address
    : (typeof addressData === 'string' ? addressData : null);

  return {
    id: rawListing._id,
    name: rawListing.Name || 'Untitled Listing',
    description: rawListing.Description || null,
    address: addressString,
    addressData: addressData, // Keep full JSONB for map coordinates
    borough: rawListing['Location - Borough'] || null,
    hood: rawListing['Location - Hood'] || null,
    boroughName: rawListing.boroughName || null, // Resolved name from lookup table
    hoodName: rawListing.hoodName || null, // Resolved name from lookup table
    photos: rawListing['Features - Photos'] || [],
    featuredPhotoUrl: rawListing.featuredPhotoUrl || null,
    houseRules: rawListing.houseRules || [],
    checkInTime: rawListing['NEW Date Check-in Time'] || null,
    checkOutTime: rawListing['NEW Date Check-out Time'] || null,
    hostUserId: rawListing['Host User'] || null
  };
}

/**
 * Transform raw host data from Bubble.io format
 * @param {Object} rawHost - Raw host object from Supabase
 * @returns {Object} Transformed host object
 * @throws {Error} If rawHost is null/undefined
 */
export function processHostData(rawHost) {
  if (!rawHost) {
    throw new Error('processHostData: Host data is required');
  }

  return {
    id: rawHost._id,
    firstName: rawHost['Name - First'] || null,
    lastName: rawHost['Name - Last'] || null,
    fullName: rawHost['Name - Full'] || null,
    profilePhoto: rawHost['Profile Photo'] || null,
    bio: rawHost['About Me / Bio'] || null,
    linkedInVerified: rawHost['Verify - Linked In ID'] || false,
    phoneVerified: rawHost['Verify - Phone'] || false,
    userVerified: rawHost['user verified?'] || false,
    // hostUserId same as user._id after migration (Host User column contains user._id directly)
    hostUserId: rawHost._id || null
  };
}

/**
 * Transform raw virtual meeting data from Bubble.io format
 * @param {Object} rawVirtualMeeting - Raw virtual meeting object from Supabase
 * @returns {Object} Transformed virtual meeting object
 * @throws {Error} If rawVirtualMeeting is null/undefined
 */
export function processVirtualMeetingData(rawVirtualMeeting) {
  if (!rawVirtualMeeting) {
    throw new Error('processVirtualMeetingData: Virtual meeting data is required');
  }

  return {
    id: rawVirtualMeeting._id,
    bookedDate: rawVirtualMeeting['booked date'] || null,
    confirmedBySplitlease: rawVirtualMeeting.confirmedBySplitLease || false,
    meetingLink: rawVirtualMeeting['meeting link'] || null,
    meetingDeclined: rawVirtualMeeting['meeting declined'] || false,
    requestedBy: rawVirtualMeeting['requested by'] || null,
    suggestedTimeslots: rawVirtualMeeting['suggested dates and times'] || [],
    guestName: rawVirtualMeeting['guest name'] || null,
    hostName: rawVirtualMeeting['host name'] || null,
    proposalId: rawVirtualMeeting.proposal || null,
    uniqueId: rawVirtualMeeting.unique_id || null
  };
}

/**
 * Transform complete proposal data from Bubble.io format
 * Includes nested transformations for listing, host, and virtual meeting
 *
 * @param {Object} rawProposal - Raw proposal object from Supabase
 * @returns {Object} Transformed proposal object
 */
export function processProposalData(rawProposal) {
  if (!rawProposal) {
    throw new Error('processProposalData: Proposal data is required');
  }

  if (!rawProposal._id) {
    throw new Error('processProposalData: Proposal ID (_id) is required');
  }

  // Extract and transform nested data
  const listing = processListingData(rawProposal.listing);
  const host = processHostData(rawProposal.listing?.host);
  const virtualMeeting = processVirtualMeetingData(rawProposal.virtualMeeting);

  return {
    // Core identifiers
    id: rawProposal._id,
    _id: rawProposal._id, // Keep for compatibility

    // Status and state
    status: rawProposal.Status || 'Unknown',
    deleted: rawProposal.Deleted || false,

    // Schedule - Original proposal terms
    daysSelected: rawProposal['Days Selected'] || [],
    nightsSelected: rawProposal['Nights Selected (Nights list)'] || [],
    reservationWeeks: rawProposal['Reservation Span (Weeks)'] || 0,
    nightsPerWeek: rawProposal['nights per week (num)'] || 0,
    checkInDay: rawProposal['check in day'] || null,
    checkOutDay: rawProposal['check out day'] || null,
    moveInStart: rawProposal['Move in range start'] || null,
    moveInEnd: rawProposal['Move in range end'] || null,

    // Pricing - Original proposal terms
    totalPrice: rawProposal['Total Price for Reservation (guest)'] || 0,
    nightlyPrice: rawProposal['proposal nightly price'] || 0,
    cleaningFee: rawProposal['cleaning fee'] || 0,
    damageDeposit: rawProposal['damage deposit'] || 0,

    // Counteroffer fields (hc = host-changed)
    counterOfferHappened: rawProposal['counter offer happened'] || false,
    hcDaysSelected: rawProposal['hc days selected'] || null,
    hcReservationWeeks: rawProposal['hc reservation span (weeks)'] || null,
    hcNightsPerWeek: rawProposal['hc nights per week'] || null,
    hcCheckInDay: rawProposal['hc check in day'] || null,
    hcCheckOutDay: rawProposal['hc check out day'] || null,
    hcTotalPrice: rawProposal['hc total price'] || null,
    hcNightlyPrice: rawProposal['hc nightly price'] || null,
    hcCleaningFee: rawProposal['hc cleaning fee'] || null,
    hcDamageDeposit: rawProposal['hc damage deposit'] || null,
    hcHouseRules: rawProposal['hc house rules'] || null,

    // Metadata
    createdDate: rawProposal['Created Date'] || null,
    modifiedDate: rawProposal['Modified Date'] || null,
    aboutYourself: rawProposal.about_yourself || null,
    specialNeeds: rawProposal.special_needs || null,
    reasonForCancellation: rawProposal['reason for cancellation'] || null,
    proposalStage: rawProposal['Proposal Stage'] || null,
    isFinalized: rawProposal['Is Finalized'] || false,
    isSuggestedByHost: rawProposal['Is Suggested by Host'] || false,

    // Related IDs
    guestId: rawProposal.Guest || null,
    listingId: rawProposal.Listing || null,
    rentalApplicationId: rawProposal['rental application'] || null,
    virtualMeetingId: rawProposal['virtual meeting'] || null,

    // Nested transformed data
    listing,
    host,
    virtualMeeting,

    // House rules (resolved from listing or counteroffer)
    houseRules: rawProposal.houseRules || listing?.houseRules || []
  };
}

/**
 * Get display text for proposal in dropdown
 * Format: "{host name} - {listing name}"
 * Shows the host who owns the listing, allowing guests to identify proposals by host
 *
 * @param {Object} proposal - Transformed proposal object
 * @returns {string} Display text for dropdown option
 */
export function getProposalDisplayText(proposal) {
  if (!proposal) return 'Unknown Proposal';

  const hostName = proposal.host?.firstName || proposal.host?.fullName || 'Host';
  const listingName = proposal.listing?.name || 'Property';

  return `${hostName} - ${listingName}`;
}

/**
 * Format price for display
 * @param {number} price - Price value
 * @param {boolean} includeCents - Whether to include cents
 * @returns {string} Formatted price string
 */
export function formatPrice(price, includeCents = true) {
  if (price === null || price === undefined) return null;

  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: includeCents ? 2 : 0,
    maximumFractionDigits: includeCents ? 2 : 0
  });

  return formatter.format(price);
}

/**
 * Format date for display
 * @param {string|Date} date - Date value
 * @returns {string} Formatted date string
 */
export function formatDate(date) {
  if (!date) return null;

  const dateObj = typeof date === 'string' ? new Date(date) : date;

  if (isNaN(dateObj.getTime())) return null;

  return dateObj.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
}

/**
 * Format datetime for display
 * @param {string|Date} datetime - Datetime value
 * @returns {string} Formatted datetime string
 */
export function formatDateTime(datetime) {
  if (!datetime) return null;

  const dateObj = typeof datetime === 'string' ? new Date(datetime) : datetime;

  if (isNaN(dateObj.getTime())) return null;

  return dateObj.toLocaleString('en-US', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: '2-digit',
    timeZone: 'America/New_York',
    timeZoneName: 'short'
  });
}

/**
 * Get the effective terms for a proposal (counteroffer if exists, otherwise original)
 * @param {Object} proposal - Transformed proposal object
 * @returns {Object} Effective terms object
 */
export function getEffectiveTerms(proposal) {
  if (!proposal) {
    throw new Error('getEffectiveTerms: Proposal is required');
  }

  // If counteroffer happened and has values, use those
  if (proposal.counterOfferHappened) {
    return {
      daysSelected: proposal.hcDaysSelected || proposal.daysSelected,
      reservationWeeks: proposal.hcReservationWeeks || proposal.reservationWeeks,
      nightsPerWeek: proposal.hcNightsPerWeek || proposal.nightsPerWeek,
      checkInDay: proposal.hcCheckInDay || proposal.checkInDay,
      checkOutDay: proposal.hcCheckOutDay || proposal.checkOutDay,
      totalPrice: proposal.hcTotalPrice || proposal.totalPrice,
      nightlyPrice: proposal.hcNightlyPrice || proposal.nightlyPrice,
      cleaningFee: proposal.hcCleaningFee || proposal.cleaningFee,
      damageDeposit: proposal.hcDamageDeposit || proposal.damageDeposit,
      isCounteroffer: true
    };
  }

  // Otherwise use original terms
  return {
    daysSelected: proposal.daysSelected,
    reservationWeeks: proposal.reservationWeeks,
    nightsPerWeek: proposal.nightsPerWeek,
    checkInDay: proposal.checkInDay,
    checkOutDay: proposal.checkOutDay,
    totalPrice: proposal.totalPrice,
    nightlyPrice: proposal.nightlyPrice,
    cleaningFee: proposal.cleaningFee,
    damageDeposit: proposal.damageDeposit,
    isCounteroffer: false
  };
}

