/**
 * Normalizes and validates a profile photo URL.
 *
 * @function processProfilePhotoUrl
 * @intent Transform raw photo URL into validated, normalized format
 * @rule Handle protocol-relative URLs (//example.com/photo.jpg → https://example.com/photo.jpg)
 * @rule Return null for invalid or missing URLs (NO FALLBACK to placeholder)
 * @rule Validate URL format to prevent XSS (must be http/https)
 * @rule Empty strings are treated as null (no photo)
 *
 * @param {object} params - Named parameters
 * @param {string|null|undefined} params.photoUrl - Raw profile photo URL
 * @returns {string|null} Normalized URL or null if invalid/missing
 *
 * @example
 * processProfilePhotoUrl({ photoUrl: 'https://example.com/photo.jpg' }) // "https://example.com/photo.jpg"
 * processProfilePhotoUrl({ photoUrl: '//example.com/photo.jpg' }) // "https://example.com/photo.jpg"
 * processProfilePhotoUrl({ photoUrl: '' }) // null
 * processProfilePhotoUrl({ photoUrl: null }) // null
 * processProfilePhotoUrl({ photoUrl: 'javascript:alert(1)' }) // null (XSS prevention)
 */
export function processProfilePhotoUrl({ photoUrl }) {
  // NO FALLBACK: Return null for missing/invalid, never a placeholder
  if (photoUrl === null || photoUrl === undefined) {
    return null;
  }

  // Type check
  if (typeof photoUrl !== 'string') {
    return null;
  }

  // Trim whitespace
  const trimmedUrl = photoUrl.trim();

  // Empty string is treated as no photo
  if (trimmedUrl.length === 0) {
    return null;
  }

  // Handle protocol-relative URLs (//example.com/photo.jpg)
  if (trimmedUrl.startsWith('//')) {
    return `https:${trimmedUrl}`;
  }

  // Validate that URL starts with http:// or https:// (XSS prevention)
  if (!trimmedUrl.startsWith('http://') && !trimmedUrl.startsWith('https://')) {
    // Invalid protocol (could be javascript:, data:, etc.)
    return null;
  }

  // Return normalized URL
  return trimmedUrl;
}

/**
 * Process raw user data from Supabase into a clean, validated user object.
 *
 * @intent Transform raw user rows from Supabase into consistent, UI-ready format.
 * @rule NO FALLBACK - Throws explicit errors for missing critical fields.
 *
 * @param {object} rawUser - Raw user object from Supabase.
 * @returns {object} Clean, validated user object.
 *
 * @throws {Error} If rawUser is null/undefined.
 * @throws {Error} If critical _id field is missing.
 *
 * @example
 * const user = processUserData({
 *   _id: '123',
 *   'Name - Full': 'Jane Doe',
 *   'Name - First': 'Jane',
 *   'Profile Photo': 'https://...',
 *   'About Me / Bio': 'Software engineer...',
 *   'user verified?': true
 * })
 */
export function processUserData(rawUser) {
  if (!rawUser) {
    throw new Error('processUserData: User data is required');
  }

  if (!rawUser._id) {
    throw new Error('processUserData: User ID (_id) is required');
  }

  return {
    id: rawUser._id,
    firstName: rawUser['Name - First'] || null,
    lastName: rawUser['Name - Last'] || null,
    fullName: rawUser['Name - Full'] || null,
    profilePhoto: rawUser['Profile Photo'] || null,
    bio: rawUser['About Me / Bio'] || null,
    linkedInVerified: rawUser['Verify - Linked In ID'] || false,
    phoneVerified: rawUser['Verify - Phone'] || false,
    userVerified: rawUser['user verified?'] || false,
    proposalsList: rawUser['Proposals List'] || []
  };
}

