/**
 * Formats user's display name for UI presentation.
 *
 * @function processUserDisplayName
 * @intent Transform user name data into formatted display string
 * @rule If showFull is true and lastName exists, return "FirstName LastName"
 * @rule If showFull is false or no lastName, return "FirstName"
 * @rule NO FALLBACK: firstName is required, throw if missing
 * @rule Trims whitespace and ensures proper capitalization
 *
 * @param {object} params - Named parameters
 * @param {string} params.firstName - User's first name (required)
 * @param {string|null|undefined} params.lastName - User's last name (optional)
 * @param {boolean} params.showFull - Whether to show full name (first + last)
 * @returns {string} Formatted display name
 * @throws {Error} If firstName is missing or invalid
 *
 * @example
 * processUserDisplayName({ firstName: 'John', lastName: 'Doe', showFull: true }) // "John Doe"
 * processUserDisplayName({ firstName: 'John', lastName: 'Doe', showFull: false }) // "John"
 * processUserDisplayName({ firstName: 'Jane', lastName: null, showFull: true }) // "Jane"
 * processUserDisplayName({ firstName: '', lastName: 'Doe', showFull: true }) // throws Error
 */
export function processUserDisplayName({ firstName, lastName, showFull }) {
  // NO FALLBACK: firstName is mandatory for user identity
  if (typeof firstName !== 'string' || firstName.trim().length === 0) {
    throw new Error('processUserDisplayName requires a valid firstName. Cannot display user without name.');
  }

  // Validate showFull is a boolean
  if (typeof showFull !== 'boolean') {
    throw new Error('processUserDisplayName requires showFull to be a boolean');
  }

  // Normalize firstName (trim whitespace)
  const normalizedFirstName = firstName.trim();

  // If not showing full name or no lastName, return first name only
  if (!showFull || !lastName) {
    return normalizedFirstName;
  }

  // Validate lastName if we're trying to use it
  if (typeof lastName !== 'string' || lastName.trim().length === 0) {
    return normalizedFirstName;
  }

  // Return full name with space separator
  const normalizedLastName = lastName.trim();
  return `${normalizedFirstName} ${normalizedLastName}`;
}

/**
 * Generates user initials from first and last name for avatar display.
 *
 * @function processUserInitials
 * @intent Create 1-2 letter initials for avatar when no profile photo available
 * @rule If both names provided, use first letter of each (uppercase)
 * @rule If only firstName, use first letter (uppercase)
 * @rule NO FALLBACK: firstName is required, throw if missing
 *
 * @param {object} params - Named parameters
 * @param {string} params.firstName - User's first name (required)
 * @param {string|null|undefined} params.lastName - User's last name (optional)
 * @returns {string} 1-2 letter initials (e.g., "JD" or "J")
 * @throws {Error} If firstName is missing or invalid
 *
 * @example
 * processUserInitials({ firstName: 'John', lastName: 'Doe' }) // "JD"
 * processUserInitials({ firstName: 'Jane', lastName: null }) // "J"
 * processUserInitials({ firstName: 'Bob', lastName: '' }) // "B"
 * processUserInitials({ firstName: '', lastName: 'Smith' }) // throws Error
 */
export function processUserInitials({ firstName, lastName }) {
  // NO FALLBACK: firstName is mandatory for user identity
  if (typeof firstName !== 'string' || firstName.trim().length === 0) {
    throw new Error('processUserInitials requires a valid firstName. Cannot generate initials without user name.');
  }

  // Get first letter of firstName (uppercase)
  const firstInitial = firstName.trim().charAt(0).toUpperCase();

  // If lastName exists and is valid, add its first letter
  if (lastName && typeof lastName === 'string' && lastName.trim().length > 0) {
    const lastInitial = lastName.trim().charAt(0).toUpperCase();
    return firstInitial + lastInitial;
  }

  // Return just first initial if no valid lastName
  return firstInitial;
}

/**
 * Check if current page is a protected page requiring authentication.
 *
 * @intent Determine if the current URL requires user authentication.
 * @rule Protected pages redirect to home if user is not logged in.
 * @rule Handles both clean URLs (/account-profile) and .html URLs (/account-profile.html).
 *
 * @param {object} params - Named parameters.
 * @param {string} params.pathname - Current URL pathname.
 * @returns {boolean} True if current page requires authentication.
 *
 * @throws {Error} If pathname is not a string.
 *
 * @example
 * const protected = isProtectedPage({ pathname: '/account-profile' })
 * // => true
 *
 * const public = isProtectedPage({ pathname: '/search' })
 * // => false
 */
export function isProtectedPage({ pathname }) {
  // No Fallback: Validate input
  if (typeof pathname !== 'string') {
    throw new Error(
      `isProtectedPage: pathname must be a string, got ${typeof pathname}`
    )
  }

  const protectedPaths = [
    '/account-profile',
    '/host-dashboard'
  ]

  // Normalize path by removing .html extension for consistent matching
  const normalizedPath = pathname.replace(/\.html$/, '')

  // Check if current path matches any protected path exactly or starts with it
  return protectedPaths.some(path =>
    normalizedPath === path || normalizedPath.startsWith(path + '/')
  )
}

/**
 * Determine if a guest can accept a proposal (or host counteroffer).
 *
 * @intent Enforce business rules for when proposal acceptance is allowed.
 * @rule Guest can only accept when host has countered (Stage 2).
 * @rule Original proposals don't need acceptance (they're sent to host).
 * @rule Once accepted, VM scheduled, or cancelled, acceptance is not applicable.
 * @rule Deleted proposals cannot be accepted.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can accept this proposal.
 *
 * @example
 * canAcceptProposal({ proposalStatus: 'Host Countered' })
 * // => true
 *
 * canAcceptProposal({ proposalStatus: 'Pending' })
 * // => false (waiting for host, not guest)
 *
 * canAcceptProposal({ proposalStatus: 'Accepted' })
 * // => false (already accepted)
 */
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js'

export function canAcceptProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be accepted
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()

  // Can only accept when host has countered
  // This is the only status where guest needs to make an "accept" decision
  return status === PROPOSAL_STATUSES.COUNTEROFFER_SUBMITTED_AWAITING_GUEST_REVIEW.key.trim()
}

/**
 * Determine if a guest can cancel their proposal.
 *
 * @intent Enforce business rules for when proposal cancellation is allowed.
 * @rule Guest can cancel proposals in stages 1-4 (before completion).
 * @rule Once completed, proposals cannot be cancelled (only disputes apply).
 * @rule Already cancelled/rejected/expired proposals cannot be re-cancelled.
 * @rule Deleted proposals cannot be cancelled.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can cancel this proposal.
 *
 * @example
 * canCancelProposal({ proposalStatus: 'Pending' })
 * // => true
 *
 * canCancelProposal({ proposalStatus: 'Completed' })
 * // => false
 *
 * canCancelProposal({ proposalStatus: 'Cancelled by Guest' })
 * // => false (already cancelled)
 */
import { isTerminalStatus, isCompletedStatus } from '../../constants/proposalStatuses.js'

export function canCancelProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be cancelled
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()

  if (isTerminalStatus(status) || isCompletedStatus(status)) {
    return false
  }

  // Can cancel if in any active state
  // Includes: Draft, Pending, Host Countered, VM Requested, VM Confirmed, Accepted, Verified
  return true
}

/**
 * Determine if a guest can edit their proposal.
 *
 * @intent Enforce business rules for when proposal editing is allowed.
 * @rule Guest can only edit proposals in early stages (1-2).
 * @rule Once accepted, VM scheduled, or cancelled, editing is locked.
 * @rule Deleted proposals cannot be edited.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {boolean} True if guest can edit this proposal.
 *
 * @example
 * canEditProposal({ proposalStatus: 'Pending' })
 * // => true
 *
 * canEditProposal({ proposalStatus: 'Accepted' })
 * // => false
 */
import { getActionsForStatus } from '../../constants/proposalStatuses.js'

export function canEditProposal({ proposalStatus, deleted = false }) {
  // Deleted proposals cannot be edited
  if (deleted) {
    return false
  }

  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    return false
  }

  const status = proposalStatus.trim()
  const actions = getActionsForStatus(status)

  return actions.includes('modify_proposal') || actions.includes('edit_proposal')
}

/**
 * Determine the current stage (1-6) of a proposal in the guest journey.
 *
 * @intent Map proposal status to visual progress tracker stage.
 * @rule Implements the 6-stage proposal journey:
 *   Stage 1: Proposal Sent (Draft, Pending)
 *   Stage 2: Host Countered (Host Countered)
 *   Stage 3: Virtual Meeting Scheduled (VM Requested, VM Confirmed)
 *   Stage 4: Accepted (Accepted, Verified)
 *   Stage 5: Completed (Completed)
 *   Stage 6: Cancelled (Cancelled by Guest, Cancelled by Host, Rejected, Expired)
 *
 * @param {object} params - Named parameters.
 * @param {string} params.proposalStatus - The "Proposal Status" field from Supabase.
 * @param {boolean} [params.deleted=false] - Whether proposal is soft-deleted.
 * @returns {number} Stage number (1-6).
 *
 * @throws {Error} If proposalStatus is missing.
 *
 * @example
 * const stage = determineProposalStage({ proposalStatus: 'Host Countered' })
 * // => 2
 */
import { getStageFromStatus, isTerminalStatus } from '../../constants/proposalStatuses.js'

export function determineProposalStage({ proposalStatus, deleted = false }) {
  // Validation
  if (!proposalStatus || typeof proposalStatus !== 'string') {
    throw new Error('determineProposalStage: proposalStatus is required and must be a string')
  }

  // Deleted proposals are always stage 6 (Cancelled)
  if (deleted) {
    return 6
  }

  const status = proposalStatus.trim()

  if (isTerminalStatus(status)) {
    return 6
  }

  const stage = getStageFromStatus(status)
  return stage ?? 1
}

