/**
 * Review validation rules.
 * Boolean predicates for guest review form validation.
 */

import { REVIEW_CATEGORY_COUNT } from '../../constants/reviewCategories.js';

/**
 * Check if all required rating categories are completed.
 *
 * @param {object} params - Named parameters.
 * @param {Array<{value: number}>} params.ratings - Array of rating objects.
 * @returns {boolean} True if all 12 categories have valid ratings.
 */
export function isReviewComplete({ ratings }) {
  if (!Array.isArray(ratings)) {
    return false;
  }

  if (ratings.length !== REVIEW_CATEGORY_COUNT) {
    return false;
  }

  return ratings.every(r =>
    typeof r.value === 'number' && r.value >= 1 && r.value <= 5
  );
}

/**
 * Check if a single rating value is valid.
 *
 * @param {object} params - Named parameters.
 * @param {number} params.value - Rating value to validate.
 * @returns {boolean} True if value is 1-5.
 */
export function isValidRating({ value }) {
  return typeof value === 'number' && value >= 1 && value <= 5;
}

/**
 * Check if review can be submitted.
 *
 * @param {object} params - Named parameters.
 * @param {Array<{value: number}>} params.ratings - Rating objects.
 * @param {boolean} params.isSubmitting - Whether submission is in progress.
 * @returns {boolean} True if form can be submitted.
 */
export function canSubmitReview({ ratings, isSubmitting = false }) {
  if (isSubmitting) {
    return false;
  }

  return isReviewComplete({ ratings });
}

/**
 * Check if guest has already been reviewed for this stay.
 *
 * @param {object} params - Named parameters.
 * @param {string|null} params.existingReviewId - Existing review ID if any.
 * @returns {boolean} True if review already exists.
 */
export function hasExistingReview({ existingReviewId }) {
  return existingReviewId !== null && existingReviewId !== undefined;
}

/**
 * Check if a specific date is blocked.
 *
 * @intent Determine if a date is unavailable due to blocking.
 * @rule Compares dates in YYYY-MM-DD format (ignoring time).
 *
 * @param {object} params - Named parameters.
 * @param {Date} params.date - Date to check.
 * @param {Array} params.blockedDates - Array of blocked date strings or Date objects.
 * @returns {boolean} True if date is blocked, false otherwise.
 *
 * @throws {Error} If date is not a valid Date object.
 * @throws {Error} If blockedDates is not an array.
 *
 * @example
 * const blocked = isDateBlocked({
 *   date: new Date('2025-12-25'),
 *   blockedDates: ['2025-12-25', '2025-12-26']
 * })
 * // => true
 */
export function isDateBlocked({ date, blockedDates }) {
  // No Fallback: Validate inputs
  if (!(date instanceof Date) || isNaN(date.getTime())) {
    throw new Error(
      'isDateBlocked: date must be a valid Date object'
    )
  }

  if (!Array.isArray(blockedDates)) {
    throw new Error(
      `isDateBlocked: blockedDates must be an array, got ${typeof blockedDates}`
    )
  }

  // Empty blockedDates array means nothing is blocked
  if (blockedDates.length === 0) {
    return false
  }

  // Format the check date as YYYY-MM-DD
  const dateStr = date.toISOString().split('T')[0]

  // Check if any blocked date matches
  return blockedDates.some(blocked => {
    if (typeof blocked === 'string') {
      // Extract date portion (YYYY-MM-DD) from string
      const blockedDateStr = blocked.split('T')[0]
      return blockedDateStr === dateStr
    }
    if (blocked instanceof Date) {
      const blockedDateStr = blocked.toISOString().split('T')[0]
      return blockedDateStr === dateStr
    }
    // Skip invalid entries silently (they don't block anything)
    return false
  })
}

/**
 * Check if a date is within the available range.
 *
 * @intent Validate that a date falls within the listing's availability window.
 * @rule Date must be >= firstAvailable (if specified).
 * @rule Date must be <= lastAvailable (if specified).
 * @rule Null/undefined bounds are treated as unbounded (no restriction).
 *
 * @param {object} params - Named parameters.
 * @param {Date} params.date - Date to check.
 * @param {string|Date|null} params.firstAvailable - First available date (inclusive).
 * @param {string|Date|null} params.lastAvailable - Last available date (inclusive).
 * @returns {boolean} True if date is within range, false otherwise.
 *
 * @throws {Error} If date is not a valid Date object.
 *
 * @example
 * const inRange = isDateInRange({
 *   date: new Date('2025-12-15'),
 *   firstAvailable: '2025-12-01',
 *   lastAvailable: '2026-01-31'
 * })
 * // => true
 */
export function isDateInRange({ date, firstAvailable, lastAvailable }) {
  // No Fallback: Validate date
  if (!(date instanceof Date) || isNaN(date.getTime())) {
    throw new Error(
      'isDateInRange: date must be a valid Date object'
    )
  }

  // Normalize check date (ignore time component)
  const checkDate = new Date(date)
  checkDate.setHours(0, 0, 0, 0)

  // Check first available bound
  if (firstAvailable !== null && firstAvailable !== undefined) {
    const firstDate = new Date(firstAvailable)
    if (isNaN(firstDate.getTime())) {
      throw new Error(
        `isDateInRange: firstAvailable is not a valid date: ${firstAvailable}`
      )
    }
    firstDate.setHours(0, 0, 0, 0)
    if (checkDate < firstDate) {
      return false
    }
  }

  // Check last available bound
  if (lastAvailable !== null && lastAvailable !== undefined) {
    const lastDate = new Date(lastAvailable)
    if (isNaN(lastDate.getTime())) {
      throw new Error(
        `isDateInRange: lastAvailable is not a valid date: ${lastAvailable}`
      )
    }
    lastDate.setHours(0, 0, 0, 0)
    if (checkDate > lastDate) {
      return false
    }
  }

  return true
}

/**
 * Check if selected days form a contiguous (consecutive) block.
 *
 * @intent Enforce the business rule that split lease stays must be consecutive nights.
 * @rule Days must be consecutive (Mon-Fri ✓, Mon+Wed ✗).
 * @rule Handles week wrap-around cases (Fri-Sun ✓, Sat-Tue ✓).
 * @rule For wrap-around, uses inverse logic: if unselected days are contiguous, selected days wrap around properly.
 * @rule 6 or more days is always contiguous (only 1 gap maximum).
 *
 * This function consolidates the contiguous validation logic that previously existed in:
 * - availabilityValidation.js (isContiguousSelection)
 * - scheduleSelector/validators.js (isContiguous)
 *
 * @param {object} params - Named parameters.
 * @param {number[]} params.selectedDayIndices - Array of day indices (0=Sunday, 1=Monday, ..., 6=Saturday).
 * @returns {boolean} True if days are contiguous, false otherwise.
 *
 * @throws {Error} If selectedDayIndices is not an array.
 * @throws {Error} If any day index is invalid (not 0-6).
 *
 * @example
 * isScheduleContiguous({ selectedDayIndices: [1, 2, 3, 4, 5] }) // => true (Mon-Fri)
 * isScheduleContiguous({ selectedDayIndices: [1, 3, 5] }) // => false (Mon, Wed, Fri - not contiguous)
 * isScheduleContiguous({ selectedDayIndices: [5, 6, 0] }) // => true (Fri-Sun, wraps around week)
 * isScheduleContiguous({ selectedDayIndices: [6, 0, 1, 2] }) // => true (Sat-Tue, wraps around week)
 */
export function isScheduleContiguous({ selectedDayIndices }) {
  // No Fallback: Validate input
  if (!Array.isArray(selectedDayIndices)) {
    throw new Error(
      `isScheduleContiguous: selectedDayIndices must be an array, got ${typeof selectedDayIndices}`
    )
  }

  // Empty selection is not contiguous
  if (selectedDayIndices.length === 0) {
    return false
  }

  // Single day is contiguous
  if (selectedDayIndices.length === 1) {
    return true
  }

  // Validate all day indices
  for (const day of selectedDayIndices) {
    if (typeof day !== 'number' || isNaN(day) || day < 0 || day > 6) {
      throw new Error(
        `isScheduleContiguous: Invalid day index ${day}, must be 0-6`
      )
    }
  }

  // Sort the selected days
  const sorted = [...selectedDayIndices].sort((a, b) => a - b)

  // If 6 or more days selected, it's contiguous (only 1 gap or no gaps)
  if (sorted.length >= 6) {
    return true
  }

  // Check for standard contiguous sequence (no wrap around)
  let isStandardContiguous = true
  for (let i = 1; i < sorted.length; i++) {
    if (sorted[i] !== sorted[i - 1] + 1) {
      isStandardContiguous = false
      break
    }
  }

  if (isStandardContiguous) {
    return true
  }

  // Check if selection includes both Sunday (0) and Saturday (6) - wrap-around case
  const hasZero = sorted.includes(0)
  const hasSix = sorted.includes(6)

  if (hasZero && hasSix) {
    // Week wrap-around case: use inverse logic (check not-selected days)
    // If the NOT selected days are contiguous, then selected days wrap around and are contiguous
    const allDays = [0, 1, 2, 3, 4, 5, 6]
    const notSelectedDays = allDays.filter(d => !sorted.includes(d))

    if (notSelectedDays.length === 0) {
      return true // All days selected
    }

    // Check if not-selected days form a contiguous block
    const minNotSelected = Math.min(...notSelectedDays)
    const maxNotSelected = Math.max(...notSelectedDays)

    // Generate expected contiguous range for not-selected days
    const expectedNotSelected = []
    for (let i = minNotSelected; i <= maxNotSelected; i++) {
      expectedNotSelected.push(i)
    }

    // If not-selected days are contiguous, then selected days wrap around properly
    const notSelectedContiguous = notSelectedDays.length === expectedNotSelected.length &&
      notSelectedDays.every((day, index) => day === expectedNotSelected[index])

    return notSelectedContiguous
  }

  return false
}

/**
 * Check if a listing has photos.
 *
 * @intent Validate that a listing has at least one photo for search display.
 * @rule Listings without photos must not appear in search results.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.listing - The listing object to check.
 * @returns {boolean} True if listing has at least one photo.
 *
 * @throws {Error} If listing is null or undefined.
 *
 * @example
 * const valid = hasListingPhotos({ listing: { 'Features - Photos': ['photo1'] } })
 * // => true
 *
 * const invalid = hasListingPhotos({ listing: { 'Features - Photos': [] } })
 * // => false
 */
export function hasListingPhotos({ listing }) {
  // No Fallback: Validate input
  if (listing === null || listing === undefined) {
    throw new Error('hasListingPhotos: listing cannot be null or undefined')
  }

  const photos = listing['Features - Photos']

  // Check for null, undefined, or empty array
  if (!photos) {
    return false
  }

  // Handle string representation (JSON array from database)
  if (typeof photos === 'string') {
    try {
      const parsed = JSON.parse(photos)
      return Array.isArray(parsed) && parsed.length > 0
    } catch {
      return false
    }
  }

  // Handle array directly
  if (Array.isArray(photos)) {
    return photos.length > 0
  }

  return false
}

import { VALID_PRICE_TIERS } from '../../constants/searchConstants.js';

/**
 * Check if price tier filter value is valid.
 *
 * @intent Validate price tier selection for search filters.
 * @rule Valid tiers: 'under-200', '200-350', '350-500', '500-plus', 'all'.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.priceTier - Price tier value to validate.
 * @returns {boolean} True if valid price tier.
 *
 * @throws {Error} If priceTier is not a string.
 *
 * @example
 * const valid = isValidPriceTier({ priceTier: 'under-200' })
 * // => true
 *
 * const invalid = isValidPriceTier({ priceTier: 'invalid' })
 * // => false
 */
export function isValidPriceTier({ priceTier }) {
  // No Fallback: Validate input
  if (typeof priceTier !== 'string') {
    throw new Error(
      `isValidPriceTier: priceTier must be a string, got ${typeof priceTier}`
    )
  }

  return VALID_PRICE_TIERS.includes(priceTier)
}

import { VALID_SORT_OPTIONS } from '../../constants/searchConstants.js';

/**
 * Check if sort option value is valid.
 *
 * @intent Validate sort option selection for search results.
 * @rule Valid options: 'recommended', 'price-low', 'most-viewed', 'recent'.
 *
 * @param {object} params - Named parameters.
 * @param {string} params.sortBy - Sort option value to validate.
 * @returns {boolean} True if valid sort option.
 *
 * @throws {Error} If sortBy is not a string.
 *
 * @example
 * const valid = isValidSortOption({ sortBy: 'price-low' })
 * // => true
 *
 * const invalid = isValidSortOption({ sortBy: 'invalid' })
 * // => false
 */
export function isValidSortOption({ sortBy }) {
  // No Fallback: Validate input
  if (typeof sortBy !== 'string') {
