     1→/**
     2→ * Cancel Proposal Workflow
     3→ *
     4→ * PILLAR IV: Workflow Orchestrators (The "Flow" Layer)
     5→ *
     6→ * Implements the 7 variations of the cancel proposal workflow from Bubble.io:
     7→ * - crkec5: Cancel Proposal (Condition 1) - Basic cancellation
     8→ * - crswt2: Cancel Proposal (Condition 2) - Usual Order > 5 and House manual not empty
     9→ * - crtCg2: Cancel Proposal (Condition 3) - Status is Cancelled or Rejected
    10→ * - curuC4: Cancel Proposal (Condition 4) - Additional variation
    11→ * - curuK4: Cancel Proposal (Condition 5) - Same as condition 2
    12→ * - curua4: Cancel Proposal (Condition 6) - Same as condition 3
    13→ * - crkZs5: Cancel Proposal in Compare Terms popup
    14→ *
    15→ * All cancellations result in:
    16→ * - Status: 'Proposal Cancelled by Guest'
    17→ * - Modified Date: current timestamp
    18→ * - Optional: reason for cancellation
    19→ */
    20→
    21→import { supabase } from '../../../lib/supabase.js';
    22→import { canCancelProposal, requiresSpecialCancellationConfirmation } from '../../rules/proposals/proposalRules.js';
    23→import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js';
    24→
    25→/**
    26→ * Evaluate which cancellation workflow condition applies
    27→ *
    28→ * @param {Object} proposal - Full proposal object
    29→ * @returns {Object} Condition details with workflow info
    30→ */
    31→export function determineCancellationCondition(proposal) {
    32→  if (!proposal) {
    33→    return {
    34→      condition: 'invalid',
    35→      workflow: null,
    36→      allowCancel: false,
    37→      message: 'Invalid proposal data'
    38→    };
    39→  }
    40→
    41→  const status = proposal.status || proposal.Status;
    42→
    43→  // Condition 3 & 6: Already cancelled or rejected - just inform user
    44→  if (
    45→    status === PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key ||
    46→    status === PROPOSAL_STATUSES.CANCELLED_BY_SPLITLEASE.key ||
    47→    status === PROPOSAL_STATUSES.REJECTED_BY_HOST.key
    48→  ) {
    49→    return {
    50→      condition: 'already_cancelled',
    51→      workflow: 'crtCg2',
    52→      allowCancel: false,
    53→      message: 'This proposal is already cancelled or rejected'
    54→    };
    55→  }
    56→
    57→  // Check if can cancel based on rules
    58→  if (!canCancelProposal(proposal)) {
    59→    return {
    60→      condition: 'not_cancellable',
    61→      workflow: null,
    62→      allowCancel: false,
    63→      message: 'This proposal cannot be cancelled at this stage'
    64→    };
    65→  }
    66→
    67→  // Condition 2 & 5: Usual Order > 5 AND House manual not empty
    68→  if (requiresSpecialCancellationConfirmation(proposal)) {
    69→    return {
    70→      condition: 'high_order_with_manual',
    71→      workflow: 'crswt2',
    72→      allowCancel: true,
    73→      requiresConfirmation: true,
    74→      confirmationMessage: 'You have an active rental history. Are you sure you want to cancel? This may affect your standing with the host and future rental opportunities.'
    75→    };
    76→  }
    77→
    78→  // Condition 1, 4, and default: Standard cancellation
    79→  return {
    80→    condition: 'standard',
    81→    workflow: 'crkec5',
    82→    allowCancel: true,
    83→    requiresConfirmation: true,
    84→    confirmationMessage: 'Are you sure you want to cancel this proposal? This action cannot be undone.'
    85→  };
    86→}
    87→
    88→/**
    89→ * Execute proposal cancellation in database
    90→ *
    91→ * @param {string} proposalId - Proposal ID to cancel
    92→ * @param {string} reason - Optional reason for cancellation
    93→ * @returns {Promise<Object>} Updated proposal data
    94→ */
    95→export async function executeCancelProposal(proposalId, reason = null) {
    96→  if (!proposalId) {
    97→    throw new Error('Proposal ID is required');
    98→  }
    99→
   100→  const now = new Date().toISOString();
   101→
   102→  const updateData = {
   103→    'Status': PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key,
   104→    'Modified Date': now
   105→  };
   106→
   107→  // Add reason if provided
   108→  if (reason) {
   109→    updateData['reason for cancellation'] = reason;
   110→  }
   111→
   112→  console.log('[cancelProposalWorkflow] Cancelling proposal:', proposalId);
   113→
   114→  const { data, error } = await supabase
   115→    .from('proposal')
   116→    .update(updateData)
   117→    .eq('_id', proposalId)
   118→    .select()
   119→    .single();
   120→
   121→  if (error) {
   122→    console.error('[cancelProposalWorkflow] Error cancelling proposal:', error);
   123→    throw new Error(`Failed to cancel proposal: ${error.message}`);
   124→  }
   125→
   126→  console.log('[cancelProposalWorkflow] Proposal cancelled successfully:', proposalId);
   127→  return data;
   128→}
   129→
   130→/**
   131→ * Cancel proposal from Compare Terms modal (workflow crkZs5)
   132→ * Same as regular cancellation but triggered from different UI location
   133→ *
   134→ * @param {string} proposalId - Proposal ID to cancel
   135→ * @param {string} reason - Reason for cancellation
   136→ * @returns {Promise<Object>} Updated proposal data
   137→ */
   138→export async function cancelProposalFromCompareTerms(proposalId, reason = 'Counteroffer declined') {
   139→  console.log('[cancelProposalWorkflow] Cancel triggered from Compare Terms modal (workflow crkZs5)');
   140→  return executeCancelProposal(proposalId, reason);
   141→}
   142→
   143→/**
   144→ * Soft-delete a proposal (hide from user's list)
   145→ *
   146→ * Used for already-cancelled/rejected proposals where the guest just wants
   147→ * to remove it from their view. Sets deleted = true without changing status.
   148→ *
   149→ * @param {string} proposalId - Proposal ID to delete
   150→ * @returns {Promise<Object>} Updated proposal data
   151→ */
   152→export async function executeDeleteProposal(proposalId) {
   153→  if (!proposalId) {
   154→    throw new Error('Proposal ID is required');
   155→  }
   156→
   157→  const now = new Date().toISOString();
   158→
   159→  console.log('[cancelProposalWorkflow] Soft-deleting proposal:', proposalId);
   160→
   161→  const { error } = await supabase
   162→    .from('proposal')
   163→    .update({
   164→      'Deleted': true,
   165→      'Modified Date': now
   166→    })
   167→    .eq('_id', proposalId);
   168→
   169→  if (error) {
   170→    console.error('[cancelProposalWorkflow] Error deleting proposal:', error);
   171→    throw new Error(`Failed to delete proposal: ${error.message}`);
   172→  }
   173→
   174→  console.log('[cancelProposalWorkflow] Proposal deleted successfully:', proposalId);
   175→}
