   163→ * @returns {boolean} - True if expiring soon
   164→ */
   165→export const isExpiringSoon = (expirationDate, hoursThreshold = 6) => {
   166→  const expDate = typeof expirationDate === 'string' ? new Date(expirationDate) : expirationDate;
   167→  const now = new Date();
   168→  const diffMs = expDate.getTime() - now.getTime();
   169→  const diffHours = diffMs / (1000 * 60 * 60);
   170→  return diffHours > 0 && diffHours <= hoursThreshold;
