     1→/**
     2→ * Utility functions for formatting listing data
     3→ * Implements the 4 conditional logic rules from Bubble
     4→ */
     5→
     6→/**
     7→ * Bathroom display mapping based on Bubble option set
     8→ */
     9→const bathroomDisplayMap = {
    10→  1: '1 Bath',
    11→  1.5: '1.5 Baths',
    12→  2: '2 Baths',
    13→  2.5: '2.5 Baths',
    14→  3: '3 Baths',
    15→  3.5: '3.5 Baths',
    16→  4: '4 Baths',
    17→  4.5: '4.5 Baths',
    18→  5: '5 Baths',
    19→  6: '6 Baths',
    20→};
    21→
    22→/**
    23→ * Get bathroom display text from numeric value
    24→ * @param {number} count - Number of bathrooms (can be decimal for half baths)
    25→ * @returns {string} Formatted bathroom display text
    26→ */
    27→export const getBathroomDisplay = (count) => {
    28→  return bathroomDisplayMap[count] || `${count} Baths`;
    29→};
    30→
    31→/**
    32→ * Format bedroom and bathroom text according to Bubble's 4 conditional rules
    33→ *
    34→ * CONDITIONAL 1: When bedrooms = 1
    35→ *   Display: "• 1 bedroom • [bathrooms] • [kitchen]"
    36→ *
    37→ * CONDITIONAL 2: When bathrooms = 0
    38→ *   Display: "[bedrooms] bedrooms" (bedroom info only)
    39→ *
    40→ * CONDITIONAL 3: When bedrooms > 1
    41→ *   Display: "• [bedrooms] bedrooms • [bathrooms] • [kitchen]"
    42→ *
    43→ * CONDITIONAL 4: When kitchen type is empty
    44→ *   Display: "• [bedrooms] bedroom(s) • [bathrooms]" (no kitchen)
    45→ *
    46→ * @param {number} bedrooms - Number of bedrooms
    47→ * @param {number} bathrooms - Number of bathrooms
    48→ * @param {string} [kitchenType] - Optional kitchen type
    49→ * @returns {string} Formatted display string
    50→ */
    51→export const formatBedroomBathroom = (bedrooms, bathrooms, kitchenType) => {
    52→  const parts = [];
    53→
    54→  // CONDITIONAL 2: When bathrooms = 0, show only bedroom info
    55→  if (bathrooms === 0) {
    56→    if (bedrooms === 1) {
    57→      return '1 bedroom';
    58→    } else if (bedrooms > 1) {
    59→      return `${bedrooms} bedrooms`;
    60→    }
    61→    return '';
    62→  }
    63→
    64→  // CONDITIONAL 1: When bedrooms = 1
    65→  if (bedrooms === 1) {
    66→    parts.push('1 bedroom');
    67→  }
    68→  // CONDITIONAL 3: When bedrooms > 1
    69→  else if (bedrooms > 1) {
    70→    parts.push(`${bedrooms} bedrooms`);
    71→  }
    72→
    73→  // Add bathroom information (if > 0)
    74→  if (bathrooms > 0) {
    75→    const bathroomDisplay = getBathroomDisplay(bathrooms);
    76→    parts.push(bathroomDisplay);
    77→  }
    78→
    79→  // CONDITIONAL 4: Add kitchen type only if provided
    80→  if (kitchenType && kitchenType !== '') {
    81→    parts.push(kitchenType);
    82→  }
    83→
    84→  // Join parts with bullet separator
    85→  return parts.length > 0 ? '• ' + parts.join(' • ') : '';
    86→};
    87→
    88→/**
    89→ * Format price with currency symbol
    90→ * @param {number} price - Price amount
    91→ * @param {string} [currency='USD'] - Currency code
    92→ * @returns {string} Formatted price string (e.g., "$1,029/night")
    93→ */
    94→export const formatPrice = (price, currency = 'USD') => {
    95→  const formatter = new Intl.NumberFormat('en-US', {
    96→    style: 'currency',
    97→    currency: currency,
    98→    minimumFractionDigits: 0,
    99→    maximumFractionDigits: 0,
   100→  });
