     1→/**
     2→ * Supabase Utilities Module
     3→ *
     4→ * Centralized data fetching logic for photos, hosts, and amenities.
     5→ * Follows NO FALLBACK principle - returns real data or empty/null values.
     6→ *
     7→ * @module supabaseUtils
     8→ */
     9→
    10→import { supabase } from './supabase.js';
    11→import { DATABASE } from './constants.js';
    12→import { logger } from './logger.js';
    13→import { parseJsonArrayFieldOptional } from '../logic/processors/listing/parseJsonArrayField.js';
    14→
    15→// Internal alias for use within this module
    16→const parseJsonArray = parseJsonArrayFieldOptional;
    17→
    18→/**
    19→ * Parse a value that may be a native array or stringified JSON array
    20→ * Re-exported from logic layer to maintain backward compatibility.
    21→ *
    22→ * @deprecated Prefer importing parseJsonArrayFieldOptional directly from logic layer
    23→ * @see app/src/logic/processors/listing/parseJsonArrayField.js
    24→ */
    25→export { parseJsonArrayFieldOptional as parseJsonArray };
    26→
    27→/**
    28→ * Fetch photo URLs in batch from database
    29→ * @param {Array<string>} photoIds - Array of photo IDs to fetch
    30→ * @returns {Promise<Object>} Map of photo ID to photo URL
    31→ */
    32→export async function fetchPhotoUrls(photoIds) {
    33→  logger.debug('fetchPhotoUrls called', { count: photoIds?.length || 0 });
    34→
    35→  if (!photoIds || photoIds.length === 0) {
    36→    return {};
    37→  }
    38→
    39→  try {
    40→    const { data, error } = await supabase
    41→      .from('listing_photo')
    42→      .select('_id, Photo')
    43→      .in('_id', photoIds);
    44→
    45→    if (error) {
    46→      logger.error('Error fetching photos:', error);
    47→      return {};
    48→    }
    49→
    50→    if (!data || data.length === 0) {
    51→      return {};
    52→    }
    53→
    54→    // Create a map of photo ID to URL
    55→    const photoMap = {};
    56→    data.forEach(photo => {
    57→      if (photo.Photo) {
    58→        // Add https: protocol if URL starts with //
    59→        let photoUrl = photo.Photo;
    60→        if (photoUrl.startsWith('//')) {
    61→          photoUrl = 'https:' + photoUrl;
    62→        }
    63→        photoMap[photo._id] = photoUrl;
    64→      }
    65→    });
    66→
    67→    logger.debug('Fetched photo URLs', { count: Object.keys(photoMap).length });
    68→    return photoMap;
    69→  } catch (error) {
    70→    logger.error('Error in fetchPhotoUrls:', error);
    71→    return {};
    72→  }
    73→}
    74→
    75→/**
    76→ * Fetch host data in batch from database
    77→ * After migration, hostIds are always user._id values (Host User column)
    78→ * @param {Array<string>} hostIds - Array of host IDs (user._id)
    79→ * @returns {Promise<Object>} Map of host ID to host data {name, image, verified}
    80→ */
    81→export async function fetchHostData(hostIds) {
    82→  if (!hostIds || hostIds.length === 0) {
    83→    return {};
    84→  }
    85→
    86→  const hostMap = {};
    87→
