     1→/**
     2→ * Proposal Data Fetcher Utilities
     3→ * Handles URL parsing and data fetching for guest proposals page
     4→ *
     5→ * Based on the same pattern as listingDataFetcher.js but for user/proposal data
     6→ */
     7→
     8→import { supabase } from './supabase.js';
     9→
    10→/**
    11→ * Extract user ID from URL pathname
    12→ * Supports multiple URL formats:
    13→ * - /guest-proposals/userId (primary format)
    14→ * - /guest-proposals.html/userId (alternative format)
    15→ * - /guest-proposals?user=userId (query string fallback)
    16→ *
    17→ * @returns {string|null} User ID or null if not found
    18→ */
    19→export function getUserIdFromUrl() {
    20→  // 1. Check query string: ?user=userId
    21→  const urlParams = new URLSearchParams(window.location.search);
    22→  const idFromQuery = urlParams.get('user');
    23→  if (idFromQuery) return idFromQuery;
    24→
    25→  // 2. Parse pathname for segment after 'guest-proposals'
    26→  const pathSegments = window.location.pathname.split('/').filter(segment => segment);
    27→  const proposalsSegmentIndex = pathSegments.findIndex(segment =>
    28→    segment === 'guest-proposals' ||
    29→    segment === 'guest-proposals.html'
    30→  );
    31→
    32→  if (proposalsSegmentIndex !== -1 && pathSegments[proposalsSegmentIndex + 1]) {
    33→    const nextSegment = pathSegments[proposalsSegmentIndex + 1];
    34→    // Return the segment if it doesn't contain a file extension
    35→    if (!nextSegment.includes('.')) {
    36→      return nextSegment;
    37→    }
    38→  }
    39→
    40→  // 3. No user ID found in URL
    41→  return null;
    42→}
    43→
    44→/**
    45→ * Fetch user data from Supabase by user ID
    46→ * @param {string} userId - The user's _id
    47→ * @returns {Promise<object|null>} User data or null if not found
    48→ */
    49→export async function fetchUserById(userId) {
